css:
@-moz-document domain('tumblr.com'), url-prefix('http://www.tumblr.com/dashboard') {
	/* 
		the code in here only applies to:
			-pages on the domain http://www.tumblr.com/dashboard
			-pages whose URLs start with http://www.tumblr.com/dashboard
	*/
}

a {
    -webkit-transition:all .2s linear;
    -moz-transition:all .2s linear;
    -o-transition:all .2s linear;
    -ms-transition:all .2s linear;
    transition:all .2s linear;
    color:#2b2b2b;
    text-decoration:none;
}

a:hover {
    color:#2b2b2b;
    text-decoration:none;
    opacity:.45;
    filter: alpha(opacity = 45);
    text-decoration:none;
}

#logo {height: 0 !important; 
width: 0 !important; 
padding-left: 350px !important; 
padding-top: 44px !important; 
background: url(http://i.imgur.com/Z69uX.png) no-repeat !important;}

body {background: url('http://www.cartoonnetwork.co.uk/sites/www.cartoonnetwork.co.uk/files/at_wallpapers_gliding_1680x1050.jpg') center right #fff repeat fixed !important;} 
*{text-shadow:none !important; color:#7a7a7a !important;} 

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    background: rgba(20,30,100,0.1) !important;
  }

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: rgba(0,10,1,0.4) !important;
    -webkit-border-radius: 20px;
    -moz-border-radius: 20px;
    border-radius: 20px;
    margin: auto;
    padding: 20px 20px;
  }
 
#nav .nav_item.active 
	{background-color:#6D6D6D} 
.permalink  
	{background: none !important; 
	width: 16px !important; 
	height: 16px !important; 
  	background: #f9f9f9 !important; 
  	border-bottom-left-radius: 10px !important; 
  	border-top-right-radius: 10px !important; 
  	margin-top: 3px !important; 
  	margin-right: 4px !important;} 
h1,h2,h3,h4,h5,h6 
	{color:#fff !important;} 
 
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"])  
	{background: none !important; 
	width: 0px !important; 
	height: 0px !important; 
	margin-top: 0px !important; 
	border-bottom: solid 11px white !important; 
	border-right: 11px solid transparent !important; 
	border-left: 11px solid transparent !important; } 
.selection_nipple:not([class="selection_nipple white"])  
	{border-bottom: solid 11px #f9f9f9 !important;} 
 
 
 
#dashboard_switch_blog_menu_current, 
#dashboard_switch_blog_arrow_and_divider, 
#dashboard_switch_blog_arrow_current 
	{background-image:none !important;} 
#new_post_notice_container 
	{color:#fff !important;} 
#new_post_notice_container a 
	{color:#fff !important;} 
ol#posts li.notification 
	{background-color: #f9f9f9 !important; 
	border-bottom: 2px solid #9d92a5 !important; 
	color: #000000 !important;} 
 
 
 
.notification_type_icon{display:none !important} 
.selected .following, 
.selected .followers, 
.selected .settings, 
.selected .posts, 
.selected .messages, 
.selected .drafts, 
.selected .queue,  
.selected .likes  
	{color: #A099A1 !important; 
	background: #e9e9e9 !important;} 
 
 
 
a.explore_more_tags, 
a.add_and_remove, 
a.customize, 
a.mass_editor 
	{background-color:#e9e9e9 !important;   
	background-image:none !important; 
	border:0px !important;} 
div.gradient, 
div.dashboard_nav_item,  
a.permalink 
	{background-color:#e9e9e9 !important; 
	opacity:.9;  
	background-image:none !important; 
	border:0px !important;} 
.content.is_media,  
.mask.top,  
.mask.bottom  
	{background: none !important; } 
.post.photo_post .photo,  
.post.photo_post .video_thumbnail  
	{border-radius: 16px !important;} 
.post.photo_post .photo,  
.post.photo_post .video_thumbnail  
	{-moz-box-shadow: 1px 1px 3px #000000 !important;} 
 
 
 
#auto_pagination_loader 
	{background: #fff !important;} 
div.return_to_top_icon{	background: none !important; 
	width: 0px !important; 
	height: 0px !important; 
	margin-top: 0px !important; 
	border-bottom: solid 30px white !important; 
	border-right: 15px solid transparent !important; 
	border-left: 15px solid transparent !important;} 
 
#left_column #posts .post .permalink 
	{background-color:#f9f9f9;} 
#left_column #posts .notification  
	{background-color:#f9f9f9 !important; 
	color:#B85858 !important; 
	border-bottom-color: #eee !important;} 
#left_column #posts .post .post_avatar 
	{-moz-border-radius: 38px !important;  
	-webkit-border-radius: 60px !important;  
	width: 64px !important; height: 64px !important;  
	margin: 0px !important;} 
#left_column #posts .post .sub_avatar_inner 
	{-moz-border-radius: 38px !important;  
	-webkit-border-radius: 60px !important;  
	width: 32px !important; height: 32px !important;  
	margin: 0px !important;} 
input, select, textarea {
   background: #fefefe;
   font: 11px 'Trebuchet MS', Helvetica, sans-serif;
   color: #a4a4a4;
   padding: 8px 10px;
   margin: 3px;
   border: 1px solid #efefef;
   -moz-border-radius: 3px;
   -webkit-border-radius: 3px;
   border-radius: 3px;
}

   input:hover, select:hover, textarea:hover {
	background: #fafafa;
	border: 1px solid #e4e4e4;
   }

   input[type=submit], input.button {
	background: url( 'http://static.tumblr.com/6zzcccz/Hwklybeh6/input-white.png' ) center;
	font-weight: bold;
	border: 1px solid #efefef;
	-moz-border-radius: 5px;
	-webkit-border-radius: 5px;
	border-radius: 5px;
	cursor: pointer;
   }
   
strong {
   color: #545454;
}

div.clear {
   clear: both;
}


iframe input, iframe submit, iframe textarea, iframe div, iframe table {background-color:transparent!important;}

::-webkit-scrollbar{width:7px;height:7px;}
::-webkit-scrollbar-button:start:decrement
::-webkit-scrollbar-button:end:increment{display:block;height:0;background-color:transparent;}
::-webkit-scrollbar-track-piece{background-color: #f7f7f7;-webkit-border-radius:0;-webkit-border-bottom-right-radius:3px;-webkit-border-bottom-left-radius:3px;}
::-webkit-scrollbar-thumb:vertical{height:50px; background-color:{color:titlebox1};-webkit-border-radius:px;}
::-webkit-scrollbar-thumb:horizontal{width:50px; background-color:{color:titlebox1};-webkit-border-radius:px;}

#sidebar{float: left; width: 454px; height:450px; background-color: #ffffff; padding: 5px; margin-left: 30px; margin-top:0px}
{block:ifFixedSidebar}#sidebar {position: fixed; padding: 0px}{/block:ifFixedSidebar}
.sidebardescr{margin: 5px 10px 5px 10px; margin-left: 253px; padding: 0 5px 0 5px; text-align: justify}
.sidebarimg {margin: 1px 10px 5px 10px; margin-left:-8px; margin-top:-7px; padding: 5px 5px 0 5px}
.sidebarimg img{width: 164px; height: auto; text-align:center}
   
img {
margin: 1px;
border: none;
filter: alpha(opacity=75);
moz-opacity:.80;
opacity:.80 ;
-webkit-transition-duration: .50s;
}

img:hover {
margin: 1px;
border: none;
filter: alpha(opacity=100);
moz-opacity:1.0;
opacity:1.0;
}

body, a:hover {cursor: url(http://free-themes.info/cursor/misc/mustache.png), progress !important;}
