/* by hkr (dreamflame in) */
@-moz-document url("about:newtab"){
  * {
    text-shadow: 0px 0px 3px rgba(0,10,20,1), 0px 0px 5px rgba(0,10,20,1), 0px 0px 12px rgba(0,10,20,1) !important;
    -moz-transition: opacity .4s ease-out, color .4s ease-out, background-image .4s ease-out, box-shadow .4s ease-out !important;
  }
  div#newtab-scrollbox {
    background: #1e2840 top left repeat url("data:image/png;base64,") !important;
    box-shadow: inset 0px 0px 8px 2px rgba(0,0,0,1), inset 0px 0px 200px 10px rgba(0,0,0,1) !important;
    outline: none !important;
    border: 0px !important;
  }
  div#newtab-grid, div.newtab-row {
    overflow: visible !important;
    width: 720px !important
  }
  div.newtab-cell {
    outline-color: transparent !important;
    margin: 8px !important;
    background: rgba(0,10,30,.2) !important;
    box-shadow: inset 0px 0px 10px rgba(0,0,0,.5), 0px 0px 0px 1px rgba(200,225,255,.06), 0px 0px 10px rgba(200,225,255,.04) !important;
    border-radius: 10px !important;
  }
  div.newtab-cell:hover { outline-color: rgba(255,255,255,.6) !important; }
  div.newtab-cell { border-radius: 9px !important; }
  a.newtab-link, span.newtab-thumbnail { border-radius: 8px !important; }
  a.newtab-link {
    outline-color: transparent !important;
    background: -moz-radial-gradient(center bottom, ellipse farthest-side, rgba(50,80,200,1), rgba(30,60,175,0) 80%), -moz-linear-gradient(top, rgba(30,60,175,.1) 0%, rgba(25,55,255,.3) 60%, rgba(90,120,255,.4) 100%), top left repeat url("data:image/png;base64,") !important;
    box-shadow: inset 0px 0px 30px rgba(0,10,20,1), 0px 0px 10px rgba(0,10,20,1) !important;
    border: 1px solid rgba(0,0,0,1) !important;
    opacity: .8 !important;
  }
  a.newtab-link:focus { box-shadow: inset 0px 0px 30px rgba(200,225,255,.3), 0px 0px 10px rgba(0,10,20,1) !important; }
  a.newtab-link:hover, a.newtab-link:focus { opacity: 1 !important; }
  span.newtab-title { box-shadow: 0px 0px 10px rgba(0,0,0,1) !important; }
  span.newtab-title {
    background: -moz-linear-gradient(center top, rgba(255,255,255,.01) 0%, rgba(0,0,0,.3) 4%, rgba(0,0,0,.9) 100%) center bottom repeat !important;
    height: 29px !important;
    padding-top: 2px !important;
    border: none !important;
    border-top: 1px solid rgba(0,0,0,.7) !important;
    border-radius: 0px 0px 6px 6px;
    color: rgba(200,225,255,1) !important;
  }
  span.newtab-title { opacity: .5 !important; }
  span.newtab-title:hover { opacity: 1 !important; }
  .newtab-control:-moz-focusring, .newtab-site:hover > .newtab-control { opacity: .8 !important; }
  .newtab-site[dragged] { background-color: transparent !important; }
}
