.below_threadlist .newcontent_textcontrol {
    margin-left: 20px !important;
}

.forumhead .forumtitle {
    margin: 0 !important;
    margin-top: 2px !important;
}

.forumhead .forumtitle a {
    padding: 0 !important;
}

#postpagestats_above {
    margin-top: 5px !important;
    clear: both !important;
}

blockquote a {
    color: #004080 !important;
    text-decoration: underline !important;
}

.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols {
    background-color: #EFEFEF !important;
}



.postbitlegacy .postfoot .postcontrols, .postbit .postfoot .postcontrols {
    padding-right: 0 !important;
}

.textcontrols .postcontrols .seperator {
    display: none !important;
}

.textcontrols .postcontrols a {
    margin: 0 !important;  
}

.textcontrols .postcontrols {
    width: auto !important;
    float: left !important;
    margin-left: 210px !important;
}

.textcontrols .postlinking {
    float:left !important;
    left: 0 !important;
}

.postbitlegacy .postfoot .textcontrols a.multiquote, .postbit .postfoot .textcontrols a.multiquote {
    padding: 4px 4px 4px 24px !important;
}

.postlinking .report {
    margin-left: 0 !important;
    padding: 4px 8px !important;
    background-color: #EFEFEF !important;
}

.postlinking .seperator {
    display: none !important;
}

.forumbit_post .forumdescription {
    font-size: 12px;
    line-height: 130% !important;
}

#sidebar_container {
    padding: 10px 0 !important;
}

.postbitlegacy .postfoot .textcontrols a.multiquote, .postbit .postfoot .textcontrols a.multiquote {
    background-color: #EFEFEF !important;
}

.postbitlegacy .postfoot .textcontrols a.newreply, .postbit .postfoot .textcontrols a.newreply {
    background-color: #EFEFEF !important;
}

.postbitlegacy .postfoot .textcontrols a.quickreply, .postbitlegacy .postfoot .textcontrols a.editpost, .postbit .postfoot .textcontrols a.quickreply, .postbitlegacy .postfoot .textcontrols a.forwardpost, .postbit .postfoot .textcontrols a.forwardpost {
    background-color: #EFEFEF !important;
}

.isuser {
    background-color: #eaeaea !important;
    padding: 5px 0 3px 0 !important;
    
}

.navtabs li:first-child {
    border: 0 !important;
}

.navtabs li {
    border: 0 !important;
}

.navtabs li a.navtab {
    background: none !important;
}

.navtabs li.selected a.navtab {
    background-color: #951718 !important;
}

.postbit .postfoot, .postbitlegacy .postfoot, .eventbit .eventfoot {
    border-top: 1px solid !important;
}

.globalsearch .textboxcontainer {
    border-left: 0 !important;
}

.navbar_search input {
    padding: 2px !important;
}

.globalsearch .buttoncontainer {
    border-left: 0 !important;
}

.above_postlist .pagination_top .pagination, .above_postlist .pagination_top .postpagestats {
    margin-top: 5px !important;
}

.navtabs .popupbody {
    background: #FFF !important;
}

.navtabs li.selected li:hover a.popupctrl {
    background-color: #EAEAEA !important;
}

.isuser .active, .isuser a:hover {
    background-color: #c5c5c5 !important;
}

.floatcontainer li.popupmenu a:hover {
    color: #000 !important;  
}

.floatcontainer li.popupmenu a {
    color: #000 !important;  
}

