.formcontrols {
    overflow: hidden;
}


.forum_info {
    margin-bottom: 0 !important;
    top: 0 !important;
    float:none !important;
}


.forum_info {

    
}

#forum_info img {
    margin-top: 2px !important;
}

#forum_onlineusers, #forum_display_options, #icon_legends, #posting_rules {
/*    border-left: 1px solid !important;
    border-right: 1px solid !important;
    border-bottom: 1px solid !important;
    overflow: hidden;*/
}
.threadlisthead {
    background: none !important;
    background-color: #D8D4D5 !important;
    color: #000 !important;
}

.forum_info .options_block_container, .thread_info .options_block_container {
    margin-bottom: 5px !important;
}

.threadbit .threadlastpost dd {
    height: auto !important;
    line-height: 100% !important;
    margin: 0 !important;
    overflow: visible !important;
    white-space: normal !important;
}

.forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
    background: none !important;
    background-color: #f5f5f5 !important;
}

.threadbit .sticky {
    background: none !important;
}

.threadbit .alt {
    background: none !important;
    background-color: #D5D5D5 !important;
}


.announcements .announcerow {
    box-shadow: -1px 1px 0 #C8C8C8 !important;
}
.threadbit .nonsticky, .threadbit .discussionrow {
    background: none !important;
    background-color: #d4d4d4 !important;
}

.wgo_block .blockbody, #thread_onlineusers, #thread_tags_list, #posting_rules, #forum_onlineusers, #forum_display_options, #icon_legends  {
    background-color: #f5f5f5 !important;
}

