css:
@-moz-document domain("erepublik.com")
{
body#country .indent{background-color:#ddd !important;opacity:0.9!important}
*{text-shadow : 0px 0px 0.1px  #000  !important}
a{color:#FF6900!important}
span{color:#D139FE!important}
h1{color:#FF9953 !important}
h2{color:#FF8836 !important}
h3{color:#FF700E !important}
h4{color:#FF6800 !important}
h5{color:#DF5B00 !important}
h6{color:#BD4D00 !important}
p{color:#3C3AFF !important}
td.m_stock{color:#783100 !important}
small{color:#FF0000 !important}
span.eday>*{color:#010068 !important}
div.header_info>*{background-color:#ddd !important}
div.msg_body{background-color:#ddd !important}
/*背景圖*/

body{
background-image: url("http://i.imgur.com/2dCnB.jpg")!important;

background-repeat: no-repeat ;background-attachment: fixed !important;

background-position: center center;background-color:#000 !important }



/*頁面底部連結*/

div#footer{background:none !important}



/*頭像欄*/

div#large_sidebar{background-color:#fff !important}




/*shout牆*/

#citizen_feed.duo .wall_post{width:435px !important}

#citizen_feed.duo .post_content{width:370px !important}

div.inner,div#citizen_feed.duo{width:450px !important}

div.post_content{float:right !important}

div.inner>*>*>*>*>*>*>*>*>p{font-size:1.3em !important;color: #1589FF !important}

div.inner>*>*>*>*>p{font-size:1.3em !important;color: #1589FF !important}

#citizen_feed .vote_bar{background-color:#000 !important}

#citizen_feed.duo{background:none !important;opacity:0.85!important}

div.inner{background-image:none !important;background-color:#000 !important;opacity:0.85!important}

.articlecomtent{display:block !important;}




/*首頁NEWS欄*/


/*倉庫*/

div.items_holder{background-image:none !important}



/*就業市場*/

tr.alt>*{background:none !important}



/*輸入文字區*/

textarea{font-size:1.3em;background-color:#ddd !important}


}



@-moz-document url-prefix("http://www.erepublik.com/en/military/battlefield"){

/*戰鬥頁面*/

div#pvp{background-image:url("http://i.imgur.com/Gr8ks.jpg") !important;background-position:center!important}

div.mask.left_side, div.mask.right_side{background-color:#000000 !important;opacity:0.8!important}

strong#fighter_skill, strong#enemy_skill, em{background:none  !important}

div.accountdisplay{background-color:#463E41}

h4{color:#fff !important}
strong#fighter_skill,strong#enemy_skill{color:#9CFFA4 !important}
}



/*報紙頁面*/

@-moz-document url-prefix("http://www.erepublik.com/en/article")
,url-prefix("http://www.erepublik.com/en/main/news") 
,url-prefix("http://www.erepublik.com/en/news"){
div.full_content, div.commentscontent, p.smallholder{font-size:18px!important;color: #3C3AFF !important}
p>*{font-size:18px!important}
div.commentscontent{float:none !important}
div.post{background-color:#ddd !important}
div#content{background-color:#eee !important;opacity:0.88!important}
i{color:#010068}
strike{color:#FF0000}
}

/*市場*/
@-moz-document url-prefix("http://economy.erepublik.com/en/market"),
url-prefix("http://www.erepublik.com/en/main/messages-inbox"),
url-prefix("http://www.erepublik.com/en/main/messages-alerts"),
url-prefix("http://www.erepublik.com/en/economy"){
table{background-color:#FFF !important;opacity:0.88!important}
tr>td{color:#010068!important}}

/*MY LAND*/

@-moz-document url-prefix("http://www.erepublik.com/en/economy/myCompanies"){

div.area{background-color:#D4E2EE !important}

div.list_group{background-color:#ddd!important}

div.area>*>*>*>*{background-color:#D4E2EE !important}

}

@-moz-document url-prefix("http://www.erepublik.com/en/economy/inventory")
{div#content>*>*>img{display:none !important}}
