/* ==UserStyle==
// @name         Len R's WRounded Dialogs/Textbox fields for Firefox 3
//			based on code by Lee Netherton
// @author       LRodman
// @version      v4.0b6
//			much more lightweight!
//
// ==/UserStyle==*/

@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@namespace html url("http://www.w3.org/1999/xhtml");


/***********************************************
            statusbar, findbar bknd
 ***********************************************/
statusbar { -moz-appearance: statusbar; border: 0px !important;}

#status-bar { 
	-moz-appearance: none !important;
	background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAYCAIAAAC0rgCNAAAAI0lEQVR42mPYdOo6w+03Xxle/fzDxMzMzPT//38mBgYGamEAuugLH+6I3fwAAAAASUVORK5CYII=")!important;}

.statusbar-resizerpanel {-moz-appearance:none!important; min-width:70px!important; 
	margin-left: -56px !important;background: url("data:image/png;base64,") !important;}

#FindToolbar { -moz-appearance: none !important;  border: 0px !important; 
	height: 26px !important; background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAcCAIAAAAvP0KbAAAAL0lEQVR42qXFsQ0AIAwDQcuTZ2JESZGPKRiB4nSqKiUxE8O7GUPcxDBm4rWPJf264KQfT7gX6iMAAAAASUVORK5CYII=");}




/*****************************************
           fields (tbx, mbx)
 *****************************************/
/* Right */
textbox:not([multiline="true"]):not([readonly="true"]),
menulist, menulist >dropmarker.menulist-dropmarker /*replace dropmarker img*/
	{-moz-appearance: none !important;
	border: 0px!important;
	padding: 0px !important;
	margin:0px!important;
	background:url("data:image/png;base64,") no-repeat right center !important;
   	height: 22px !important;}

/* Left */
textbox:not([multiline="true"]):not([readonly="true"])> hbox.textbox-input-box,
textbox:not([multiline="true"])> hbox.autocomplete-textbox-container,
menulist>hbox.menulist-editable-box, menulist>hbox.menulist-label-box,
label[control="namePicker"],/*bookmark properties*/
label[control="editBMPanel_namePicker"] /*ctl-d*/ 
	{-moz-appearance: none !important;
	border: none !important;
	padding: 0px !important;
	margin: 0px !important;
	background:url("data:image/png;base64,") no-repeat !important;
	margin-right: 8px !important;
   	height: 22px !important;}


/* Middle */
textbox:not([multiline="true"]):not([readonly="true"])>hbox >html|input.textbox-input, 
textbox:not([multiline="true"])> hbox > hbox.textbox-input-box,
menulist > hbox.menulist-editable-box > html|input.menulist-editable-input,
menulist > hbox.menulist-label-box > label.menulist-label,
#abpEditSubscription>grid>rows>#titleRow>#title,/*abp add sub*/
menulist[flex="1"][editable="true"][sizetopopup="pref"][height="22"],/*abp add fltr*/
menulist#namePicker>hbox.menulist-editable-box, /*bmk prop*/ 
#editBMPanel_namePicker>hbox.menulist-editable-box,/*bmark ctl-d*/
#cookies-popup-expires,/*cslite cookie editor RIGHT*/
#feed-button,#star-button,#go-button, .search-go-button {
	-moz-appearance: none !important;
	border: none !important;
	padding: 0px !important;
	background:url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAWCAIAAACOpGH9AAAAIklEQVR42mOYOXMmk7q6OsP///+ZGBgYKMYfP35kevfuHQDGRwsDnOCXtAAAAABJRU5ErkJggg==") repeat-x !important;
   	height: 22px !important;
	-moz-border-radius: 5px !important;
	margin-right: -2px !important;
	margin-left: 6px !important;
	padding-top: 2px !important;}




/*********************************
      focused menulist: bold
*********************************/
menulist:focus > hbox.menulist-editable-box > html|input.menulist-editable-input,
menulist:focus > hbox.menulist-label-box > label.menulist-label {
	font-weight: bold !important;
	color: -moz-FieldText !important;}

/*********************************
           ML dropmarker
**********************************/
menulist > dropmarker.menulist-dropmarker {/*replace dropmarker img*/
	margin-left: -8px !important;
	padding-right: 3px !important;/*move dropmarker left so it aligns with glow when selected*/}

menulist:focus>dropmarker.menulist-dropmarker,      /*glow dropmarker behind selected*/
menulist:active>dropmarker.menulist-dropmarker{
	background: transparent url(chrome://browser/skin/Search-addengines.png) no-repeat 1px center!important;}

/*********************************
            menulist
**********************************/

menulist, /* Right */
menulist > hbox.menulist-editable-box, /* Left */
menulist > hbox.menulist-label-box {
   /*	margin-left:0px!important;	padding-left:0px!important;  */
	margin-right:0px!important;}





/*****************************************************
                    Custom Fixes
*****************************************************/

/* LRerr: round (urlbar, srchbar, bmk-prop, ctl-d-nm-fldr, ext srch)*/
#urlbar >hbox >hbox,#feed-button,#star-button,#go-button
,#searchbar >textbox >hbox  >hbox, .search-go-button
 /*,.actionsMenu>.menulist-label-box >.menulist-label*/
,menulist#namePicker>hbox.menulist-editable-box
,#editBMPanel_namePicker>hbox.menulist-editable-box
	{margin-right: 0px !important;
	margin-left: 8px !important;
	-moz-border-radius: 0px !important;}

menulist.actionsMenu ,#cookies-filter  /*options-mime, CSL-edit-viewfor*/
,#tfText1 >hbox, #extensionsBox>#searchPanel>#searchbox  /*dom-find, extsrch*/
	{margin-top:1px !important;} /*pushes L+M down*/

#extensionsBox>#searchPanel>#searchbox>button
	{ display: none !important;}   /*extsrch hide glass*/

#siteTextbox, #image   /*LRerr: L+7 (flashblock, custbtn)*/
  {margin-top:7px !important;	margin-bottom: 5px !important;}/*middle/right->up*/

menulist.actionsMenu>hbox>image{
	margin-left:3px !important;/*LRerr: LIco-3 L-shift*/
	margin-right:-4px !important;}/*LRerr: MT+4 R-shift*/

#ifl_expires:not([readonly="true"]) ,#lookupcriterium {/*AnEC cookie editor*/        
	margin-top:5px !important;  margin-bottom:2px !important;}
#lookupcriterium {margin-bottom:6px!important;}   /*push L and M down*/



/****************************************************
            bm-panel (ctl-d popup), feeds
 ****************************************************/
.actionsMenu>.menulist-label-box >.menulist-label  /*optn-mime*/
,#editBMPanel_folderMenuList >hbox>label
,#handlersMenuList >hbox>label /*subscribe feed dropbox*/
	{padding-left:14px !important; /*create white space left of txt*/
	margin-left:-8px !important;}  /*push txtbox to the right*/

#handlersMenuList >hbox >image,#editBMPanel_folderMenuList >hbox >image{
	margin-left:5px !important;    /*LRerr: LIco-3 L-shift*/
	margin-right:-6px !important;}    /*LRerr: MT+4 R-shift*/

#editBMPanel_namePicker>hbox.menulist-editable-box > html|input  
,#namePicker > hbox.menulist-editable-box > html|input 
	{ margin-left:-4px!important;}  /*pull txt L to cover new L img*/

#editBMPanel_namePicker >hbox.menulist-editable-box,/*bmark ctl-d*/
menulist#namePicker >hbox.menulist-editable-box{
	padding-right: 8px !important; /*show R img(push R img R past changed tbx)*/
	padding-top: 0px !important;} /*LRerr: MT extends+12R*/

#handlersMenuList >hbox {margin-top:2px !important;}/*moves mid and left down*/

#editBMPanel_tagsRow{margin-top:-3px!important;}/*brings closer together*/

label[value="Name:"][accesskey="N"][control="namePicker"],/*bookmark properties*/
label[value="Name:"][accesskey="N"][control="editBMPanel_namePicker"]/*ctl-d*/{/*Left*/
	padding-left:6px !important;/*align label*/
	margin-right:-9px !important;/*align image*/
	background:url("data:image/png;base64,") no-repeat right center !important;}





/***************************************************
            CS-Lite cookie editor, ABP
****************************************************/

#abpEditSubscription>grid>rows>#titleRow>#title,/*abp add sub*/
#listEditor[editable="true"][sizetopopup="pref"],/*abp add filter R*/
#cookies-popup-expires{/*cslite cookie editor middle*/
	padding-bottom:1px !important; /*push up*/
	/*margin-right:0px !important;*/
	-moz-border-radius-topright: 12px !important;
	-moz-border-radius-bottomright: 12px !important;}
#listEditor[editable="true"][sizetopopup="pref"]>hbox,/*abp-flt-L*/
#cookies-popup-expires>.menulist-editable-box,
#abpEditSubscription>grid>rows>#titleRow>#title>.menulist-dropmarker{/*left*/
	margin:-2px 12px -2px -6px !important;}

#cookies-popup-expires>.menulist-dropmarker { margin-top:-1px !important;}

menulist[flex="1"][editable="true"][sizetopopup="pref"][height="22"]>.menulist-dropmarker
	{margin-bottom:1px !important;} /*abp add flt*/

#abpEditSubscription>grid>rows>#titleRow>#title>.menulist-editable-box
	{margin-left:-9px !important;}  /*abp add sub*/

#abpEditSubscription>grid>rows>#titleRow>#title>.menulist-dropmarker
	{margin-top:0px !important;
	margin-right:-3px !important;}




/**************************************************
          number boxes with spinbuttons
***************************************************/
/*
textbox[type="number"][size]>hbox.numberbox-input-box.textbox-input-box
	{padding-bottom: 4px !important;}     fix for 96dpi*/
	/*margin-right:9px!important;         pulls R side and buttons in*/

spinbuttons
	{margin-right:-13px!important;}/*pulls R side and label in*/
textbox[type="number"][size]
	{margin-right:12px!important;}/*moves label over*/
/*textbox[type="number"][size]>.numberbox-input-box.textbox-input-box>html|input{}*/





/*********************************
          Save dialog
*********************************/

#favlocList{margin-bottom:-1px !important;}
#favlocList > hbox{	margin-top:4px !important;}/*pushes left down*/
#favlocList >hbox > label {	margin-bottom: 4px !important;}  /*pushes middle up*/

#openHandler > hbox{ margin-top:6px !important;}
#openHandler >hbox > label  /*pushes middle up; left*/
	{ padding-bottom: 6px !important;  margin-left: 2px !important;}   



/************************************
              URL bar
************************************/

#urlbar > #identity-box {
	margin-left:0px !important;
	border: 1px solid #bbb !important;
	border-left-style: none !important;
	border-top: 2px solid !important;
	-moz-border-top-colors: #999999 #c0c0c0!important;
	-moz-border-radius-topleft: 6px !important;
	border-bottom: 2px solid !important;
	-moz-border-bottom-colors: #dadada #e8e8e8 !important;
	-moz-border-radius-bottomleft: 8px !important;}
#urlbar > #identity-box>hbox{
	padding-top:2px !important;
	padding-bottom:2px !important;}

/* Middle */
#urlbar hbox.autocomplete-textbox-container > hbox.textbox-input-box {
	margin-left:0px !important;
	padding-left:3px !important;
	padding-right:3px !important;}

/* Right */
#feed-button,#star-button,#go-button,.search-go-button 
	{margin:0px !important; /*prev blank spot L*/
	padding:3px 2px 3px 1px!important;}/*prev vert str*/
#urlbar > dropmarker.autocomplete-history-dropmarker {
	background: none !important;
	border:none !important;
	margin-left: -15px !important;
	padding-top: 1px!important;}
#urlbar > dropmarker.autocomplete-history-dropmarker:hover {
	background: transparent url(chrome://browser/skin/Search-addengines.png) no-repeat 2px 4px!important;}





/**********************************
           Search-Bar
**********************************/

.searchbar-engine-button {
	background: none !important;
	border:none !important;
	margin-left: -4px !important; /*pulls image L*/
	margin-right: -2px !important;
	margin-top: -1px !important;}
.search-go-button /*pulls glass right; eliminates blankspot*/
	{margin-left: -1px !important;
	margin-right: -5px !important;
	-moz-border-radius-topright: 9px !important;
	-moz-border-radius-bottomright: 9px !important;}
.searchbar-dropmarker-image {margin-left:2px !important; margin-right:-1px !important;}

.searchbar-engine-button:hover>hbox {
	background: transparent url(chrome://browser/skin/Search-addengines.png) no-repeat 18px 1px !important;}





