css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.nicovideo.jp/watch/") {

/* [MAIN] */
#PAGEMAIN /* to extend your display and move a few pixels downward */
{position:absolute; top:55px; width:80% !important;}
#PAGEHEADER /* to move the search bar and logo to the right */
{position:absolute; left:1000px; width:50% !important;}
#WATCHHEADER /* to move director's explanations to the right */
{position:absolute; top:70px; left:1000px !important;}


/* [WATCHHEADER]                                     All belows are belong to WATCHHEADER's xD
                                                             We should move or return to the left to read easier. */
#video_controls /* to move registered tags to the left */
{position:absolute; left:-1000px; top:480px; width:130% !important;}

                    /* to move "Mylist buttons" to the left, of course */
#WATCHHEADER > [style="float:right; width:256px; padding:4px;"]
{position:absolute; left:-300px; top:550px !important;}

                    /* to move the button that is direcotor's profile or to watch other movies by him/her */
#WATCHHEADER > [style="width:256px; overflow:hidden; float:right; padding:4px;"]
{position:absolute; left:-1000px; top:550px !important;}


/* [OTHERS except WATCHHEADER] */
embed#flvplayer /* to move the player upward */
{position:absolute; top:10px; left:10px; display:block; margin:1px !important;}
#player_bottom_share /* to move the button to share */
{position:absolute; left:520px !important;}


/* [DLT e3] */
.tag_sync_wrap, /* f*cking pop-up */
.des_2 > p, /*the button to omit the director's explanation*/
#WATCHFOOTER > table.font12, /* things nobody need, are the melody calls and voices */
#PAGEFOOTER, /* garbage ward so called downward */
#head_ads, /* because we hate ads */
#ichiba_placeholder /* If you were to be a slave for NicoNico, you could remove this part */
{display:none !important;}
}
