/* ==UserStyle==
// @name         LR Well Rounded search and text boxes
// @author       LRodman
// @version      2.7
// ==/UserStyle==*/

@namespace html url(http://www.w3.org/1999/xhtml);

.t,/*goog*/
h4,/*yfantasy*/
.hd, td.ttb, td.rsb, td.bts, .page-title, td#headerText, 
table [bgcolor="#e5ecf9"], table [bgcolor="#d5ddf3"]
  { border-top:0px!important; background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAYCAIAAAC0rgCNAAAAI0lEQVR42mPYdOo6w+03Xxle/fzDxMzMzPT//38mBgYGamEAuugLH+6I3fwAAAAASUVORK5CYII=") top left repeat-x !important; }


/*********************************
          XL - 400px
*********************************/
INPUT[type="text"][maxlength="200"][name="query"][class="text"][style="display: block;"],/*ms*/
input[size="41"][name="q"],/*google*/
INPUT[size="40"][name="q"],
input[type="text"][size="31"][name="q"],
INPUT[title="Google Search"][size="55"][name="q"],
input[type="text"][size="70"][title="Search"],
INPUT[size="50"][name="q"][maxlength="256"],
INPUT[type="text"][name="q"][id="q"][class="input-search"],/*gvid*/
input[type="text"][style="width: 33em;"],/*google maps*/
INPUT[type="text"][class="MtafId"],/*gmail*/
INPUT[type="text"][maxlength="128"][name="search_query"],/*ytube*/
/*INPUT[type="text"][size="50"][name="field-keywords"][class="searchSelect"],amzn*/
input#twotabsearchtextbox,/*amzn*/
input[type="text"][class="plong inputtext"],/*yahoo*/
input[type="text"][size="50"][name="satitle"],/*ebay*/
input[type="text"][size="60"][name="satitle"],/*ebay*/
INPUT[name="EntrezSystem2.PEntrez.Pubmed.SearchBar.Term"],/*pmed*/
input[type="text"][size="60"][id="searchTextBox"],/*ign*/
input[name="query"][size="60"][id="querybox"],/*ign*/
input[type="text"][size="50"][id="style_short_description"],/*userstyles*/
input[name="search"][size="50"][style="margin-left: 0px;"],/*wiki*/
INPUT[type="text"][name="q"][class="inputbox"],/*pbay*/
INPUT[type="text"][name="q"][style="margin: 6px; width: 390px;"],/*pbay*/
input[type="text"][style="width: 110px;"]/*amf*/
	{-moz-appearance: none !important;
	border:none !important;
	height:22px !important;
	padding-left:9px !important;
	margin-left:-5px !important;
	padding-right:10px !important;/*prevents overflow - IMPORTANT!!!*/
	padding-top:3px !important;
	margin-top:2px !important;
	margin-bottom:-4px !important;
	width:400px !important;
	background:url("data:image/png;base64,")no-repeat!important;}


input[type="text"][class="plong inputtext"][id="p"]{/*yahoo fix - only mainpage*/
	padding-right:295px !important;	width:380px !important;}

input[type="text"][size="50"][name="satitle"],/*ebay*/
input[type="text"][size="60"][name="satitle"]/*ebay*/
	{margin-top:0px !important;
	padding-top:4px !important;}
input[type="text"][size="50"][name="satitle"]   {width:382px!important;}

INPUT[name="EntrezSystem2.PEntrez.Pubmed.SearchBar.Term"],/*pmed*/
INPUT[type="text"][class="MtafId"]{/*gmail*/
	margin-left:3px !important;
	width:382px!important;}


INPUT[title="Google Search"][size="55"][name="q"],
INPUT[size="50"][name="q"][maxlength="256"]/*goog*/
	{margin-top:3px !important;
	margin-bottom:3px !important;
	width:382px!important;
	padding-right:16px !important;}

INPUT[size="41"][name="q"][type="text"]
	{margin-top:3px !important;
	margin-bottom:3px !important;}


@-moz-document url-prefix("http://www.google.com/products?")   /*gproducts but NOT regular*/
	{  INPUT[size="31"][name="q"][type="text"],
	   INPUT[size="41"][name="q"]{
	   margin-top:3px !important;
	   margin-bottom:3px !important;
	   width:382px!important;
	   padding-right:16px !important;}   }

input[type="text"][style="width: 33em;"]/*gmaps fix*/
	{padding-left:6px !important;
	margin-left:3px !important;
	width:382px!important;
	padding-right:16px !important;}


input#twotabsearchtextbox,/*amzn*/
input[type="text"][size="60"][id="searchTextBox"]{/*ign fix*/
	margin-left:8px!important;
	margin-right:-10px !important;
	margin-top:-2px !important;}
input[name="query"][size="60"][id="querybox"]{/*ign fix*/
	margin-left:-40px !important;
	margin-right:-12px !important;
	margin-top:-2px !important;}

input[type="text"][size="50"][id="style_short_description"]{/*userstyles*/
	margin-left:1px!important;
	margin-bottom:-7px !important;}

INPUT[type="text"][name="q"][class="inputbox"]{/*pbay*/
	width:382px!important;
	padding-right:12px !important;
	margin-left:4px !important;
	margin-top:1px !important;}

input[name="search"][size="50"][style="margin-left: 0px;"]{/*wiki*/
	margin-left:2px!important;
	margin-top:-3px!important;
	margin-right:-3px !important;
	width:382px!important;
	padding-right:12px !important;}


input[type="text"][style="width: 110px;"]  { margin-left:-300px!important;}/*amf fix*/

INPUT[type="text"][size="50"][name="field-keywords"][class="searchSelect"],/*amzn*/
INPUT[type="text"][maxlength="200"][name="query"][class="text"][style="display: block;"]/*ms*/{
	margin-left:2px!important;
	margin-top:-3px!important;}

INPUT[type="text"][maxlength="128"][name="search_query"]{/*ytube*/
	padding-top:3px !important;
	width:382px!important;
	margin-left:-50px!important;}





/*********************************
        large - 280px
*********************************/
INPUT[type="text"][name="q"][autosave="com.facebook.ubersearch"][maxlength="256"][class="inputtext"],/*facebook topsearch*/
INPUT[type="text"][id="search"],/*taboo*/
INPUT[size="28"][name="q"],/*imdb*/
INPUT[maxlength="255"][name="qu"][id="msviSearchBox"],/*ms dls*/
INPUT[type="text"][class="textinput"][maxlength="60"][name="freetext"],/*ms dls*/
INPUT[type="textbox"][name="q"],/*goog reader*/
INPUT[type="text"][name="q"][style="width: 48%;"],/*moz addons*/
INPUT[type="text"][name="data[Login][email]"],/*moz addons*/
INPUT[type="password"][name="data[Login][password]"],/*moz addons*/
select[name="sacat"],/*ebay droplist*/
SELECT[tabindex="2"][size="1"][name="category0"][id="category0"],/*ebay*/
INPUT[type="text"][size="28"][maxlength="2048"],/*gmail*/
INPUT[size="20"][class="textareawhite"],/*tusk*/
INPUT[type="text"][class="quicksearch"][name="searchterms"]/*studentconsult*/
	{-moz-appearance: none !important;
	border:none !important;
	height:22px !important;
	padding-left:7px !important;
	margin-left:-3px !important;
	padding-right:10px !important;
	margin-right:-5px !important;
	padding-top:1px !important;
	margin-top:0px !important;
	margin-bottom:-4px !important;
	width:280px !important;
	background:url("data:image/png;base64,")no-repeat!important;}


INPUT[size="28"][name="q"],/*imdb*/
INPUT[maxlength="255"][name="qu"][id="msviSearchBox"],/*ms dls*/
INPUT[type="text"][class="textinput"][maxlength="60"][name="freetext"]/*ms dls*/{
	margin-top:-5px !important;
	padding-top:4px !important;/*move box up, text down (relative)*/
	margin-left:0px !important;
	margin-right:0px !important;}

INPUT[type="text"][name="q"][autosave="com.facebook.ubersearch"][maxlength="256"][class="inputtext"],/*facebook topsearch*/
INPUT[type="text"][id="search"],/*taboo*/
INPUT[type="textbox"][name="q"],/*greader*/
INPUT[type="text"][class="quicksearch"][name="searchterms"]/*studentconsult*/
	{width:270px !important;
	padding-top:4px !important;}

INPUT[type="text"][name="q"][style="width: 48%;"],
INPUT[type="text"][value=""][name="data[Login][email]"],
INPUT[type="password"][value=""][name="data[Login][password]"]{/*moz addons*/
	padding-top:2px!important;
	font-size: 1.3em !important;
	width:265px!important;
	padding-right:12px !important;}
	LABEL[for="query"][style="display: inline;"]{
	font-size: 1.3em !important;
	padding-top:0px!important;}




/********************************************
        medium - 170px
*********************************************/
INPUT[type="text"][name="q"][id="q"],/*userscripts-gmonkey*/
input[size="20"][id="search-terms"],/*userstyles*/
SELECT[title="Search in"][class="searchSelect"][name="url"],
SELECT[name="fsop"],/*ebay*/
SELECT[name="pocId"]/*ms dls*/
	{-moz-appearance: none !important;
	border:none !important;
	height:22px !important;
	padding-left:5px !important;
	margin-left:3px !important;
	padding-right:7px !important;
	margin-right:3px !important;
	padding-top:1px !important;
	margin-top:0px !important;
	margin-bottom:0px !important;
	width:170px !important;
	background:url("data:image/png;base64,")no-repeat!important;}


INPUT[type="text"][name="q"][id="q"],/*userscripts-gmonkey*/
input[size="20"][id="search-terms"]/*userstyles topsearch*/
	{margin-bottom:-7px !important;
	margin-top:-2px !important;
	padding-top:5px !important;}


/*********************************
           small-sized - 140px
*********************************/

SELECT[name="s"],/*imdb*/
DIV[unselectable="on"][tabindex="0"][class="QOD9Ec"],/*gmail*/
INPUT[type="text"][class="u4Qprd"],/*gmail*/
SELECT[name="cat"][id="category"],/*mozadd dropbox*/
INPUT[type="text"][maxlength="100"][size="11"][name="p"],/*yfantasy*/
INPUT[type="text"][accesskey="f"][title="Search Wikipedia [alt-f]"][name="search"],
INPUT[type="text"][value="Start new search"][size="20"][name="satitle"],/*ebay*/
INPUT[value="a"][name="satitle"][maxlength="100"][size="26"],/*ebay*/
SELECT[name="sargn"],SELECT[name="sabfmts"],SELECT[name="saobfmts"],
SELECT[name="ftrt"],SELECT[name="ftrv"], input[type="text"][size="15"][name="satitle"],/*ebay*/
