	border:none !important;
	height:22px !important;
	padding-left:7px !important;
	margin-left:-3px !important;
	padding-right:10px !important;
	margin-right:-1px !important;
	padding-top:1px !important;
	margin-top:0px !important;
	margin-bottom:0px !important;
	width:145px !important;
	background:url("data:image/png;base64,")no-repeat !important;}

SELECT[name="s"]  {margin-left:3px !important;}/*imdb*/


INPUT[type="text"][value="Start new search"][size="20"][name="satitle"],/*ebay*/
INPUT[value="a"][name="satitle"][maxlength="100"][size="26"],/*ebay*/
input[type="text"][size="15"][name="satitle"],/*ebay*/
INPUT[type="text"][accesskey="f"][title="Search Wikipedia [alt-f]"][name="search"]{
	width:126px  !important;}

SELECT[name="cat"][id="category"]{/*mozadd dropbox*/
	padding-top:2px!important;
	font-size: 1.3em !important;}

DIV[unselectable="on"][tabindex="0"][class="QOD9Ec"],/*gmail*/
INPUT[type="text"][class="u4Qprd"]{/*gmail*/
	padding-top:3px !important;
	margin-top:-4px !important;
	margin-left:-4px !important;}

INPUT[type="text"][size="25"][maxlength="100"][name="q"][autocomplete="on"][autosave=""][results="0"]{/*facebook*/
	padding-top:4px !important;
	margin-left:-4px !important;
	width:126px  !important;}




/************************************************************************************************
        selector fix: move up 1px; center selector, remove top/bottom white over roundbox
*************************************************************************************************/
SELECT[title="Search in"][class="searchSelect"][name="url"], /*amzn*/
SELECT[name="pocId"],/*ms dls (170px)*/
SELECT[name="s"]/*imdb*/
	{padding-top:3px !important;
	margin-top:-1px !important;
	padding-bottom:2px !important;
	margin-bottom:-1px !important;} 
	SELECT[title="Search in"][class="searchSelect"][name="url"]{
	margin-bottom:-2px !important;} /*amzn*/

select[name="sacat"],SELECT[name="sargn"],SELECT[name="sabfmts"],SELECT[name="fsop"],
SELECT[name="saobfmts"],SELECT[name="ftrt"], SELECT[name="ftrv"], /*ebay droplist (280px)*/
SELECT[tabindex="2"][size="1"][name="category0"][id="category0"]/*ebay (280px)*/
	{padding-top:3px !important;
	margin-top:-1px !important;
	padding-bottom:2px !important;
	margin-bottom:-5px !important;
	margin-right:0px !important;
	margin-left:0px !important;} 
	SELECT[name="ftrt"]  {margin-bottom:1px !important;}

