css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {



#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members{

background:#2e8b57 url(http://static.tumblr.com/rodxhpu/5rMm1d4a7/tm-por-afd.png) no-repeat center bottom fixed !important

}




#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: rgba(10,10,10,0.6) !important;

}



#dashboard_edit_post{

background-color: #000000!important

}



#right_column .dashboard_nav_item {

border-bottom:0px solid #eee !important;

background-image:none  !important

}



#right_column .dashboard_nav_item .dashboard_nav_title,

                    #right_column .dashboard_nav_item .dashboard_nav_title a,

                    #right_column .dashboard_nav_item a,

                    #right_column .dashboard_nav_item ul.dashboard_subpages,

                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {

                        color:#fff !important;

}



#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {

                        color:#888 !important ;

                    }



#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {

                        background: none !important;

                    }

#left_column #posts .notification .avatar {

                        border-color:transparent !important;

                        background-color:#2d504e!important;

opacity:0.5 !important;

                    }



#left_column #posts .notification .avatar:hover{

opacity:1 !important;

                    }

.post{opacity:0.8 !important; border-bottom:0px !important; color:#333}

.post:hover{opacity:3 !important}

.permalink{background-color: rgba(10,10,10,0.9) !important; background-image:none !important}

.active{background-color:#ffffff!important}

.nav_item_nipple{display:none !important}

.nav_menu{background-color:#2d5e51!important; border-bottom:0px !important}



#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

background-color:#8c0010!important;

background-image:none !important;

border:0px !important

}



#nav .nav_item .nav_menu {

border-bottom:0px !important

}



#nav .nav_item .nav_menu a {

color:#f9a035!important;

border-top-color:#e3c067!important;

}



#nav .nav_item .nav_menu a:hover {

color:#fbfbfb !important;

}



.dashboard_switch_blog_menu_header{

background-color:#1b2424!important;

color:#666 !important;

}

.dashboard_switch_blog_menu_item{

background-color:#fefefe!important;

color:#666 !important;

border:0px !important

}

.dashboard_switch_blog_menu_footer{

background-color:#51a380!important;

color:#666 !important;

}

.dashboard_switch_blog_menu_wrapper{

background-color:#2b2b2b!important;

}



#new_post_notice_container{

color:#fff !important

}



#new_post_notice_container a{

color:#fff !important

}



#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current

{

background-image:none !important

}



#footer{

background: rgba(10,10,10,0.6) !important;

background-image:none !important

}





.new_post_label_icon {

    background-image: url('http://static.tumblr.com/rodxhpu/2NLm1d3e4/allfrodash.png') !important;

    top: 9px !important;

    left: -1px !important;

}



.new_post_label {

    padding-top: 67px !important;

}



#new_post_label_text:active .new_post_label_icon {

    background-position: 2px -5px !important;

}

#new_post_label_photo:active .new_post_label_icon {

    background-position: -73px -5px !important;

}

#new_post_label_quote:active .new_post_label_icon {

    background-position: -148px -5px !important;

}

#new_post_label_link:active .new_post_label_icon {

    background-position: -223px -5px !important;

}

#new_post_label_chat:active .new_post_label_icon {

    background-position: -298px -5px !important;

}

#new_post_label_audio:active .new_post_label_icon {

    background-position: -373px -5px !important;

}

#new_post_label_video:active .new_post_label_icon {

    background-position: -448px -5px !important;






}


#left_column #posts .notification {

                        background-color:#2e8055!important;

                        color:#ffffff!important;

                        border-bottom-color: #6cbe86!important

                    }



#left_column #posts .notification .block {

                            color:#2e6e53!important;

                        }



#left_column #posts .notification.alt {

                        background-color: #2d504e!important;

border-bottom-color: #233831!important

                    }

#left_column #posts .notification.last_notification,

                    #left_column #posts .notification.single_notification {

                        border-bottom-color:#2e8956!important;

                    }

#left_column #posts .notification a {

                        color:#9c844e!important;

                        border-bottom:0px !important;

                    }

#left_column #posts .notification blockquote {

                        border-left-color:#eee !important;

                    }



#left_column #posts .notification .avatar {

                        border-color:transparent !important;

                        background-color:#fbfbfb !important;

opacity:0.7 !important;

                    }



#left_column #posts .notification .avatar:hover{

opacity:1 !important;

                    }

.notification_type_icon{display:none !important}



h1,h2,h3,h4,h5,h6{color:#232323!important}



}






body, a, a:hover {cursor: url(http://static.tumblr.com/rodxhpu/Ggtm1d3u3/cursor.png), progress;}




#logo {height: 0 !important;















	width: 0 !important;















	padding-left: 230px !important;















	padding-top: 64px !important;















	background: url(http://static.tumblr.com/rodxhpu/0eOm1d488/hv88yr.png) no-repeat !important;}




.selection_nipple.white,.selection_nipple:not([class="selection_nipple white"]){background-image:url("http://static.tumblr.com/rodxhpu/AlNm1d4f3/seletor.png")!important;background-repeat:no-repeat!important;width:40px!important;height:48px!important;margin-top:-12px!important}.selection_nipple.white{background-position:0 0!important;width:40px!important;height:48px!important}.selection_nipple:not([class="selection_nipple white"]){border-bottom:solid 11px rgba(0,0,0,0)!important}#inbox_button.tab.iconic.selected .selection_nipple:not([class="selection_nipple white"]){margin-left:-50px!important;left: 45px !important;}
