css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://matome.naver.jp/odai/") {

body {/* SHITA */
    background: #F7F7F7 !important;
}
.LyTop {/* UEのtitle部分 */
    background-color: #E5E5E5 !important;
}
h1.mdHeading01Ttl {
    font-size: 26px !important;
}


.MdMTMWidget01:hover {
    background: #EEEEEE !important;
}
.MdMTMWidget01 .mdMTMWidget01ItemTtl01View {/* h3の様な */
    font-size: 19px !important;
}
.MdMTMWidget01.mdMTMWidget01TypeTtl .mdMTMWidget01ItemTtl01View {/* h3の様なの下 */
    border-bottom: 2px solid #ADADAD !important;
}
.MdMTMWidget01 .mdMTMWidget01ItemTweet01View {/* Tweet */
    font-size: 15px !important;
}
.MdMTMWidget01 .mdMTMWidget01ItemQuote01View {/* Quote */
    font-size: 19px !important;
}
.MdPagination03 a:link, .MdPagination03 a:visited {
    border: none !important;
}


.LySub div[style^="height:20px"] {/* 右カラム最上部余白調整 */
    height: 30px !important;
}
.ArSub01 { /* R - ボーダー #E2E5E8 */
    border: 3px solid #ADADAD !important;
}
.ArSubSeparator02 { /* R - 枠内セパレーター */
    border-top: 1px solid #ADADAD !important;
    border-bottom: none !important;
}
.ArSubSeparator03 { /* R - BG白うめ */
    background: #F7F7F7 !important;
}
.MdSubList01 .mdSubList01Link:hover {
    background-color:  #EEEEEE !important;
}


header,
.MdMTMViewHead01,   /* タグ */
.mdHeading01Option, /* 共有ボタン */
.LyMain > :nth-of-type(8),/* L - カテゴリ一覧 */
.ArMain03, /* L - コメント欄 */
.ArSub03, /* R - 編集コンペ結果発表 */
footer,

div[class*="Ad"] {
    display: none !important;
}
}
