css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
    url-prefix('http://weheartit.com/')
    
    {
    .topbar-new .topbar-inner {width:1150px !important;}
    .container {max-width:1330px !important;}
    #main-container {width:1330px !important;}
    #content {width:1000px !important;}
    #sidebar {width:200px !important; }
    ul.grid-tiny {width:205px !important; }
    ul.grid-tiny li.entry, img.tiny {width:66px !important; height:54px !important;}
    .fullwidth.three-cols .grid-thumb .entry {width:240px !important; height:200px !important;}
    div.gray-box hr {display:none !important;}
    div.entry {margin: 0 20px 20px 0 !important;}
    div.grid-thumb {margin:0 -30px 0 -10px !important;}
    .ad, .advertisebtn, .likebox, #photostream-container, #facebook-timeline-message, .app-promo, .social-widgets, .beta-test {display:none !important;}
    }

    
@-moz-document
    url("http://weheartit.com/"),
    url-prefix('http://weheartit.com/dashboard')
    
    {
    #main-container {width:1330px !important;}
    #content {width:100% !important;}
    #sidebar, .sidebar {display:none !important;}
    .grid-thumb {margin:0 !important;} 
    }

@-moz-document
    url-prefix('http://weheartit.com/dashboard/keywords'),
    url-prefix('http://weheartit.com/?before='),
    url-prefix('http://weheartit.com/?page='),
    url-prefix('http://weheartit.com/tag/'),
    url-prefix('http://weheartit.com/search'),
    url-prefix('http://weheartit.com/?login='),
    url-prefix('http://weheartit.com/?logout=')
    
    {
    #main-container {width:1330px !important;}
    #content {width:100% !important;  margin: 0 -35px !important;}
    #sidebar, .sidebar {display:none !important;}
    .grid-thumb {margin:0 -50px 0 -10px !important;}
    }
    
@-moz-document
    url-prefix('http://weheartit.com/entry'),
    regexp('^http://weheartit.com/[a-zA-Z0-9_/]*/(contacts|fans)') 
    
    {
    .topbar.fixed .topbar-inner {width:940px !important;}
    #main-container {width:930px !important;}
    #content {width:500px !important;}
    #sidebar {width:300px !important;}
    .likebox {display:block !important;}
    #photostream-container {display:block !important;}
    ul.grid-tiny {width:310px !important; }
    ul.grid-tiny li.entry, img.tiny {width:99px !important; height:82px !important}
    div.topbar ul.tabs  {margin-left:0px  !important;}
    }   

@-moz-document
    url-prefix('http://weheartit.com/dashboard/keywords')
    
    {
    div.entry {margin: 12px 20px 20px 0 !important;}
    .fullwidth.three-cols .grid-thumb .sidebar { 
        display: block !important;
        position: absolute !important;
        top: 12px !important;
        left: 20px !important;
        z-index: 10 !important;
        }
    div.sidebar div.vspan1 {height: 20px !important;} 
    div.sidebar div.vspan1 h5 {
        margin-right: 10px !important;
        float: left !important;
        font-weight: normal !important;
        }
    ul#user-keywords-list {width:1000px !important;}
    form.tagform{
        position:absolute !important;
        top: -7px !important;
        left: 1040px !important;
        
        }
    input#keyword {width: 185px !important;}
    form.tagform input.button {
        position:absolute !important;
        left: 190px !important;
        }
    }
