#icons {display:none;}
img[alt="//static.thepiratebay.se/img/comments.gif"] {width:0;padding:10px 12px !important;background:url("http://i.imgur.com/bHUzH.png") !important;}


select#category {border:2px solid #272727 !important;background-color:#111 !important;color:#7d7d7d !important;}
select#category:focus {outline:0 !important;border-color:#999 !important;}
#main-content {margin-bottom:0 !important;margin-left:0 !important;padding-right:0 !important;}
