textarea#aboutyou {width:360px !important;max-width:360px !important;}
#irc {width:500px !important;margin:30px auto !important;padding:10px !important;border:2px solid #272727;background:#171717;}
#contact {width:500px !important;padding:10px;border:2px solid #272727 !important;background:#171717;}
#searchcloud {width:660px !important;margin:30px auto !important;padding:10px !important;border:2px solid #272727 !important;background:#171717 !important;}}
