css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
/* --- Feather 3.2 --- */

body {
	background-position: left 0px !important;
}

@-moz-keyframes lefttoright {
0% { margin-left: -20px; opacity: 0;}
100%{ margin-left: 0px; opacity: 1 !important;;}
}

@-moz-keyframes toptobottom {
0% { margin-top: -20px; opacity: 0;}
100%{ margin-right: 0px; opacity: 1;}
}

@-moz-keyframes fade {
0% { opacity: 0;}
100%{ opacity: 1;}
}
.js-content-top {
}

#page-node-home {
}

.global-nav {
	background: transparent !important;
	position: fixed !important;
	margin-top: 10px !important;
}

.topbar {
 	box-shadow: none !important;
 	z-index: 1 !important;
}

.nav li a {
	text-shadow: none !important;
}

.nav li a:hover {
	color: white !important;
}

.nav .active a {
	color: white !important;
}

.nav-home, .nav-people, .nav-topics {
	display: none !important;
}

.nav .active > a, .nav .active > a:hover, .nav .dropdown.open .dropdown-toggle, .form-search.has-saved-searches.focus {
	background: transparent !important;
	box-shadow: none !important;
	-moz-box-shadow: none !important;
}

.wrapper, .wrapper-narrow, .wrapper-permalink {
	background: rgba(0,0,0,.4) !important;
	box-shadow: rgba(255,255,255,0.2) 0px 0px 1px !important;
	padding: 65px 20px 20px !important;
}

.dashboard {
	position: fixed !important;
}

.stream-item-header .account-group .username {
	opacity: 0 !important;
	-moz-transition-duration: 100ms !important;
}

.stream-item-header .account-group .username:hover {
	opacity: 1 !important;
}

.bird-topbar-etched {
	background-position: -40px 0 !important;
	width: 68px !important;
	height: 14px !important;
	margin-top: 12px !important;
	opacity: .5 !important;
	display: none !important;
}

.home a, .people a, .topics a {
	padding: 0px !important;
	margin-right: 25px !important;
	margin-top: 13px !important;
	color: black;
}

.home a active, .people a active, .topics a active{
	color: #333 !important;
}

.global-nav .container {
	width: 835px !important;
}
	
.stream-item:first-child {

}

.profile-card {
	width: 520px !important;
	float: right !important;
}

.profile-card-inner {
	width: 42% !important;
}

.profile-card .fullname {
	font-size: 24px !important;
}

.profile-card .username {
	font-size: 14px !important;
}

.size128 {
	border-radius: 2px !important;
}

.avatar {
	border-radius: 3px !important;
}

.open.stream-item {
	padding: 0px !important;
	box-shadow: 0px 1px 10px rgba(0,0,0,.2) !important;
}

.topbar-tweet-btn {
	display: none !important;
}

.topbar-divider {
	display: none !important;
}

.stream-item, .unfocusable-stream-item .tweet p {
	font-size: 13px !important;
}

.profile-modal-header .follow-status {
	text-transform: capitalize !important;
}

.nav-me {
	background-position: 430px, 60px !important;
}

.nav .new-wrapper, .global-nav .pull-right .nav-me {
	display: none !important;
}

.pull-right {
	margin-left: 12px !important;
	float: left !important;
}

.dropdown-menu {
	border-radius: 6px !important;
	-moz-animation-name: toptobottom !important;
	-moz-animation-duration: 300ms !important;
	top: 40px !important;
}

.global-nav .form-search {
	display: none !important;
}

/* 

.global-nav .form-search {
	position: fixed !important;
	z-index: 0 !important;
	bottom: 0 !important;
	margin-left: -271px !important;
	padding: 0px !important;
}

.global-nav .search-input {
	color: white !important;
	background: rgba(0,0,0,.2) !important;
	box-shadow: none !important;
	border-radius: 5px 5px 0px 0px !important;
	width: 263px !important;
}


.global-nav .search-input:hover {
	padding: 6px 13px 6px 12px !important;
	background: rgba(0,0,0,.4) !important;
	box-shadow: none !important;
}


.global-nav .search-input::-moz-input-placeholder {
	color: rgba(255,255,255,.6) !important;
}

.global-nav .search-input:focus, .global-nav .search-input.focus {
	background: rgba(0,0,0,.3) !important;
}

.global-nav .search-icon {
	margin-top: -7px !important;
}

*/

.new-tweets-bar {
	font-size: 12px !important;
	padding: 10px 1px !important;
	text-shadow: 0 1px 0 rgba(255, 255, 255, .6) !important;
	background-color: #EEE !important;
	border-top: 1px solid #CCC !important;
	box-shadow: none !important;
	border-bottom: 1px solid #CCC !important;
	color: #333 !important !important;
	z-index: 1 !important;
}

s {
	color: #DDD !important;
	opacity: 1 !important;
	margin-right: 1px !important;
}

.profile-banner img {
	width: 520px !important;
}

.global-nav .pull-right .dropdown-menu {
	right: -86px !important;
	margin-top: -2px !important;
}

.close-all-tweets {
	display: none !important;
}

/* ----------  Huge thanks to Nick Heer for identifying these tricky elements */

div.trends .flex-module {
	border-radius: 6px !important;
	height:13px !important;
	overflow: hidden !important;
	-moz-transition-duration: 200ms !important;
}

div.trends .flex-module:hover {
	border-radius: 6px !important;
	height:220px !important;
	overflow: hidden !important;
	-moz-transition-duration: 200ms !important;
}

div[data-component-term="user_recommendations"] .flex-module {
	height: 13px !important;
	overflow: hidden !important;
	-moz-transition-duration: 200ms !important;
}

div[data-component-term="user_recommendations"] .flex-module:hover {
	height: 190px !important;
	-moz-transition-duration: 200ms !important;
}

div[data-component-term="footer"] {

}

a[data-component-term="home_nav"]:before{
	content: "â˜° " !important;
}

a[data-component-term="connect_nav"]:before{
	content: "@ " !important;
}

a[data-component-term="discover_nav"]:before{
	content: "# " !important;
}

body.count-ready div#widget{margin-top:-20px;}

/* ---------- End of @nickheer awesomeness ----------  */

.wordmark-secondary {
	display: none !important;
}

.content-main, .content-narrow, .page-canvas, .permalink {
	border-radius: 12px !important;
}

.wrapper-narrow, .wrapper-permalink {
	width: 835px !important;
}

.permalink-inner {
	padding: 20px !important;
}

.permalink-tweet div.tweet-media img {
	max-width: 770px !important;
}

.stream-item, .unfocusable-stream-item {
	box-shadow: rgba(0,0,0,0.2) 0px 0px 5px !important;
}

.module {
	text-shadow: none !important;
}

.flex-module-header small a{
	color: #DDD !important;
	-moz-transition-duration: 50ms !important;
}

.flex-module-header small {
	color: transparent !important;
}

.flex-module-header small a:hover{
	color: #555 !important;
	text-decoration: none !important;
	-moz-transition-duration: 50ms !important;
}

.stream-item-header .sm-lock {
	float: right !important;
	margin-right: 50px !important;
	display: none !important;
}

.module .promptbird {
	padding-bottom: -20px !important;
} 

.pull-right, .pull-right .nav {
}

.nav {
	margin-right: 0px !important;
}

.twttr-dialog {
	-moz-animation-name: toptobottom !important;
	-moz-animation-duration: 200ms !important;
	-moz-transition-duration: 200ms !important;
}

#global-nav-home {
	margin-left: 14px !important;
}

.caret {
	border-top: 4px solid rgba(255,255,255,.8) !important;
}

.session .dropdown-toggle .caret {
	box-shadow: none !important;
	-moz-box-shadow: none !important;
}

.caret:hover {
	border-top: 4px solid rgba(255,255,255,1) !important;
}

.dropdown-menu li > a, .dropdown-menu .dropdown-link {
	margin-left: 3px !important;
	margin-right: 3px !important;
	border-radius: 2px !important;
	padding: 3px 15px 3px 15px !important;
}

.dropdown-menu li > a:hover, .dropdown-menu .dropdown-link:hover {
	background: #569FD2 !important;
}

.global-nav .session .dropdown-menu .dropdown-caret {
	right: 95px !important;
}

.follow-status {
	background-color: #c6c6c6 !important;
	color: white !important;
	text-transform: uppercase !important;
}

.profile-modal-header .follow-status {
	text-transform: uppercase !important;
	margin-top: -2px !important;
}

.twttr-dialog-header .twttr-dialog-close, .profile-modal-header .twttr-dialog-close {
	height: 24px !important;
	width: 24px !important;
	position: absolute;
	right: -10px !important;
	top: -9px !important;
}

div[data-component-term="footer"] {
	bottom: 0px !important;
	position: fixed !important;
	background: transparent !important;
	box-shadow: none !important;
	width: 302px !important;
	z-index: 0 !important;
	border: none !important;
}

div[data-component-term="footer"] .module .flex-module {
	background: transparent !important;
	box-shadow: none !important;
	border: none !important;
	color: rgba(255,255,255,0.5) !important;
}

div[data-component-term="footer"] .module {
	background: transparent !important;
	box-shadow: none !important;
	border: none !important;
}

div[data-component-term="footer"] li {
	color: rgba(255,255,255,.5) !important;
}

div[data-component-term="footer"] a {
	color: rgba(255,255,255,.2) !important;
}

div[data-component-term="footer"] a:hover {
	color: rgba(255,255,255,.7) !important;
	text-decoration: none !important;
}
}
