css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.rinkworks.com/adventure/smash.cgi") {
body
{
	background-color: #333;
	font-family: Arial, sans-serif;
	color: #ccc;
}


img[alt="The Game of the Ages"]
{
	border: 1px solid #AAA;
	margin: 0 10px 15px;
	border-radius: 5px;
	padding: 5px;
	box-shadow: inset 0 0 7px #000;
	background: white;
	border-right: none;
	border-bottom: none;
}


table[cellpadding='1']
{
	width: 730px;
	margin: 10px auto;
}

td[bgcolor='#ccddff']
{
	background-color: #222 !important;
	padding: 10px !important;
}

td[bgcolor='#ccddff'] font[color='#3333aa']
{
	color: #aaa !important;
}

td[bgcolor='#ccddff'] font[color='#3333aa']:hover
{
	color: #fff !important;
}


table[cellspacing='3']
{
	width: 730px;
	padding: 10px 0 0;
}


table [bgcolor='#ffe4dd']
{
	background-color: #222 !important;
	color: #aaa !important;
	padding: 10px !important;
	line-height: 1.4em;
	width: 40%;
}


table [bgcolor='#ccccff']
{
	background-color: #223 !important;
	color: #aaa !important;
	padding: 10px !important;
	line-height: 1.4em;
}


td[bgcolor='#e8ffe8']
{
	background-color: #111 !important;
	color: #AAA !important;
	padding: 15px !important;
	border: 2px solid #444;
	border-radius: 5px;
	box-shadow: inset 0 0 8px #000;
	border-right: none;
	border-bottom: none;
}


table[cellpadding='0'] td[valign='top'] table
{
	margin-right: 30px;
}


td[bgcolor='#ffffdd']
{
}


table[cellpadding='0'] td[valign='top'] tr
{
	height: 25px;
}


input[type='image']
{
	width: 0;
	height: 0;
	box-shadow: 1px 1px 3px #111;
	border-radius: 5px;
	border: 7px solid #37C;
}

input[type='image']:hover
{
	border-color: #7af;
}


table[width='100%']
{
	width: 700px !important;
	padding: 5px 15px;
	margin: 0 auto !important;
}

input[type='submit']
{
	border-radius: 3px;
	background-color: #888;
	border: 1px solid #888;
	box-shadow: 2px 2px 5px #000;
	color: #000;
}
}
