css:
@-moz-document url("http://dsl.sk/"), url("http://www.dsl.sk/"), url-prefix("http://dsl.sk/index"), url-prefix("http://www.dsl.sk/index") {
#menu > div:nth-child(-n+9) { display: none; }
#header > table { display: none; }
div#bg { width: 1000px; background-color: #202020; }
#body table tr > td:nth-child(1), div#news_box { width: 780px !important; border: 0px solid grey; }
embed { display: none; }
table[cellspacing="2"][cellpadding="1"][bgcolor="#000000"][border="0"] { display: none; }
font.ad_font { display:none; }
td[rowspan="1"][valign="top"][align="right"][width="250"] { width: 1px; }
body{ background-color: #666; color: #EEE !important; }
div#body { background-color: #202020; }
div#body a { color: #FFF !important; border-bottom: 1px solid white !important; }
div#body a:hover { border-bottom: 0px solid white !important; }
td[bgcolor="#ffffff"] { background-color: gray !important; }
td[bgcolor="#ffffff"] > div { color: #FFFFFF !important; }
td[bgcolor="#ffffff"] > div font { color: #FFFFFF !important; }
div#news_box a { color: #000 !important; text-decoration: none; border-bottom: 1px solid black !important; }
div#news_box a:hover { color: #000 !important; }
div#news_box a:visited { border-bottom: 1px solid gray !important; color: gray !important; }
div#menu, td[width="165"] {
background: #808080 !important; 
background: -moz-linear-gradient(left, #808080 0%, #000000 100%) !important; 
background: -webkit-gradient(linear, left top, right top, color-stop(0%,#808080), color-stop(100%,#000000)) !important; 
background: -webkit-linear-gradient(left, #808080 0%,#000000 100%) !important; 
background: -o-linear-gradient(left, #808080 0%,#000000 100%) !important; 
background: -ms-linear-gradient(left, #808080 0%,#000000 100%) !important; 
background: linear-gradient(left, #808080 0%,#000000 100%) !important; 
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#808080', endColorstr='#000000',GradientType=1 ) !important; 
}
}

@-moz-document url-prefix("http://dsl.sk/article"), url-prefix("http://www.dsl.sk/article") {
#menu > div:nth-child(-n+9) { display: none; }
#header > table { display: none; }
div#bg { width: 1000px; background-color: #202020; }
font[style="font-size: 7pt;"] { display: none; }
a[href="http://www.dsl.sk/index.php"] { position: absolute; left: 10px; top: 40px; }
img[src="/images/design/DSL_9.png"] { width: 100px; height: 22px; margin-top: 2px; }
table[style="height: 255px; width: 255px; margin-right: 12px;"] { display: none; }
body{ background-color: #666; color: #EEE !important; }
div#body { background-color: #202020; }
div#body a, div#body a:hover { color: #FFF !important; }
div#body a { border-bottom: 1px solid white; }
td[bgcolor="#ffffff"] { background-color: gray !important; }
td[bgcolor="#ffffff"] > div { color: #FFFFFF !important; }
td[bgcolor="#ffffff"] > div font { color: #FFFFFF !important; }
div#poll { background-color: #333 !important; }
div#menu, td[width="165"] {
background: #808080 !important; 
background: -moz-linear-gradient(left, #808080 0%, #000000 100%) !important; 
background: -webkit-gradient(linear, left top, right top, color-stop(0%,#808080), color-stop(100%,#000000)) !important; 
background: -webkit-linear-gradient(left, #808080 0%,#000000 100%) !important; 
background: -o-linear-gradient(left, #808080 0%,#000000 100%) !important; 
background: -ms-linear-gradient(left, #808080 0%,#000000 100%) !important; 
background: linear-gradient(left, #808080 0%,#000000 100%) !important; 
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#808080', endColorstr='#000000',GradientType=1 ) !important; 
}
}
