css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twittascope.com") {
body {background-image: none !important; background: #FB6808 !important; overflow-x: hidden !important;}
#site-signup-banner {display: none !important;}
#site-karmic-deal h3  {display: none !important;}
#site-header {display: none !important;}
#site-content {background-image: -moz-linear-gradient(to bottom right, #FB6808, rgba(255,255,0,0))}
#site-content p {color: #8C045B !important;}
#site-content a.yesterday, a.today, a.tomorrow {color: #FFFFFF !important; }
#site-footer {display: none !important;}
#site-container{-moz-box-shadow:0 0 0px #651540 !important; box-shadow:0 0 0px #651540 !important;}
#psychic-bird {display: none !important; }

.karmic-deal-promo  {display: none !important;}
_.container h4  {display: none !important;}
.site-nav {display: none !important;}
.dh-signlist {display: none !important;}
.section-hgroup {display: none !important;}
.button {display: none !important; }
.more-fun {background: #FB6808 !important; margin-top: 8px !important;}
}
