css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tianya.cn") {

.content{background:none !important}

/*font*/
body,div,table,center,a,td,tr{font-family:"Lucida Grande",Tahoma,Verdana,arial,sans-serif,hei !important; font-size:13px !important;line-height:15px !important}

a{font-size:inherit !important}
a{color:#0063DC !important; text-decoration:none !important}
a:hover{color:white !important;background-color:#0063DC !important}

/*sent from wap*/
font[size="2"][color="blue"]{display:none !important}

/*flash ad*/
embed{display:none !important}

/*top nav*/
#tianyatopguide_nav{display:none !important}

/*title*/
table[cellspacing="2"][bordercolor="#ffffff"] td[bgcolor="#336699"] font[size="+1"]{font-size:18px !important;line-height:20px !important;text-align: justify !important;}

td[bgcolor="#336699"]{background:#FFFFde !important;padding:8px !important;-moz-border-radius:8px !important;border:2px solid green !important}

/*poster name time*/
td[width="100"],td[width="137"]{display:none !important}

#pContentDiv table[width="100%"][cellspacing="0"][align="center"][border="0"] tbody tr td[align="center"],center{text-align:left !important;background-color:#cdFFcd!important;-moz-border-radius:3px !important;padding:3px !important;padding-left:8px !important;color:grey !important}

br{line-height:2px !important}

center center,table[width="640"][height="80"],#Layer1,.friend,.explain,#tianyafootercopyArea{display:none !important}

font[style="color: white; cursor: pointer;"]{color:purple !important}

#pContentDiv{width:auto !important}

div[class="content"] center{padding:0px 3px !important;}

}
