css:
@-moz-document url-prefix('http://fitocracy.com/'), url-prefix('http://www.fitocracy.com') {
.stream_item_info { 
    width: 150px !important; 
    margin-right: 10px !important;  
    position: absolute !important; 
}
.stream_item_content { width: 600px !important; margin-left: 80px !important}
.stream_item.clearfix {min-height: 135px !important}
.sidebar-column { width: 267px !important; }
.sidebar-column .sticky { width: 300px !important}
.main-content-column { width: 810px !important; } 
.avatar.large img { margin-left: auto !important; margin-right: auto !important; width: auto !important; }
.avatar.large { width: auto !important; overflow: hidden !important}
a.avatar.medium.stream_pic { 
    position: relative !important; 
    top: 60px !important;
    left: 4px;
}
div#profile-sidebar-follower-list .avatar.small { margin-bottom: 10px !important}
header { 
	background: /*[[headertocolor]]*/ !important;
	background:-webkit-gradient(linear,left bottom,left top,color-stop(0,/*[[headerfromcolor]]*/),color-stop(1,/*[[headertocolor]]*/)) !important;
	background:-ms-linear-gradient(bottom,/*[[headerfromcolor]]*/,/*[[headertocolor]]*/) !important;
	background:-moz-linear-gradient(center bottom,/*[[headerfromcolor]]*/ 0,/*[[headertocolor]]*/ 100%) !important;
}
div#footer_stats { width: 304px !important; }
div#footer_stats ul { padding: 10px 5px 10px 5px !important}
div#footer_stats ul li { width:90px !important; margin-right: 8px !important; }
div#footer_stats ul li label { font-size: 12px !important; }
#footer_stats #footer-achievement-count label:before,#footer_stats #footer-quest-count label:before {
    background:none !important;
    width:0 !important;
    height:0 !important;
    margin: 0 !important;
    }
    
div#date-container { width: 220px !important }
input#activity-search-field { width:240px !important}
#group-search-container input[type="text"] { width: 240px !important}
.profiles-vertical-list li a.group_title { width: 200px !important}
.user_comment_input .user_comment { min-height: 30px !important}
.user_comment textarea { font-size: 12px !important}
text.chart_title { font-size: 32px !important}

/* tricky to fix in CSS what's shifted in JS */
div#activity_search_results_container { position: relative !important;
     left: -35px !important; }
div#activity_search_slideout { 
    background: none !important; 
    box-shadow: none !important;
    width: 320px !important
    }
}
