html { font-size: 100%; }

body {
  background: #fff;
  color: #000;
  font: normal 0.9em/1.5 "Helvetica Neue", "Lucida Grande", Helvetica, Arial, sans-serif;
  text-align: center;
  margin: 0;
  padding: 0;
}

a, a:active, a:visited {
  color: #2b4ba8;
  outline: none;
}

a:hover {
  background: #f5ff58;
}

a img {
  border: 0;
}

h1 {
  font-size: 3em;
  font-weight: normal;
  letter-spacing: -1px;
  margin: 0;
}

h1 a, h1 a:active, h1 a:visited, h1 a:hover {
  color: #000;
  text-decoration: none;
  padding: 0;
  margin: 0;
}

h1 img#portrait {
  position: relative;
  top: 7px;
  width: 40px;
  height: 40px;
  padding: 2px;
  border: 1px solid #999;
  margin: 0 10px 0 0;
}

#description {
  font-size: 1.1em;
  line-height: 1.2em;
}


blockquote {
  font-style: italic;
  margin: 0 1.5em 1.5em;
}

p {
  margin: 0 0 0.5em 0;
}

ol, ul {
  margin: 0 0 1.5em;
  padding: 0 1.5em;
}


/* LAYOUT */
#contain {
  margin: 0 auto;
  padding-top: 2em;
  text-align: left;
  width: 48em;
}

#header {
  margin: 0 0 1.5em 0;
}

#content {
  float: left;
  width: 30em;
  margin: 0 3em 0 0;
}

#footer {
  float: left;
  width: 15em;
}




/* time does not exist */
.newdate,
.samedate {
  display: none;
}


/* POST-HEADER */

.post-header {
  text-align: right;
}

.post-header .post-metadata {
  margin: 0.5em 0;
  padding: 0 0.5em 0 0;
  border: 1px solid #d0d0d0;
  border-width: 1px 0 0 0;
  background: #eee;
}

.post-header .post-metadata .post-type {
  display: none;
}

.post-header .post-metadata a.permalink {
  color: #3d5571;
  font-size: 0.8em;
  text-transform: lowercase;
}



/* POST STYLES */


.post {
  margin: 0 0 5em 0;
}

.post h3 {
  font-size: 1.3em;
  margin: 0 0 0.3em 0;
}

.post .caption {
  margin-bottom: 1.5em;
}

.quote .words {
  font-size: 1.25em;
  line-height: 1.2em;
  margin: 0 0 0.4em 0;
}

.quote .short {
  font-size: 1.4em;
  line-height: 1.3em;	
}

.quote .medium {
  font-size: 1.1em;
  line-height: 1.2em;	
}

.quote .long {
  font-size: 1em;
  line-height: 1.3em;	
}

.photo img {
  display: block;
  padding: 8px;
  margin: 0.7em auto;
  background: #eee;
}

.chat .conversation {
  list-style: none;
  padding: 0;
  font-size: 0.9em;
}

.chat .conversation .line {
  border-bottom: 1px solid #ddd;
  padding: 0.4em;
}

.chat .conversation .person {
  font-weight: bold;
}

.chat .conversation li.odd {
  background: #f4f4f4;
}

.chat .conversation li.even {
  background: #e8e8e8;
}
  
.was-reblogged {
  margin: 0.5em 0 0 0;
  color: #606060;
  font-size: 0.8em;
  text-align: right;
}
  
  
/* NAVIGATION */
.previous-next {
  margin: 0;
  border: 1px solid #c0c0c0;
  border-width: 1px 0 0 0;
  color: #333;
  background: #ddd;
  text-align: center;
  font-size: 1em;
  text-transform: lowercase;
  line-height: 4em;
}

.previous-next a {
  color: #3d5571;
}

.previous-next .previous {
  margin-right: 1em;
}
  

.previous-next .next {
  margin-left: 1em;
}


/* "FOOTER" sidebar */
#footer {
  font-size: 0.875em;
}

#footer h4 {
  margin: 0 0 .5em 0;
}

#footer ul#rss-archive {
  list-style: none;
  padding: 0;
}

#footer #links,
#footer .followed {
  list-style: none;
  padding: 0;
}
  
#footer .followed li {
  display: inline;
  margin-right: .25em;
}

#footer .followed li a {
  background: none;
}
    
#footer p.please_dont_hide_this {
  margin: 4em 0 0 0;
  border-top: 1px solid #e0e0e0;
  padding: 0.5em 0 0 0;
  font-size: 0.8em;
}
