@-moz-document domain("login.live.com") {
#iUser0,
   #iUser0 *,
   .cssAppBigBold,
   #i1670,
   #mainTD,
   #footerTD,
   #footerTD *,
   #leftTD,
   #leftTD *,
   body,
   #idRemoveMeLink09 { color: #ffffff !important ; background-color: #000000 !important ; background-image: none !important ; border-color: #555555 !important }
