css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body {
background-image: url(http://static.tumblr.com/xezzqte/9dnm1nr1l/bg_ums.jpg);
background-repeat: repeat;
background-attachment: fixed;
background-position: center left;
background-color: #fff;
color: #333;
}

#logo {
height: 0 !important;
width: 0 !important;
padding-left: 218px !important;
padding-top: 49px !important;
background: url(http://static.tumblr.com/xezzqte/6bim1m66y/logo.png) no-repeat !important

}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: url(http://static.tumblr.com/xezzqte/tPcm1ns2u/logo.png) no-repeat top center;
padding-top: 110px;
text-shadow:0px 0px 23px #ffffff;
margin-top: 30px;
}

#right_column {
background: transparent;
padding: 7px;

-moz-border-radius:7px;
-webkit-border-radius:7px;
border-radius:7px;
margin-top: 1px;
-moz-box-shadow: 0px 0px 3px #919191;
-webkit-box-shadow: 0px 0px 3px #919191;
box-shadow: 0px 0px 3px #919191;
}

.tab_bar, .with_inbox, #tabs_container {
color: #000;
}
.tab_bar li, .with_inbox li, #tabs_container li {
color: #000;
}

#left_column #posts .notification a {
                        color:#565656 !important;
                        border-bottom:0px !important;
                    }

*{text-shadow:0px 0px 9px #ffffff; color:#000 !important}

a {
webkit-transition: opacity 0.3s linear;
   -webkit-transition: all 0.3s ease-out;
   -moz-transition: all 0.3s ease-out;
transition: all 0.3s ease-out; 
}
a:hover{
opacity: 0.5;
}
a:active {
color: #ff0000;
}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://static.tumblr.com/xezzqte/NVrm1m9ng/barra.jpg') center center no-repeat !important;
}
.new_post_label{ padding-top:73px !important;}
.new_post_label_icon{background-image:none !important}


#content_top, #content_bottom {
display: none;
}

#left_column #posts .notification {
                        background-color:#fbfbfb !important;
                        color:#666 !important;
                        border-bottom-color: #eee !important
                    }

#left_column #posts .notification .block {
                            color:#333 !important;
                        }

#left_column #posts .notification.alt {
                        background-color: #f7f7f7 !important;
                    }
#left_column #posts .notification.last_notification,
                    #left_column #posts .notification.single_notification {
                        border-bottom-color:#ececec !important;
                    }
#left_column #posts .notification a {
                        color:#565656 !important;
                        border-bottom:0px !important;
                    }
#left_column #posts .notification blockquote {
                        border-left-color:#eee !important;
                    }

#left_column #posts .notification .avatar {
                        border-color:transparent !important;
                        background-color:#fbfbfb !important;
opacity:0.5 !important;
                    }

#left_column #posts .notification .avatar:hover{
opacity:1 !important;
                    }

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) { 
	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px #da0000 !important;}	

div.l-header {
background: url(http://static.tumblr.com/xezzqte/y6Qm1o8iv/transp.png);
position: fixed;
z-index: 1;
border-bottom: 10px solid #da0000;
margin-top: -101px;
webkit-transition: opacity 0.3s linear;
   -webkit-transition: all 0.3s ease-out;
   -moz-transition: all 0.3s ease-out;
transition: all 0.3s ease-out; 
}
div.l-header:hover {
border-bottom: 5px solid #da0000;
margin-top: -30px;
}
}
