css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tuenti.com") {

/* NEW tuenti.com CSS STYLE "TuentiBlue IV" v1.4 BY EDUARDO MARTÍNEZ [ARDOYE] .................  Twitter: @Ardoye */

/* GENERAL */
* { font-family: tahoma !important; }

/* HEADER */
.header { box-shadow: 0 0 14px black !important;  background-color: #464646 !important;  background-image: none !important;  border: none !important; }

#sections a { color: white !important; border: none !important; }

#sections a:hover , #more_options_button_header:hover { background-color: transparent !important; background-image: none !important; border-bottom: none !important; text-shadow: 0 0 5px #90C4EC!important; }

#sections , .actionGroup , #more_options_button_header .settingsLink{ border: none !important; }

#sections .active { text-shadow: 0 0 5px black !important; color: #90C4EC !important; }

a.active  { background-color: transparent !important; background-image:none !important; }
    
/* GENERAL BACKGROUND #EEE */ 
body , #home_main_info .body , #avatar .body , #messages_menu .active , .userName , .pagedEntry , #blog_entry_actions , #tag_entries li , #inbox-menu-entry.active span , #sentbox-menu-entry.active span , #spambox-menu-entry.active span , .nAlbumsList li { background-color: #EEE !important; }
    
/* ROUND CORNERS */
.statusBox , .status , #user_status_box , #header .search , #chat_roster_module_search_container input { border-radius: 15px !important; }
#home_main_info .body ,  #avatar .body { border-bottom-left-radius: 1em !important; border-bottom-right-radius: 1em !important; }

/* TRANSPARENT ELEMENTS */
#chat_home_module , .updatesBar , #chat_roster_settings .userEntry h3 , #avatar .action , #sponsorships_list img { background-color: transparent !important; }

/* HIDDEN ELEMENTS */
#canvas_friend_suggestions , #invitations , #ltaAdItem , #home_unit_ads_container_id { display: none !important;}

/* IMAGE SHADOW */
img { box-shadow: 0 2px 4px #999 !important;}

/* SPECIAL ELEMENTS "COLOR" */
    /* satus*/
    .userEntry .status , .statusWall { background-color: #B7D6EF !important; border: 0em solid #bbb !important;  padding: 5px !important;}
    /* header badage */
    #new_messages_indication_header {background-image: none !important; background-color: white !important; color: black !important; border-radius: 30px !important; box-shadow: 0 0 3px black !important; }
    /* box "write a comment" */
    .commentForm .textInput {background-color: #f5f5f5 !important; }

/* CHAT */
    /* Window */
    .nOverlay.open .overlayBody { box-shadow: 0px 0 10px #464646 !important; /* width: 26em !important;*/ background-color: #bbb !important;} 
        /*   EXCEPTIONS OVERLAY     */
        .overlayActions , .mod .nChat  { width: auto !important; }
        /* received chat message */
        .received .itemBody { background-color: #B7D6EF !important; }
        /* last received */
        .last.received .itemBody { background-color: #FEFFC1 !important; }
    
/* USER ENTRIES */
.feedItem { border-bottom: 0.1em solid #ccc !important; }

/* HOME MAIN INFO */
#home_main_info , #home_main_info .body { background-color: #eee !important; position: relative !important; left: 0.5em !important; padding: 5em 1.3em 0.8em 1.3em !important; margin: -4em 0.2em 0 -0.9em !important; }

#home_main_info .body { box-shadow: 0 0.1em 0.8em #464646 !important; }
    
#home_main_info { background-color: transparent !important; margin-bottom: 2em!important; }

/* AVATAR */
#avatar { margin: auto !important; padding-top: 3em !important; }

#avatar, #avatar .body { top: -4em !important; }

#avatar .body { position:  relative !important; left: 0.6em !important; width: 19.5em !important; padding-top: 8em !important; margin-bottom: -3.2em!important; z-index: 1 !important;}

#avatar img { width: 98% !important; position: relative !important; left: -0.3em!important; top: -0.6em!important; }

#avatar .body { box-shadow: 0 0.1em 0.8em #464646 !important; }

/*EXCEPTIONS*/
#home_main_info .body , #avatar  .body{ background-color: #464646 !important; }

#home_main_info img , #avatar img { border: none !important; box-shadow: 0 0 2em #B7D6EF !important;}

#home_user_name { color: white!important; }

.request { background-image: none !important; background-color: transparent !important;  }

#request_management_filter .active { color: black!important;}


    /* "profile views" text hidden */
    #home_main_info .views { color: transparent !important; }  
    
    #home_main_info strong {color: #bbb !important; }
    
    /* news text */
    #home_main_info span { color: #B7D6EF !important;}
    
    /* ads img */
    #sponsorships_list img { background-color: transparent!important; box-shadow: none !important; }


}
