@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org"),
domain("wikimedia.org"),
domain("wiktionary.org"),
domain("wikiquote.org"),
domain("wikisource.org"),
domain("wikiversity.org")
 {

body
{
font-family: Tahoma !important;
background: #444 none 0 0 no-repeat !important;
padding-bottom: 80px !important;
}

#globalWrapper, .mediawiki
{ 
width: 92% !important; 
padding-left: 0px !important;
padding-top: 60px !important; 
font-size: inherit !important; 
} 

#column-content
{
position: relative !important;
margin: 0 !important; 
padding: 0 !important;
}

#content
{
background: #E0E0E0 !important;
color: #444 !important;

margin-left: 10px !important;
padding: 15px 20px 20px 20px !important; 
width: 100% !important;
border-color: #bbb ! important;
border-radius: 10px;
box-shadow: 10px 4px 20px #222;
border-style: solid !important;
} 


div#bodyContent
{
text-align: left !important;
font-size: small !important;

} 

#p-views, #p-namespaces, #head-base, DIV#p-tb { display: none !important; }

a:link.new { color: #9F451C !important; }
a:link { text-decoration: none !important; color: #811 !important; background-color: transparent !important; }
a:visited { text-decoration: none !important; } 
a:hover { text-decoration: none !important; } 
a:active { text-decoration: none !important; } 


#column-one { 
position: absolute !important; 
height: 20px !important; 
padding: 0px !important; 
top: 10px !important; 
text-align: left !important; 
z-index: 10 !important; 
white-space: normal !important; 
font-size: 11px !important;
}


#p-logo a,
#p-logo a:hover {
	position: absolute !important;
	top: 15px !important;
	width: 65px !important;
	height: 54px !important;
	margin: -10px auto 0px 5px !important;
	padding: 0px !important;
	white-space: normal !important;
	text-align: left !important;
	font-size: 11px !important;
	background-image: url("data:image/png;base64,") !important;	
	background-repeat: no-repeat !important;
	background-position: top right !important;
}

/* .firstHeading { font-size: x-large !important; } */

#p-logo, #mw-panel { height: 0px !important; width: 0px !important; }


.portlet ul, .portlet li { display: inline !important; margin: 0 !important; } 
.portlet h5 { display:none !important; } 
.pBody { border: none !important; padding: 0 !important; font-family: tahoma !important; background-color: transparent !important; } 
.pBody ul li { margin: 0 !important; padding: 0 0.1em 0 0.1em !important; } 
.pBody li a { display: inline !important; } 
#p-cactions { position: absolute !important; top: 165px !important; left: 0 !important; width: 86.35% !important; margin: 0 auto 0 0 !important; padding: 0 !important; float:left !important; text-transform: uppercase !important; white-space: normal !important; border-bottom:1px solid #DDDDDD !important; } 
#p-cactions ul { margin: 0 !important; padding: 0em !important; } 
#p-cactions li { padding: 0.2em 0em 0.2em 0em !important; margin: 0 !important; border-style: solid solid none solid !important; border-color: #333 !important; } 
#p-cactions li.selected { border-style: solid solid none solid !important; border-color: #333 !important; padding: 0.2em 0em 0.3em 0em !important; } 
#p-cactions li.selected a { letter-spacing: 0.5em !important; } 
#p-cactions li a { background-color: #ffffff !important; color: #333 !important; font-family: tahoma !important; text-transform: uppercase !important; } 
#catlinks { text-align: center !important; } 
#p-search { position: absolute !important; margin: 0 !important; left: 90px !important; top: 20px !important; } 
#searchBody { text-align: center !important; } 
#searchInput { color: #6F6F6F !important; background-color: #bbb ! important; border-color: #bbb ! important; } 

/* book printing DE - WIP */
/* #collectionPortletList, #coll-create_a_book {  position: absolute !important;  color: #ccc !important; top: -150px !important; left: 900px !important;  width: 70px !important; font-size: 80%; text-align:right; float: right !important; } */
#p-coll-print_export { display: none !important; }

/* place the search suggestions dropdown */
.suggestions { left: 80px !important; top: 56px !important; }


#p-lang { margin: 3px 0 0px 0 !important; position: absolute !important; float: left !important; left: 310px !important; color: white !important; top: -135px !important; }

/* display only wanted languages */
#p-lang li { display: none }
#p-lang li.interwiki-de, #p-lang li.interwiki-fr, #p-lang li.interwiki-es, #p-lang li.interwiki-it, #p-lang li.interwiki-simple, #p-lang li.interwiki-nds,  #p-lang li.interwiki-en { display: inline !important; }

#p-lang ul li a, #p-lang, #p-lang h5 { display: inline !important; display:block !important;  background: transparent !important;
 color: white !important;} 

#p-lang { width: 500px !important; }
#p-lang ul li a { height: 20px !important; }

div#p-lang li {  float: left !important; width: 80px !important; height:20px !important; }

/* prevent line flickering on loading */
div#p-lang div.body, #footer { background-image: none !important; }

/* hide top language menu selector */
#p-lang h5 { display: none !important; }


h1 { border: none !important; padding-top: 0 !important; font-family: georgia !important; color: #222 !important; font-weight: 100 !important; font-size: 200px !important; }
 

#p-navigation { margin: 0 0 3px 0 !important; float: left !important; position: relative !important; left: 75px !important; width: 750px !important;   }  #p-tb { margin: 3px 0 3px 0 !important; position: relative !important; width: 735px !important; float: left !important; left: 90px !important; }  li#pt-userpage,li#pt-anonuserpage,li#pt-login { background: none !important; padding-left: 0 !important } h1 { border: none !important; padding-top: 0 !important; font-family: georgia !important; color: #222 !important; font-weight: 100 !important; font-size: 180% !important; } h3 { font-weight: 100 !important; } h4 { font-size: 100% !important; } #contentSub { font-size: 100% !important; line-height: 1.2em; margin: 0 0 1.4em 0em !important; color: #7d7d7d; width: auto; } h3#siteSub { font-size: 80% !important; color: gray; } dl > dd > .dablink { margin-left: -2em !important; } .metadata { top: 35px !important; } div.messagebox { } #Vorlage_Dieser_Artikel, #toc, table.hintergrundfarbe1, #catlinks, #f-list { background: #aaa ! important; border-radius: 20px ! important; border-color: #aaa ! important; } .hintergrundfarbe1 { margin: 0 !important; } 

#searchGoButton,  div#f-poweredbyico, div#siteNotice, #p-cactions, #searchBody ul li, div#f-copyrightico, #p-search ul li, #p-cactions ul li, #p-navigation ul li, #searchInput ul li, #p-tb ul li, #p-interaction, #p-Mitmachen, #pt-login, #donate, #n-aboutsite, #n-recentchanges, #n-Bybrunnen, #n-Kontakta-Wikipedia, #n-sitesupport, #n-help, #p-Contribuer, #anontip, #anon-banner, #n-portal, #n-currentevents, #n-Etalage, #n-contact, #n-villagepump, #n-wikipediano, #n-FAQ, #n-bug_in_article, #n-bad-image, #n-Guidelines, #p-feedback, li#pt-acaibeta, li#n-new-entry, li#n-Wunschliste, li#n-Literaturliste, li#pt-optin-try, li#n-disclaimer, li#n-Spenden, #page-base, #pt-prefswitch-link-anon, #mw-head-base, #mw-page-base, DIV#p-navigation, #p-community, #p-beyond_the_web, #footer-info, #footer-info-lastmod, #footer-places, #footer-icons, .articleFeedback-buffer.articleFeedback-ui, #mw-articlefeedbackv5, #pt-createaccount, #p-lang > H3[tabindex="5"] { display: none !important; }


}
