css:
@-moz-document url-prefix("www.youtube.com/"), domain("youtube.com") {
body,
.watch-branded #watch-main-container,
.watch-branded #watch-sidebar {
  color: #fff important !important;
  background: #000 url(http://i1294.photobucket.com/albums/b602/ytpng/www-refreshbg-vflQx0YnW.png) !important;
}

#logo {
  background-image: url("http://i1294.photobucket.com/albums/b602/ytpng/www-refreshbg-vflQx0YnW.png") !important;
  background-position: -151px 0 !important;
}

.feed-item-outer { background-color: #202020; }



#playlist-bar-bar-container {
background-image: -webkit-gradient(linear,left top,left bottom,from(#454545),to(#000000));
}
#playlist-bar-bar-container:hover {
background-image: -webkit-gradient(linear,left top,left bottom,from(#454545),to(#000000));
}

.playlist-video-item.even, .playlist-video-item:nth-child(even) { background: #101010; }
.playlist-video-item.odd, .playlist-video-item:nth-child(odd)   { background: #353535; }
.channel-layout-two-column .tab-content-body, #playlist-pane-container { background: -webkit-gradient(linear,left top,left bottom,from(#252525),to(#000000)); }


#branded-page-body-container {
background-image: url(http://i1294.photobucket.com/albums/b602/ytpng/www-refreshbg-vflQx0YnW.png) !important;
}


#masthead-expanded .playlist-bar-item a, #masthead-expanded .playlist-bar-item {
opacity: 0.7;
filter: alpha(opacity=100);
}

.yt-uix-button-default,
.yt-uix-button-default:focus,
body .yt-uix-button-default[disabled] {
  background-color: #454545 !important;
  border-color: #333 #333 #000 !important;
  text-shadow: 0 1px 0 rgba(0, 0, 0, .45) !important;
  outline: 0 !important;
  background-image: linear-gradient(to bottom,#474747 0,#2B2B2B 100%) !important;
  background-image: -moz-linear-gradient(top,#474747 0,#2B2B2B 100%) !important;
  background-image: -webkit-linear-gradient(top,#474747 0,#2B2B2B 100%) !important;
  background-image: -o-linear-gradient(top,#474747 0,#2B2B2B 100%) !important;
  background-image: -ms-linear-gradient(top,#474747 0,#2B2B2B 100%) !important;
  filter: progid:DXImageTransform.Microsoft.Gradient(GradientType=0, startColorStr="#FF474747", endColorStr="#FF2B2B2B") !important;
  box-shadow: inset 0 1px 0 rgba(255, 255, 255, .45)) !important;
  -moz-box-shadow: inset 0 1px 0 rgba(255,255,255,.45) !important;
  -webkit-box-shadow: inset 0 1px 0 rgba(255,255,255,.45) !important;
  -ms-box-shadow: inset 0 1px 0 rgba(255,255,255,.45) !important;
}

.yt-uix-button-default,
a.yt-uix-button-default .yt-uix-button-content {
  color: #fff !important;
}

.yt-uix-button-default:hover,
.yt-uix-button-text:hover {
  background-color: #333 !important;
  box-shadow: 0 1px 2px rgba(0,0,0,0.25),inset 0 0 3px #666 !important;
  -moz-box-shadow: 0 1px 2px rgba(0,0,0,0.25),inset 0 0 3px #666 !important;
  -webkit-box-shadow: 0 1px 2px rgba(0,0,0,0.25),inset 0 0 3px #666 !important;
  -ms-box-shadow: 0 1px 2px rgba(0,0,0,0.25),inset 0 0 3px #666 !important;
}

.yt-uix-button-default:active,
.yt-uix-button-default.yt-uix-button-active,
.yt-uix-button-default.yt-uix-button-toggled {
  background-color: #454545 !important;
  background-image: linear-gradient(to bottom, #474747 0, #2B2B2B 100%) !important;
  background-image: -moz-linear-gradient(bottom,#474747 0,#2B2B2B 100%) !important;
  background-image: -webkit-linear-gradient(bottom,#474747 0,#2B2B2B 100%) !important;
  background-image: -o-linear-gradient(bottom,#474747 0,#2B2B2B 100%) !important;
  background-image: -ms-linear-gradient(bottom,#474747 0,#2B2B2B 100%) !important;
  filter: progid:DXImageTransform.Microsoft.Gradient(GradientType=0, startColorStr="#FF474747", endColorStr="#FF2B2B2B") !important;
  box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.75), 0 1px 0 #333 !important;
  -moz-box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.75), 0 1px 0 #333 !important;
  -webkit-box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.75), 0 1px 0 #333 !important;
  -ms-box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.75), 0 1px 0 #333 !important;
}

.yt-uix-button-default:active,
.yt-uix-button-text:active,
.yt-uix-button-default.yt-uix-button-toggled,
.yt-uix-button-text.yt-uix-button-toggled {
  border-color: #222 !important;
  border-bottom-color: #666 !important;
  border-top-color: #333 !important;
}

.yt-uix-button-group:hover .yt-uix-button,
.yt-uix-button-group-active .yt-uix-button {
  border-color: #666 !important;
}

.yt-uix-button-default .yt-uix-button-arrow,
.yt-uix-button-text .yt-uix-button-arrow {
  border-top-color: #fff !important;
}

.yt-uix-button:hover .yt-uix-slider-next-arrow,
.yt-uix-button:focus .yt-uix-slider-next-arrow {
  border-left-color: #fff !important;
}

.yt-uix-button:hover .yt-uix-slider-prev-arrow,
.yt-uix-button:focus .yt-uix-slider-prev-arrow {
  border-right-color: #fff !important;
}

.yt-uix-button-text,
a.yt-uix-button-text .yt-uix-button-content {
  color: #666 !important;
}

.yt-uix-button-text:active,
.yt-uix-button-text.yt-uix-button-toggled {
  background-image: linear-gradient(to bottom,#474747 0,#2B2B2B 100%) !important;
  background-image: -moz-linear-gradient(top,#474747 0,#2B2B2B 100%) !important;
  background-image: -webkit-linear-gradient(top,#474747 0,#2B2B2B 100%) !important;
  background-image: -o-linear-gradient(top,#474747 0,#2B2B2B 100%) !important;
  background-image: -ms-linear-gradient(top,#474747 0,#2B2B2B 100%) !important;
  filter: progid:DXImageTransform.Microsoft.Gradient(GradientType=0, startColorStr="#FF474747", endColorStr="#FF2B2B2B") !important;
  box-shadow: inset 0 1px 2px #333 !important;
  -moz-box-shadow: inset 0 1px 2px #333 !important;
  -webkit-box-shadow: inset 0 1px 2px #333 !important;
  -ms-box-shadow: inset 0 1px 2px #333 !important;
}

.yt-uix-button-text,
body .yt-uix-button-text[disabled] {
  text-shadow: none !important;
  border-color: #333 !important;
  box-shadow: 0 1px 0 #333 !important;
  -moz-box-shadow: 0 1px 0 #333 !important;
  -webkit-box-shadow: 0 1px 0 #333 !important;
  -ms-box-shadow: 0 1px 0 #333 !important;
}

.yt-tile-default,
.yt-tile-default a,
.yt-tile-visible,
.yt-tile-visible a {
  color: #666 !important;
}

.yt-tile-static,
.yt-tile-visible,
.yt-tile-default:hover {
  color: #333 !important;
}

.video-list-item:hover a .title,
.video-list-item:hover .stat {
  color: #333 !important;
}

.yt-alert-naked .yt-alert-content {
  color: #fff !important;
}





.yt-uix-button .yt-uix-button-icon-watch-flag {
  background: url(http://i1214.photobucket.com/albums/cc493/JohnTuley/Stylish%20Themes/YouTube%20-%20Earth%20Hour%202012/www-refresh-vflnJTVZv.png) -96px -119px !important;
}

.active .yt-uix-button-icon-watch-flag,
.yt-uix-button:hover .yt-uix-button-icon-watch-flag {
  background: url(http://i1214.photobucket.com/albums/cc493/JohnTuley/Stylish%20Themes/YouTube%20-%20Earth%20Hour%202012/www-refresh-vflnJTVZv.png) -75px -66px !important;
}

.yt-uix-button-icon-addto {
  background: url(http://i1214.photobucket.com/albums/cc493/JohnTuley/Stylish%20Themes/YouTube%20-%20Earth%20Hour%202012/www-refresh-vflnJTVZv.png) 0 -66px !important;
}

.yt-uix-button .yt-uix-button-icon-watch-insight {
  background: url(http://i1214.photobucket.com/albums/cc493/JohnTuley/Stylish%20Themes/YouTube%20-%20Earth%20Hour%202012/www-refresh-vflnJTVZv.png) -154px -207px !important;
}





.yt-uix-button:hover .yt-uix-button-icon-watch-insight {
  background: url(http://i1214.photobucket.com/albums/cc493/JohnTuley/Stylish%20Themes/YouTube%20-%20Earth%20Hour%202012/www-refresh-vflnJTVZv.png) -235px -199px !important;
}



.yt-horizontal-rule {
  border-top-color: #333 !important;
  border-bottom-color: #000 !important;
}


.yt-tile-default.video-list-item a .title,
#watch-sidebar .video-list-item .title {
  color: #666 !important;
}



#watch-channel-discoverbox {
  box-shadow: inset 0 4px 8px rgba(0,0,0,.05),0 1px 0 #333 !important;
  -moz-box-shadow: inset 0 4px 8px rgba(0,0,0,.05),0 1px 0 #333 !important;
  -webkit-box-shadow: inset 0 4px 8px rgba(0,0,0,.05),0 1px 0 #333 !important;
  -ms-box-shadow: inset 0 4px 8px rgba(0,0,0,.05),0 1px 0 #333 !important;
}

#watch-channel-discoverbox .video-list-item .title {
  color: #666 !important;
}

.yt-uix-pager .yt-uix-button,
.yt-uix-pager .yt-uix-pager-link,
.yt-uix-pager .yt-uix-pager-selected {
  color: #fff !important;
}



#watch-description .expand,
#watch-description .collapse {
  background-color: transparent !important;
}

#watch-description-expand,
#watch-description-collapse {
  border-top-color: #333 !important;
}

#watch-description-expand .yt-uix-button,
#watch-description-collapse .yt-uix-button {
  color: #fff !important;
  text-shadow: 0 1px 0 #333 !important;
}

#watch-description-expand .yt-uix-button:hover,
#watch-description-collapse .yt-uix-button:hover {
  background-image: linear-gradient(to bottom,#333 0,#000 100%) !important;
  background-image: -moz-linear-gradient(top,#333 0,#000 100%) !important;
  background-image: -webkit-linear-gradient(top,#333 0,#000 100%) !important;
  background-image: -ms-linear-gradient(top,#333 0,#000 100%) !important;
  filter: progid:DXImageTransform.Microsoft.Gradient(GradientType=0, startColorStr="#FF333333", endColorStr="#FF000000") !important;
}




#masthead-nav a,
#masthead-user a,
#masthead-nav a:hover,
#masthead-user a:hover,
#masthead-nav a:active,
#masthead-user a:active {
  color: #ccc !important;
}

#masthead-expanded-container {
  box-shadow: 0 5px 5px #222 !important;
  -moz-box-shadow: 0 5px 5px #222 !important;
  -webkit-box-shadow: 0 5px 5px #222 !important;
  -ms-box-shadow: 0 5px 5px #222 !important;
}



#watch-headline h1,
#eow-title-input {
  color: #fff !important;
}

#masthead-user-expander .yt-uix-expander-head,
#masthead-gaia-user-wrapper,
#masthead-gaia-photo-wrapper {
  border-color: #000 !important;
}

#masthead-search .search-btn-compontent .yt-uix-button-content {
  background: no-repeat url(http://i1214.photobucket.com/albums/cc493/JohnTuley/Stylish%20Themes/YouTube%20-%20Earth%20Hour%202012/www-refresh-vflnJTVZv.png) -163px -102px !important;
}

#masthead-search-terms {
  background-color: #898989 !important;
}

#masthead-search-terms input {
  color: #fff !important;
  text-shadow: 0 1px 1px #333 !important;
}

.masthead-search-terms-border {
  border-color: #333 !important;
  box-shadow: inset 0 1px 2px #333 !important;
  -moz-box-shadow: inset 0 1px 2px #333 !important;
  -webkit-box-shadow: inset 0 1px 2px #333 !important;
  -ms-box-shadow: inset 0 1px 2px #333 !important;
}



#watch-context {
  border-color: #333 !important;
}

#watch-context .context-head {
  border-bottom-color: #333 !important;
}

.context-link {
  color: #ccc !important;
  text-shadow: 0 0 0 rgba(255, 255, 255, 0),0 1px 0 #000 !important;
  background-image: linear-gradient(to bottom,#333 0,#202020 100%) !important;
  background-image: -moz-linear-gradient(top,#333 0,#202020 100%) !important;
  background-image: -webkit-linear-gradient(top,#333 0,#202020 100%) !important;
  background-image: -o-linear-gradient(top,#333 0,#202020 100%) !important;
  background-image: -ms-linear-gradient(top,#333 0,#202020 100%) !important;
  box-shadow: 0 1px 2px rgba(255,255,255,0.25),inset 0 0 3px #333 !important;
  -moz-box-shadow: 0 1px 2px rgba(255,255,255,0.25),inset 0 0 3px #333 !important;
  -webkit-box-shadow: 0 1px 2px rgba(255,255,255,0.25),inset 0 0 3px #333 !important;
  -ms-box-shadow: 0 1px 2px rgba(255,255,255,0.25),inset 0 0 3px #333 !important;
}

a.context-link:hover {
  background-image: linear-gradient(to bottom,#333 0,#202020 100%) !important;
  background-image: -moz-linear-gradient(top,#333 0,#202020 100%) !important;
  background-image: -webkit-linear-gradient(top,#333 0,#202020 100%) !important;
  background-image: -o-linear-gradient(top,#333 0,#202020 100%) !important;
  background-image: -ms-linear-gradient(top,#333 0,#202020 100%) !important;
}

.context-seeall-icon {
  background: url(http://i1214.photobucket.com/albums/cc493/JohnTuley/Stylish%20Themes/YouTube%20-%20Earth%20Hour%202012/www-refresh-vflnJTVZv.png) -75px -37px !important;
}

.context-body {
  background: #222 !important;
}



#watch-actions-area {
  background: #333 !important;
  box-shadow: 0 1px 1px #666 !important;
  -moz-box-shadow: 0 1px 1px #666 !important;
  -webkit-box-shadow: 0 1px 1px #666 !important;
  -ms-box-shadow: 0 1px 1px #666 !important;
}

#watch-description-toggle {
  border-top-color: #333 !important;
}

.watch-sparkbars {
  border-color: #333 !important;
}

.watch-view-count {
  color: #fff !important;
}

#insight-ratings td {
  color: #fff !important;
}

.watch-stats-title-cell {
  background-color: #666 !important;
}

#flag-video-panel h3,
#flag-video-panel label,
#flag-video-panel-buttons,
#flag-video-panel .flag-video-result {
  color: #fff !important;
}

.share-panel .share-options,
.share-panel .share-options-secondary {
  border-color: #666 !important;
  background-color: #333 !important;
  color: #666 !important;
  box-shadow: 0 1px 0 #333,inset 0 1px 1px rgba(0,0,0,0.2) !important;
  -moz-box-shadow: 0 1px 0 #333,inset 0 1px 1px rgba(0,0,0,0.2) !important;
  -webkit-box-shadow: 0 1px 0 #333,inset 0 1px 1px rgba(0,0,0,0.2) !important;
  -ms-box-shadow: 0 1px 0 #333,inset 0 1px 1px rgba(0,0,0,0.2) !important;
}

.share-panel-show-more,
.share-panel-show-url-options {
  color: #fff !important;
}

.share-panel-hangout .share-panel-hangout-description {
  color: #fff !important;
}

#watch-description-text {
  color: #666 !important;
}



.comments-section h4 {
  border-bottom-color: #333 !important;
}

.comments-post-alert {
  background-color: #333 !important;
  border-color: #666 !important;
  color: #666 !important;
}




#footer ul {
  text-shadow: 0 1px 1px #333 !important;
}

#footer-links-primary a,
#footer-links-secondary a,
#footer .pickers a,
#picker-loading {
  color: #fff !important;
}

#footer-main .pickers .yt-uix-button {
  color: #fff !important;
  text-shadow: 0 1px 1px #333 !important;
}


#masthead-container { background: none !important; }


#yt-masthead-container.yt-masthead-hh {
background-image: url("http://i1294.photobucket.com/albums/b602/ytpng/www-refreshbg-vflQx0YnW.png");
border-bottom: 2px solid black;
}
#.masthead-search-terms-border {
border: 1px solid #888;
}
#.yt-uix-button-hh-default, .yt-uix-button-subscription, .yt-uix-button-panel:hover .yt-uix-button-hh-text, body .yt-uix-button-hh-default[disabled] {
border-color: #888;
}



#watch7-content {
background: url("http://i1294.photobucket.com/albums/b602/ytpng/www-refreshbg-vflQx0YnW.png");
}
}
