css:
@-moz-document domain("tumblr.com") {
.new_post_label_icon {
    background-image: url('http://i53.tinypic.com/x5z2ud.jpg') !important;
    background-size:auto !important;
    top: 9px !important;
    left: -1px !important;
}

.new_post_label {
    padding-top: 67px !important;
}

#new_post_label_text:active .new_post_label_icon {
    background-position: 2px -5px !important;
}
#new_post_label_photo:active .new_post_label_icon {
    background-position: -73px -5px !important;
}
#new_post_label_quote:active .new_post_label_icon {
    background-position: -148px -5px !important;
}
#new_post_label_link:active .new_post_label_icon {
    background-position: -223px -5px !important;
}
#new_post_label_chat:active .new_post_label_icon {
    background-position: -298px -5px !important;
}
#new_post_label_audio:active .new_post_label_icon {
    background-position: -373px -5px !important;
}
#new_post_label_video:active .new_post_label_icon {
    background-position: -448px -5px !important;
}


}




@-moz-document url('https://www.tumblr.com/login'),
url-prefix('http://www.tumblr.com/dashboard'),
url-prefix('http://www.tumblr.com/new/blog'),
url-prefix('http://www.tumblr.com/inbox'),
url('http://www.tumblr.com/help'),
url('https://www.tumblr.com/preferences'),
url('http://www.tumblr.com/lookup'),
url('http://www.tumblr.com/explore'),
url('http://www.tumblr.com/following'),
url('http://www.tumblr.com/likes'),
url-prefix('http://www.tumblr.com/new/'),
url-prefix('http://www.tumblr.com/tumblelog/'),
url-prefix('http://www.tumblr.com/blog/'),
url-prefix('http://www.tumblr.com/tagged/'),
url-prefix('http://www.tumblr.com/spotlight/'),
url-prefix('http://www.tumblr.com/edit/')
, url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/new/blog"), url-prefix("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/lookup"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/") {

	/*BIG BACKGROUND begin - background for the entire screen*/
	body {
	  background: url("http://i53.tinypic.com/6p9c03.jpg") no-repeat fixed !important;
	  background-color: #092cc7 !important;
	  background-position: 0% 0% !important;
	  background-size: 100% 100%  !important;
	}
	/*BIG BACKGROUND end*/

	/*LOGO begin - tumblr logo*/
	#logo {
	  width: 0px !important;
	  height: 0px !important;
	  background:none !important;
	  width: 196px !important;
	  height: 44px !important;
	}
	/*LOGO end*/

	/*ICON BAR begin - new post icons bar - graphic replacement*/
	img[src*=""]{
	opacity: 0.00 !important;  
	}
	#new_post{
	  background-image: none !important;
	  background-position: center center !important; 
	  background-repeat: no-repeat !important;
	}
	/*ICON BAR  end*/

	/*ICON BAR BACKGROUND begin - background for the icon bar*/
	#new_post {
	  background-color: rgba(255, 255, 255, 1.0) !important;
	}
	/*ICON BAR BACKGROUND end*/

	/*AVATAR ARROW begin - arrow to the left of the icon bar*/
	#new_post .arrow {
	  opacity: 1.0 !important;
	  margin-left: 0px !important; 
	}
	/*AVATAR ARROW end*/

	/*CONTENT begin - background for posts, icon bar, sidebar*/
	#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	  background: rgba(10,10,10,0.0) !important;
	}
	/*CONTENT*/ 
	
	/*DASHBOARD CONTROLS begin*/
	#dashboard_controls a, #dashboard_controls .control{
		color: gold !important;
		background-color: rgba(19,19,19, 1.0) !important;
		padding: 5px;
	}
	#dashboard_controls a:hover{
		color:yellow !important;
	}
	/*DASHBOARD CONTROLS end*/
	
	/*POST BACKGROUND begin*/
	#posts LI[class*=" post"] {
	  background: rgba(255, 255, 255, 1.0) !important;
	}
	/*POST BACKGROUND end*/

	/*SELECTION ARROW begin - points to dashboard selection*/
	.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
	  background: none !important;
	  width: 0px !important;
	  height: 0px !important;
	  margin-top: 0px !important;
	  border-bottom: solid 11px rgba(255,255,255,1.0) !important;
	  border-right: 11px solid transparent !important;
	  border-left: 11px solid transparent !important;
	}
	.selection_nipple:not([class="selection_nipple white"]) {
	  border-bottom: solid 11px rgba(19,19,19, 1.0)  !important;
	}
	/*SELECTION ARROW end*/

	/*RADAR begin - removes radar blue frame - softens appearance*/
	.post.photo_post .photo, .post.photo_post .video_thumbnail {
	  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
	  border-radius: 12px !important;
	}
	.content.is_media, .mask.top, .mask.bottom {
	  background: none !important;
	}
	/*RADAR end*/

	/*NEW CONTENT begin - text post message body background*/
	.mceContentBody {
	  color: rgba(0, 0, 0, 1.0) !important;
	  background: rgba(255,255,255, 1.0) !important;
	}
	/*NEW CONTENT end*/

	.permalink {
	  background: none !important;
	  width: 16px !important;
	  height: 16px !important;
	  background: rgba(170,179,188, .85) !important;
	  border-bottom-left-radius: 10px !important;
	  border-top-right-radius: 24px !important;
	  margin-top: 6px !important;
	  margin-right: 6px !important;
	}

	.permalink:hover {
	background: rgba(152,143,127, 1.0 ) !important;
	}

	.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}
	.post.photo_post .photo, .post.photo_post .video_thumbnail {
	  -moz-box-shadow: 2px 2px 2px rgba(0,0,0, 0.50) !important;
	  border-radius: 12px !important;
	}

	.content.is_media, .mask.top, .mask.bottom {
	  background: none !important;
	}

	#left_column #posts .notification {
	  background-color:#242424 !important;
	  color:#666 !important;
	  border-bottom-color: #0f0f0f !important;
	  border-top-color: #0f0f0f !important;
	  border-right-color: #0f0f0f !important;
	  border-left-color: #0f0f0f !important
	}

	#left_column #posts .notification .block {
	  color:#242424 !important;
	}

	#left_column #posts .notification.alt {
	  background-color: #242424 !important;
	}

	#left_column #posts .notification.last_notification,

	#left_column #posts .notification.single_notification {
	  border-bottom-color:#0f0f0f !important;
	}

	#left_column #posts .notification a {
	  color:#929292 !important;
	  border-bottom:0px !important;
	}

	#left_column #posts .notification blockquote {
	  border-left-color:#242424 !important;
	}

	#left_column #posts .notification .avatar {
	  border: 0px !important;
	  background-color:#242424 !important;
	  opacity:1 !important;
	}

	#left_column #posts .notification .avatar:hover {
	  opacity:1 !important;
	}

	.nipple {
	  background: none !important;
	  width: 0px !important;
	  height: 0px !important;
	  border-right: solid 6px rgba(36,36,36, 1.00) !important;
	  border-bottom: 6px solid transparent !important;
	  border-left: 0px solid transparent !important;
	  border-top: 6px solid transparent !important;
	  margin-top: 0px !important;
	}

	.nipple.border {
	  background: none !important;
	  width: 0px !important;
	  height: 0px !important;
	  border-right: solid 6px rgba(19,19,19, 1.0) !important;
	  border-bottom: 6px solid transparent !important;
	  border-left: 1px solid transparent !important;
	  border-top: 6px solid transparent !important;
	  margin-top: 1px !important;
	}
	
	div.l-content #search_query{
		background-color: rgba(19,19,19, 1.0) !important;
	}

	.controls_section {
		background-color: rgba(19,19,19, 1.0) !important;
	}
	.recessed {
		background-color: rgba(19,19,19, 1.0) !important;
	}
	#tumblrlife-shortcut-key-help{
		display: none !important;
	}
	.radar{
		display: none !important;
	}
	#footer{
		color: white !important;
	}
	#footer a{
		color: white !important;
	}
	#pagination a{
		background-color: rgba(19,19,19, 1.0) !important;
	}

	*{
	  text-shadow:none !important;
	}
	
/*DO NOT DELETE THIS BRACKET*/
}

@-moz-document url-prefix('http://www.tumblr.com/new/'), url-prefix('https://www.tumblr.com/new/')  {
    #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    background-image: url("http://tumblr.com/images/content_bg_edit_form.png") !important;
    }
}

@-moz-document url-prefix('http://www.tumblr.com/following'), url-prefix('https://www.tumblr.com/preferences'), url-prefix("https://www.tumblr.com/following") {
    #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
    background-color: white !important;
    }
}
