css:
@-moz-document url("9.douban.com") {
/*

author: tumuyan
update: april.1 ,2012
version: 0.1

*/
}


@-moz-document url("http://9.douban.com/reader/") {
.zblog_item img{display:block !important;}


.zblog_item:hover .zblog_item_desp > a[href^="/site/entry/"]{position:fixed !important ;left:150px !important ;bottom:60px !important ;
background:#ff0 !important ;
z-index:3 !important ;padding:2px 7px !important ;
border-radius:9px;
color:#fff !important ;

}

.zblog_item:hover .zblog_item_desp > a[href^="/site/entry/"]:hover{
z-index:6 !important ;
color:#00f !important ;
}





.zblog_item:hover a.zblog_item_title_now
{
background:#eef !important ;padding:8px 24px !important ;border-radius:15px !important ;font-size:18px !important ;box-shadow:8px 5px 8px #ccc;
position:fixed !important;left:250px !important;bottom:50px !important;

-moz-transition:box-shadow .5s ease-in;
-webkit-transition:box-shadow .5s ease-in;
-o-transition:box-shadow .5s ease-in;
}




.zblog_item:hover a.zblog_item_title_now > small
{margin:0px 15px  !important }



.zblog_item  p.rec_p

{
display:none !important ;
}

.zblog_item:hover  p.rec_p

{
display:inline !important ;
}




.zblog_item:hover  p.rec_p a.lnk-douban-sharing

{position:fixed !important;left:150px !important;bottom:40px !important;
color:transparent !important ;background:#faa96c !important ;
width:20px !important ;overflow:hidden !important ;
text-align:right !important ;
z-index:4 !important ;

padding:8px 20px !important ;border-radius:15px;font-size:18px !important ;box-shadow:8px 5px 14px #ffd;
}
.zblog_item:hover  p.rec_p a.collected,a.collect

{position:fixed !important;left:150px !important;bottom:50px !important;
color:transparent !important ;
z-index:5 !important ;
width:12px !important ;height:16px !important ;
overflow:hidden !important ;
background:#afe !important ;
text-align:left !important ;
padding:4px 7px;border-radius:15px !important ;font-size:18px !important ;box-shadow:-4px 3px 4px #ddd;
}
.zblog_item:hover  p.rec_p a.collected

{
background:#f67 !important ;box-shadow:-4px 3px 4px #f9c;
}




.zblog_item_desp{background:#eee !important ;}

@media all and (min-width:800px) and (min-height:400px) {


img[onclick="show_unread()"]{position:fixed ;right:10px;top:215px}
a#next_page {position:fixed !important ;right:90px !important ;top:210px !important ;background:transparent !important ; }
a#pre_page {position:fixed !important ;right:130px !important ;top:210px !important ;background:transparent !important ; }
body{background:#f3f2f0 !important ;}



.readerright{position:fixed !important ;right:0px !important ;background:#eee !important ;padding:10px !important ;top:200px !important }


}
}
