@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("snakenetmetalradio.com") {

a, a font { text-decoration: none !important;
	color: #FF9900 !important;
}

a:hover, a font:hover {
    color: red !important;
}


table[width="700"] a[href*="profile.asp"], a[href*="profile.asp"] font, .spnMessageText  a[href*="profile.asp"], td   a[title*="View Member"] {
    color: /*[[un_color]]*/ !important;
}

table[width="700"] a[href*="profile.asp"]:hover, a[href*="profile.asp"] font:hover, .spnMessageText  a[href*="profile.asp"]:hover, td   a[title*="View Member"]:hover {
    color:red !important;
}


.spnMessageText font[size="1"]{
    font-style: italic !important;
}

table[width="95%"] td {
    padding:4px !important; 
}

table[width="725"] td , table[width="700"][align="center"] td {
    padding:4px !important;
    vertical-align:middle !important;
}   

table[cellspacing="10"]  {
 vertical-align:top !important;
}

}
