 * Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
 * See http://creativecommons.org/licenses/by-nc-sa/3.0/ 
 * for further details on this licence.
 * ----------------------------------------------------------------------------
 */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.openstreetmap.org") {
body {
 position:absolute!important;
 top:0px!important;
 left:0px!important;
 bottom:0px!important;
 border:solid 0px!important;
 width:100%!important;
 height:100%!important; 
 margin:0px!important;
 padding:0px!important;
 font-family:"Times", serif!important; 
 font-size:16px!important;
 box-shadow:
  inset 0px 1px 3px -2px rgba(255,255,255,0.8),
  inset 0px 2px 70px 0px rgba(0,0,0,0.5)
 !important;
  
 background:#5E6066 url(data:image/png;base64,)!important;
/* Selected colors: #DDD, #666,#9CA4A8, #84736B, #755055, #E8CEAA*/
 background-repeat:no-repeat!important;
 background-attachment:fixed!important;
 background-position:bottom center!important;
}
/* mainframe */
#content {
 position:fixed!important;
 top:30px!important;
 bottom:0px!important;
 left:0px!important;
 right:0px!important;
 margin:0px!important;
 padding:0px!important;
 overflow:auto!important;
 color:#DDD!important;
}
#content>* {
 margin:10px!important;
}
table td>img {
 margin:0px!important;
 border:solid 3px rgba(100,100,100,0.5)!important;
 border-radius:5px!important;
}
/* Basic formatting */
h1, h2, h3, h4, h5, h6 { 
 border-bottom:solid 1px rgba(50,50,50,0.5)!important;
 padding:5px 0px!important;
 color:inherit!important;
}
a {
 text-decoration:none!important; 
 color:#DDD!important; 
}
a:hover {
 color:#fff!important;
 text-shadow:0px 0px 7px #FC0!important;
}
/* Form controls */
#signupForm,
#accountForm,
#new_trace table,
table.diary_entry {
 padding:10px!important;
 border-radius:4px!important;
 background-color:#333!important;
 color:#fff!important;
}
input, textarea, select {
 appearance:none!important;
 -moz-appearance:none!important;
 margin:0px!important;
 padding:1px!important;
 border:solid 1px #444!important;
 font-size:13px!important;
 color:#AAA!important;
 max-width:250px!important;
 background:#2e2e2e!important; 
 background:-moz-linear-gradient(bottom, #424242, #2e2e2e)!important;
 background:linear-gradient(bottom, #424242, #2e2e2e)!important; 
 box-shadow: inset 0px 1px 2px #333;  
 z-index:103!important;
 height:auto!important;
}
input:focus,
input:hover,
textarea:focus,
textarea:hover {
 -moz-box-shadow:inset 0px 1px 2px #000!important;
 box-shadow:inset 0px 1px 2px #000!important;
}
input[type="submit"],
input[type="button"]
{
 padding:3px 5px!important;
 background:#2e2e2e!important; 
 background:-moz-linear-gradient(top, #2e2e2e, #1e1e1e)!important;
 background:linear-gradient(top, #2e2e2e, #1e1e1e)!important;
 border-radius:4px!important;
 z-index:103!important;
}
input[type="submit"]:hover,
input[type="button"]:hover,
input[type="file"]:hover {
 color:#FFF!important;
 text-shadow:0px 0px 7px #EBFCFF!important;
 box-shadow:none!important;
}
input[type="submit"]:active,
input[type="button"]:active {
 box-shadow:inset 0px 1px 5px #000!important;
}
/* End form controls */
ul#tabnav {
 position:fixed!important;
 top:0px!important;
 left:0px!important;
 margin:0px!important;
 padding:0px 0px 0px 20px!important;
 width:100%!important;
 height:30px!important;
 z-index:100!important; 
 background:-moz-linear-gradient(bottom, #1A1B1D, #3B3B43)!important;
 background:linear-gradient(bottom, #1A1B1D, #3B3B43)!important;
 box-shadow:
 0px 2px 5px 2px rgba(60,60,60,0.8), 
 inset 0px 5px 8px -3px rgba(0,0,0,1)!important;
 border-bottom:solid 1px #9093A0!important;
}
l#tabnav a {
 z-index:205!important;
}
#tabnav a {
 display:block!important;
 height:23px!important;
 float:left!important; 
 background:#252525!important;
 background:-moz-linear-gradient(top, #44444B, #1E1F21) #2e2e2e!important;
 background:linear-gradient(top, #444, #1F1F1F) #2e2e2e!important;
 box-shadow:
 inset 0px 7px 5px -5px rgba(0,0,0,1)
!important;
 border:solid 0px!important;
 border-left:solid 1px rgba(128,128,128,0.1)!important;
 border-right:solid 1px rgba(128,128,128,0.1)!important;
 padding:5px 8px 0px 8px!important;
 margin:0px!important;
 text-align:center!important; 
 font-weight:normal!important; 
 font-size:13px!important;
 color:#ddd!important;
 text-shadow:0px 1px 1px #000!important;
}
#tabnav a:hover,
#tabnav a.active {
 background:#3F454A!important;
 background:-moz-linear-gradient(top, #2e2e2e, #424242)!important;
 background:linear-gradient(top, #2e2e2e, #424242)!important;
 -moz-border-radius: 3px 3px 0px 0px!important;
 border-radius:0px!important;
 box-shadow:inset 0px 5px 3px 0px rgba(0,0,0,0.9)!important;
 text-shadow:0px 1px 6px #3BCEFF!important;
 color:#f4f4f4!important;
}
#tabnav a:focus {
 color:#fff!important;
 text-shadow:0px 1px 6px #3BCEFF!important;
}
/* Despite the copyright notice, all elements inside div#left 
  are decoupled from this container by using "position:fixed"
  Hence the position of the copyright notice is controlled by
  the position of #left.
*/
#left { 
 position:fixed!important;
 display:block!important;
 top:0px!important;
 left:0px!important;
 right:0px!important;
 z-index:103!important;
 margin:0px auto!important;
 padding:0px!important;
 border:solid 0px transparent!important;
 width:0px!important;
 height:auto!important;
 z-index:101!important;
}
#left div.sidebar-copy {
 display:none!important;
}
/*
#left div.sidebar-copy {
 position:relative!important;
 top:-100px;
 left:-200px!important;
 right:0px!important;
 background:rgba(0,0,5,0.5)!important;
 margin:0px auto!important;
 padding:4px!important;
 width:400px!important;
 color:#fff!important;
 border:solid 0px transparent!important;
 -moz-animation:moveout 7s!important;
}
#left div.sidebar-copy *{
 margin:0px!important;
}
*/
/* --- menus --- */
#left_menu,
#greeting a,
#sidebar a,
div.optionalbox p.search_help a
{
 margin:0px!important;
 padding:0px!important;
 color:#fff!important;
}
/* --- Drop-down menus --- */
#left_menu>ul:hover,
#greeting:hover,
#editmenu>* /* respects overlapping embedded objects */
{
 visibility:visible!important;
 width:auto!important;
 height:auto!important; 
 background:#1e1e1e!important;
 background:-moz-linear-gradient(bottom, #101010, #2e2e2e)!important;
 background:linear-gradient(bottom, #101010, #2e2e2e)!important; 
 border:solid 1px #AAA!important;
 border-radius:4px!important;
 box-shadow:0px 1px 7px #000!important;
 font-size:13px!important;
 font-weight:normal!important;
 margin:0px!important;
 padding:5px!important;
}
#left_menu>ul:before,
#greeting:before {
 visibility:visible!important;
 content: "▼"!important;
 color: #DDD;
 font-size:0.7em!important;
 z-index:104!important;
 width:10px!important;
 height:30px!important;
 text-align:center;
}
#left_menu:hover:before,
#greeting:hover:before {
 color:#F0F0F0!important;
 text-shadow:0px 0px 7px #3BCEFF!important;
}
#left_menu>ul>li,
#greeting>* {
 display:none!important;
}
#left_menu ul:hover li,
#greeting:hover>* {
 display:block!important;
}
/* --- Specific Drop-down settings: left_menu --- */
div#left_menu>ul {
 position:fixed!important;
 display:block!important;
 top:30px!important;
 left:5px!important;
 z-index:104!important;
 width:0px!important;
 height:0px!important;
 padding:0px!important;
 margin:0px!important;
 border:solid 0px transparent!important;
 color:#fff!important;
}
#left_menu ul:before {
 position:fixed!important;
 top:5px!important;
 left:5px!important;
 margin:0px!important;
 padding:0px!important;
}
#left_menu>li {
 padding:1px 5px 0px 5px!important;
 border:solid 1px #fff!important;
}
/* --- Specific settings: greetings --- */
#greeting {
 visibility:hidden!important;
 display:block!important;
 position:fixed;
 top:30px!important;
 right:5px!important;
 z-index:104!important;
 width:0px!important;
 height:0px!important;
 margin:0px!important;
 color:#fff!important;
 text-align:center!important;
}
#greeting:before {
 position:fixed!important;
 top:0px!important;
 right:5px!important;
}
/* --- Optional box --- */
#left>div:not(.sidebar-notice):not(.optionalbox):not(#left_menu):not(.donate):not(.sidebar-copy) {
 display:none!important;
}
#left div.optionalbox {
 position:fixed!important;
 top:32px!important;
 left:auto!important; 
 right:20px!important;
 z-index:102!important;
 background-color:transparent!important;
 border:solid 1px transparent!important;
 color:#f4f4f4!important;
 width:250px!important;
 margin:0px!important;
 padding:0px!important;
}
#left div.optionalbox>*{
 display:none!important; 
 padding:0px 5px 5px 5px!important;
}
#left div.optionalbox:hover {
 background:-moz-linear-gradient(bottom, #101010, #2e2e2e)!important;
 background:linear-gradient(bottom, #101010, #2e2e2e)!important; 
 border:solid 1px #AAA!important;
 border-radius:4px!important;
 box-shadow:0px 2px 5px #000!important;
 max-height:500px!important;
 overflow:auto!important;  
}
#left div.optionalbox:hover>*:not(h1) {
 display:block!important; 
}
#left div.optionalbox .whereami {
 display:block!important;
}
#left div.optionalbox>h1 {
 position:fixed!important;
 top:2px!important;
 left:auto!important;
 right:30px!important;
 padding:0px!important;
 margin:0px!important;
 border:0px!important;
 font-weight:normal!important;
 font-size:13px!important;
 color:#c4c4c4!important;
 overflow:visible !important 
}
/* Search box */
#left div.search_container {
 position:fixed!important;
 top:4px!important;
 right:20px!important;
 left:auto!important;
 display:block!important;
 width:170px!important;
 padding:0px!important;
 margin:0px!important;
 z-index:104!important;
}
#search_form input[type="text"] {
 margin:0px!important;
}
#search_form input[type="submit"] {
 position:relative!important;
 left:-5px!important;
 border-radius:0px 4px 4px 0px!important;
 padding:inherit!important;
 margin:0px!important;
 width:30px!important;
 float:right!important; 
}
#left div.optionalbox span.whereami {
 position:fixed;
 top:4px!important;
 right:191px!important;
 z-index:102!important; 
 
}
#left div.donate{
 position:fixed;
 top:4px!important;
 right:300px!important;
 z-index:102!important; 
 
}
#left div.optionalbox span.whereami,
#left div.donate {
 margin:0px!important;
 padding:3px 5px!important;  
 background:#2e2e2e!important; 
 background:-moz-linear-gradient(top, #2e2e2e, #1e1e1e)!important;
 background:linear-gradient(top, #2e2e2e, #1e1e1e)!important; 
 border:solid 1px #444!important;
 border-radius:4px 0px 0px 4px;
 font-size:13px!important; 
 line-height:13px!important;
 float:left!important;
 width:auto!important;
}
span.whereami:active {
 background:#2e2e2e!important;
 background:-moz-linear-gradient(bottom, #2e2e2e, #1e1e1e)!important;
 background:linear-gradient(bottom, #2e2e2e, #1e1e1e)!important;
 border:solid 1px rgba(140,140,140,0.4)!important; 
}
span.whereami a,
span.whereami:active a,
span.whereami:focus a,
#left div.donate a,
#left div.donate:active a,
#left div.donate:focus a {
 color:#AAA!important;
}
#left div.optionalbox span.whereami:hover a,
#left div.donate:hover a {
 color:#FFF!important;
 text-shadow:0px 0px 7px #EBFCFF!important;
}
#left .sidebar-notice {
 position:fixed!important;
 display:block!important;
 left:0px!important;
 right:0px!important;
 top:-130px!important;
 display:block!important;
 width:auto!important;
 max-width:40%!important;
 max-height:100px!important;
 margin:0px auto!important;
 padding:7px!important;
 color:#fff!important;
 z-index:102!important;
}
#left .sidebar-notice:after {
 position:fixed!important;
 top:26px!important;
 right:0px!important;
 left:0px!important;
 margin:0px auto!important;
 width:40%!important;
 height:10px!important;
 display:block!important;
 content:""!important;
 color:#fff!important;
 font-weight:bold!important;
 background:-moz-radial-gradient(50% 50%, rgba(245,235,255, 1), rgba(245,235,255,0) 70%);
 padding:0px 0px!important;
 margin:0px auto!important;
 text-align:center!important;
 line-height:32px!important;
 font-weight:normal!important;
 opacity:0.5;
 z-index:101!important;
}
#left .sidebar-notice:hover:after {
 opacity:1;
}
#left .sidebar-notice:hover {
 top:31px!important;
}
.sidebar-notice {
 background-color:transparent!important;
 background:
  -moz-radial-gradient(50% 90%, rgba(50,200,255, 0.5), rgba(0, 20, 50, 0.0)),
  -moz-linear-gradient(top, rgba(255,255,255,0.7) 0%, rgba(255,255,255,0.25) 47%,rgba(255,255,255,0.5) 48%, rgba(50,50,50,0.3) 49%,rgba(255,255,255,0.1) 100%),
  rgba(100,150,255,0.6)!important;
 border-radius:5px!important;
 border:solid 1px #7C7C7C!important;
 border-bottom-color:#BCBCBC!important;
 border-top-color:#DDD!important;
 box-shadow:
 0px -5px 20px 5px rgba(0,0,0,0.2), 
 0px 1px 15px -2px rgba(255,255,255,0.6), 
 inset 0px 10px 50px rgba(0,0,0,0.7),
 inset 0px -10px 30px rgba(50,50,50, 0.5)!important; 
 margin:0px 40%!important;
 text-align:center!important;
}
#sidebar,
#trace_list,
#changeset_list,
#nearbyusers,
#content.trace_view table {
 z-index:100!important;
 background:rgba(0,0,0,0.8)!important;
 padding:0px!important;
 border:solid 1px #DDD!important;
 border-radius:5px!important;
 box-shadow:0px 0px 5px #000!important;
 color:#DDD!important;
}
#sidebar {
 margin-right:0px!important;
}
#sidebar .sidebar_title,
#content.trace_view tr:first-child {
 color:#fff!important;
 background:#3B3B3B!important; 
 background:-moz-linear-gradient(top, #303030, #4e4e4e)!important;
 background:linear-gradient(top, #303030, #4e4e4e)!important;
 border-radius:5px 5px 0px 0px!important;
 border-bottom:solid 1px #222!important;
}
#sidebar *[class*="_heading"] {
 color:#fff;
 border:0px!important;
 background:#303030!important; 
 background:-moz-linear-gradient(bottom, #303030, #4e4e4e)!important;
 background:linear-gradient(bottom, #303030, #4e4e4e)!important; 
 
/* background:-moz-radial-gradient(left, #3BCEFF, rgba(0,0,0,0))!important;*/
 border-style:solid!important;
 border-width:1px 0px!important;
 border-color:#626262!important;
}
#sidebar p.search_results_entry {
 color:#fc0;
}
#sidebar #search_osm_nominatim,
#sidebar #search_geonames,
#description_osm_nominatim_,
#description_geonames_
{
 padding:0px!important;
}
#sidebar p.search_results_entry:hover,
#changeset_list tr:hover,
#trace_list tr:hover,
#nearbyusers tr:hover {
 color:#fff!important;
 background:rgb(0, 168, 224)!important; 
 background:-moz-linear-gradient(bottom, rgba(0, 168, 224, 0.75), rgba(141, 210, 239, 0.75))!important; 
 background:linear-gradient(bottom, rgba(0, 168, 224, 0.75), rgba(141, 210, 239, 0.75))!important; 
 box-shadow:0px 0px 2px 0px #3BCEFF, 0px 1px 3px #000!important; 
}
#sidebar p.search_results_entry:hover a,
#changeset_list tr:hover a,
#trace_list tr:hover a,
#nearbyusers tr:hover a {
 color:#005!important;
 text-shadow:none!important;
}
#sidebar p.search_results_entry a:hover,
#changeset_list tr a:hover,
#trace_list tr a:hover,
#nearbyusers tr a:hover {
 color:#FC0!important;
 text-shadow:none;
}
#permalink {
 position:absolute!important;
 bottom:-1px!important;
 right:-1px!important;
 width:auto!important;
 height:auto!important;
 border-radius:30px 0px 0px 0px!important; 
}
#permalink {
 background-color:rgba(100,100,100,0.5)!important;
 padding:3px 7px!important;
 border:solid 1px transparent!important;
}
.SimpleLayerSwitcher:hover,
#permalink:hover {
 border-radius:0px!important;
 /*background-color:rgba(0,0,0,0.7);*/
 box-shadow:
  inset 0px 0px 25px rgba(0,0,0,0.5),
  0px 0px 7px -2px #333;
 border:solid 1px #BBB!important;
}
#permalink:before {
 display:block;
 float:left!important;
 content:"⚯"!important; /* Alternatively: ⇱ */
 color:#fff!important;
 font-family:"Arial", sans-serif!important;
 font-size:1.5em!important;
 margin:3px 0px 0px 4px!important;
 padding:0px!important;
 vertical-align:bottom!important;
}
#permalink:hover:before{
 content:"⚯"!important; /* Alternatively: ⇱ */
 border-bottom:solid 1px #fff!important;
 position:relative!important;
 margin:0px!important;
 width:100%!important;
 text-align:center!important;
}
#permalink>* {
 display:none!important;
}
#permalink:hover>* {
 display:block!important;
 width:auto!important;
 height:auto!important;
 margin:0px 4px!important;
 text-align:center!important;
}
#permalink a { color: #111!important; }
#permalink a:hover { color: #fff!important; }
/*-- -- */
.SimpleLayerSwitcher {
 position:absolute!important;
 top:-1px!important;
 right:-1px!important;
 width:auto!important;
 height:auto!important;
 padding:0px!important;
 min-width:100px!important;
}
.SimpleLayerSwitcher {
 background-color:rgba(100,100,100,0.5)!important;
 padding:0px!important;
 border:solid 1px transparent!important;
 border-radius:0px 0px 0px 15px!important;  
}
.SimpleLayerSwitcher a {
 background:transparent!important;
 border:solid 0px transparent!important;
 padding:3px 10px!important;
 text-align:center!important;
 color:#fff!important;
 text-shadow:0px 1px #777;
}
.SimpleLayerSwitcher a:hover {
 background:rgba(0,0,0,0.5)!important;
}
.SimpleLayerSwitcher a.basey,
.SimpleLayerSwitcher a.basey-on {
}
.olMap {
 background:rgba(100,140,150, 0.4)!important;
 box-shadow:0px 0px 9px 0px #141414!important;
 border:solid 1px #FFF!important;
 z-index:100!important;
 padding:0px!important;
}
#map {
 width:auto!important;
 height:auto!important;
 z-index:100!important;
 margin:10px!important;
 background:rgba(100,140,150, 0.4)!important;
 box-shadow:0px 0px 9px 0px #141414!important; 
}
#map img[src$="404.png"],
#map img[src*="/404"] {
 visibility:hidden!important;
}
/* Map controls */
div.olControlScaleLineTop,
div.olControlScaleLineBottom {
 color:#000!important;
 font-size:1.3em!important;
 font-family:Arial, Geneva, sans-serif!important;
 text-shadow:0px -1px 1px #fff!important;
}
div.olControlScaleLineTop{
 background:-moz-linear-gradient(bottom, #EEE, rgba(230,230,230,0))!important;
}
div.olControlScaleLineBottom{
 background:-moz-linear-gradient(top, #EEE, rgba(230, 230, 230,0))!important;
}
div[id$="zoomin"] img,
div[id$="zoomout"] img,
div[id*="pandown"] img,
div[id*="panup"] img,
div[id*="panright"] img,
div[id*="panleft"] img,
div[id="OpenLayers_Control_MaximizeDiv"] img,
div[id="OpenLayers_Control_MinimizeDiv"] img,
div[id*="OpenLayers.Control.PanZoomBar"] img,
div[id="OpenLayers.Control.PanZoom_6_zoomworld"] img 
{
 visibility:hidden!important;
}
div[id$="zoomin"],
div[id$="zoomout"],
div[id*="pandown"],
div[id*="panup"],
div[id*="panright"],
div[id*="panleft"],
div[id="OpenLayers_Control_MaximizeDiv"],
div[id="OpenLayers_Control_MinimizeDiv"],
div[id*="OpenLayers.Control.PanZoomBar"],
div[id="OpenLayers.Control.PanZoom_6_zoomworld"]
{
 font-size:15px!important;
 font-weight:bold!important;
 margin:0px!important;
 padding:0px!important;
 color:#fff!important;
 line-height:15px!important;
 text-align:center!important;
 vertical-align:middle!important;
 border:solid 1px #AAA!important;
 background-color:rgba(128,128,128,0.5)!important;
 box-shadow:inset 0px -1px 8px rgb(140,140,140)!important;
}
/* Slider */
div[id*="OpenLayers.Control.PanZoomBar_7_OpenLayers.Map_9"],
div[id="OpenLayers.Control.PanZoomBar_4_OpenLayers.Map_5"]{
 display:block!important;
 background:rgba(0, 168, 224, 0.8)!important; 
 background:-moz-linear-gradient(bottom, rgba(0, 168, 224, 0.5), rgba(141, 210, 239, 0.75))!important; 
 background:linear-gradient(bottom, rgba(0, 168, 224, 0.5), rgba(141, 210, 239, 0.75))!important; 
 box-shadow:0px 1px 3px #555!important;
 border-radius:3px!important;
 border:solid 1px rgba(0, 168, 224, 0.8)!important;
}
div[id*="OpenLayers.Control.PanZoomBar_7_OpenLayers.Map_9"]:hover,
div[id="OpenLayers.Control.PanZoomBar_4_OpenLayers.Map_5"]:hover {
 box-shadow:0px 2px 5px #444!important;
 background:rgba(0, 168, 224, 1)!important; 
 background:-moz-linear-gradient(bottom, rgba(0, 168, 224, 0.7), rgba(141, 210, 239, 0.9))!important;  
 border:solid 1px #3BCEFF!important; 
}
div[id*="OpenLayers_Control_PanZoomBar_ZoombarOpenLayers"] {
 background-image:none!important;
 background:rgba(128,128,128, 0.5)!important; 
 background:-moz-linear-gradient(top, rgba(128,128,128,0.5), rgba(90,90,90,0.5))!important;
