/*---------------------------------------------Prompt window---------------------------------------------*/
tabmodalprompt .mainContainer {
    background-color: rgba(179,230,255,.35)!important;
    background-image: -moz-linear-gradient(
            rgba(250,253,255,.9),
            rgba(250,253,255,.75) 20px,
            rgba(250,253,255,.70) 39px,
            rgba(250,253,255,0) 41px,
            rgba(250,253,255,0)) !important;
    margin: 5px!important;
    box-shadow: 0 1px 3px 2px rgba(0,0,0,.60)!important;
    border: 2px solid !important;
    -moz-border-left-colors: rgba(0,0,0,.7) rgba(255,255,255,.6) !important;
    -moz-border-bottom-colors: rgba(0,0,0,.7) rgba(255,255,255,.6) !important;
    -moz-border-right-colors: rgba(0,0,0,.7) rgba(255,255,255,.6) !important;
    -moz-border-top-colors: rgba(0,0,0,.7) rgba(255,255,255,.6) !important;
    border-radius: 5px 5px 5px 5px!important;}

tabmodalprompt .topContainer {
    background-color: #ffffff!important;
    text-shadow: 1px 1px rgba(255,255,255,.5);
    margin: 4px !important;
    border: 2px solid !important;
    border-radius: 2.5px 2.5px  0 0 !important;
    -moz-border-left-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
    -moz-border-right-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
    -moz-border-top-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
    border-bottom: none !important;}

tabmodalprompt .buttonContainer {
    background-color: #F1F5FB !important;
    background-image: -moz-linear-gradient(
            #CCD9EA 1px,
            #DAE1F0 2px,
            #E3EAF5 3px,
            #ECF0F8 4px,
            RGBa(255,255,255,0)) !important;
    background-clip: padding-box !important;
    margin: 4px !important;
    margin-top: -5px !important;
    border: 2px solid !important;
    border-radius: 0 0 2.5px 2.5px !important;
    -moz-border-left-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
    -moz-border-bottom-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
    -moz-border-right-colors: rgba(255,255,255,.6) rgba(0,0,0,.7) !important;
    border-top: none !important;
    padding: 5px !important;
    padding-bottom: 4px !important;}

