    /*-------------------------------------Vertical Toolbar--------------------------------------------*/

    #urlbar:not(:-moz-lwtheme),
    .searchbar-textbox:not(:-moz-lwtheme) {
        background-color: rgba(255,255,255,.725);
        border-color: rgba(0,0,0,.32) !important;
        color: black;
    }

    #urlbar:not(:-moz-lwtheme),
    .searchbar-textbox:not(:-moz-lwtheme) {
        background-color: rgba(255,255,255,.725);
        border-color: rgba(0,0,0,.32);
        color: black;
    }

    #urlbar:not(:-moz-lwtheme):hover,
    .searchbar-textbox:not(:-moz-lwtheme):hover {
        background-color: rgba(255,255,255,.898);
    }

    #urlbar:not(:-moz-lwtheme)[focused],
    .searchbar-textbox:not(:-moz-lwtheme)[focused] {
        background-color: white;
    }

    #vertical-toolbox {
        -moz-appearance: -moz-win-glass;
    }
    #vertical-toolbox:not([autohide]){
        -moz-appearance: none !important;
        border: none;
    }

    #vertical-toolbar[defaulttheme="Win"][flatbutton="true"] .toolbarbutton-1:not([checked="true"]) {
        box-shadow: 0 0 0 1px transparent inset, 0 0 0 2px transparent inset, 0 1px 0 transparent !important;}

    #vertical-toolbox:not([autohide]) #vertical-toolbar {
        -moz-binding: url("chrome://global/content/bindings/toolbar.xml#toolbar-drag") !important;}

    #vertical-toolbox:not([autohide]) #vertical-toolbar[customizing="true"] {
        -moz-binding: none !important;}

    #main-window[sizemode="normal"] #vertical-toolbox[autohide] {min-width: 3px !important;}

    #vertical-toolbox:not([autohide]) ~ #appcontent > #statusbar-display {
        margin-left: 29px !important;}

}
/*-----------------------------------------------Options windows-----------------------------------------------*/
#syncEnginesList {
    height: 150px !important;}

#BrowserPreferences {
    height: 600px !important;}

@-moz-document regexp("(?!^chrome://browser/content/browser.xul$).*") {
    prefwindow, prefpane, radiogroup, dialog, richlistbox, #tbxInsToolbox, #topBar, .windowDialog,
    #main-window, #CustomizeToolbarWindow, #SignonViewerDialog, #winInspectorMain, #DownThemAll {
        background: transparent none !important;
        -moz-appearance: -moz-win-glass !important;}
}

.paneSelector.chromeclass-toolbar {
    padding-left: 3px !important;
    padding-right: 2px !important}

caption {
    background: rgba(255,255,250,0.8) !important;
    border-radius: 5px !important;}

groupbox {
    -moz-appearance: none !important;border: none !important;
    background: rgba(255,255,250,0.8) !important; }

.paneDeckContainer, #mainDeck > vbox {
    background: rgba(255,255,250,0.8) !important;
    border-radius: 5px !important;}

.paneSelector.chromeclass-toolbar, #viewGroup {
    color: #fff !important;
    text-shadow: black 2px 2px 2px !important;}

.paneSelector.chromeclass-toolbar > radio[selected="true"], #viewGroup > radio[selected="true"] {
    color: #010726 !important;background: rgba(255,255,250,0.8) !important;
    text-shadow: none !important;
    border-top-left-radius:5px !important;
    border-top-right-radius:5px !important; }

.paneSelector.chromeclass-toolbar > radio:hover, #viewGroup > radio:hover {
    color: #010726 !important;background: rgba(255,255,250,0.5) !important;
    border-top-left-radius:5px !important;
    border-top-right-radius:5px !important; }

/*--------------------------------------Scratch Pad---------------------------------------------------------*/
@-moz-document url(chrome://browser/content/devtools/scratchpad.xul) {
    #main-window > * {
        -moz-appearance: -moz-win-exclude-glass !important;
    }
    
    #main-window {
        -moz-appearance: -moz-win-glass;
        background: transparent none;}
    
    #sp-menubar {
        -moz-box-pack: center !important;
        text-shadow: rgb(255, 255, 255) 0px 0px 6px, rgb(255, 255, 255) 0px 0px 6px, rgba(255, 255, 255, 0.4) 0px 1px 0px !important;
        background-color: rgba(255, 255, 255, 0.5) !important;
        border-radius: 4px !important;
        border-width: 0px !important;
        margin-left: 5px !important;
        margin-right: 5px !important;
        margin-bottom: 10px;}
}

/*------------------------------------------Page Info Window Only-----------------------------------------------*/
@-moz-document url(chrome://browser/content/pageinfo/pageInfo.xul) {
    #mainDeck {padding-top: 0px !important;}
}

/*-------------------------------------------Tabmix Pref window-------------------------------------------------*/
@-moz-document url(chrome://tabmixplus/content/pref/pref-tabmix.xul) {
    #TM_ButtonBox{
        padding-bottom: 0px !important;
        margin-bottom: 1px !important;
        padding-left: 5px !important;
        -moz-appearance: -moz-win-glass !important;
        background: none !important;
        opacity: 0.99 !important;}
    
    #tabpanId{
        background: rgba(255,255,250,0.8) !important;
        margin-top: -1px !important;
        padding-top: 5px !important;
        border-top-left-radius:5px !important;
        border-top-right-radius:5px !important; }
    
    #TM_ButtonBox button{
        color: #fff !important;
        text-shadow: black 2px 2px 2px !important;}
    
    #TM_ButtonBox button[checked="true"]{
        color: #010726 !important;
        background: rgba(255,255,250,0.8) !important;
        text-shadow: none !important;
        border-top-left-radius:5px !important;
        border-top-right-radius:5px !important; }
    
    #TM_ButtonBox button:hover{
        color: #010726 !important;
        background: rgba(255,255,250,0.5) !important;
        border-top-left-radius:5px !important;
        border-top-right-radius:5px !important; }
}
/*--------------------------------------general sub-windows---------------------------------------------------*/

/*-------------------------options sub windows-----------------*/
@-moz-document url(chrome://browser/content/preferences/permissions.xul){
    window[id="viewSource"] {background: none !important;}
}

/*--------------------------Library---------------------------*/
@-moz-document url(chrome://browser/content/places/places.xul){
    window {-moz-appearance: -moz-win-glass;}
    window, toolbar {background: none !important;}
    #placesView {-moz-appearance: -moz-win-exclude-glass !important;}    
    toolbarbutton {list-style-image: url("chrome://browser/skin/Toolbar-inverted.png") !important;}
}

/*-------------------------Stylish Editor--------------------*/
@-moz-document url(chrome://stylish/content/edit.xul){
    hbox {border: none !important;}
    #editor {-moz-appearance: -moz-win-exclude-glass !important;}
}
/*---------------------------------------------------------Dom Inspector---------------------------*/
@-moz-document url(chrome://inspector/content/inspector.xul) {
    #tbInspectorPrimary > hbox > toolbarbutton {
        margin: 0 1px !important;
        padding: 0 !important;
        height: 24px !important;}
    
    #bxDocPanel > toolbox > toolbar > label {
        padding-top: 4px !important;}
    
    toolbar {
        border: none !important;
    }
    
    toolbar {
        -moz-appearance: none !important;}
    
    #mbrInspectorMain {
        -moz-box-pack: center !important;
        text-shadow: rgb(255, 255, 255) 0px 0px 6px, rgb(255, 255, 255) 0px 0px 6px, rgba(255, 255, 255, 0.4) 0px 1px 0px !important;
        background-color: rgba(255, 255, 255, 0.5) !important;
        border-radius: 4px !important;
        border-width: 0px !important;
        margin-left: 5px !important;
        margin-right: 5px !important;
        -moz-appearance: none !important;}
    
    .viewer-pane-box-2, #bxInspectorMain {
        -moz-appearance: -moz-win-exclude-glass !important;
        border: 0px !important;}
}
/*---------------------------------Down them all------------------------------*/
/*--------------dta addurl-------------------*/
@-moz-document url(chrome://dta/content/dta/addurl.xul) {
    
    dialog > .box-inherit.dialog-content-box {
        border-radius: 5px !important;
        padding: 10px !important;
        background: rgba(255,255,250,0.8) !important;}
    
    #descr,#batches {
        margin: 0 6px 8px !important;
        padding: 0 0px !important;
        font-weight: normal !important;
        color: #575757 !important;}
    
    #batches {
        margin: 20px 32px 10px !important;
        -moz-appearance: none !important;
        border: 1px solid !important;
        border-radius: 3px !important;
        -moz-border-top-colors: rgba(50,50,50,0.5) !important;
        -moz-border-right-colors: rgba(50,50,50,0.3)  !important;
        -moz-border-bottom-colors:rgba(50,50,50,0.3)  !important;
        -moz-border-left-colors: rgba(50,50,50,0.5)  !important;
        padding: 6px !important;
        background-color: rgba(255,255,255,0.75) !important;}
    
    #batches > label:first-child {
        margin: -25px 0 0 -6px !important;
        padding: 0 !important;
        -moz-appearance: none !important;
        font-weight: normal !important;}
    
    #batches > label:first-child + label {
        margin-top: 10px !important;}
    
    #batches > label:first-child + label,#batches > label:first-child + label + label {
        color: #000 !important;
        font-family: "Segoe UI", Tahoma, Verdana, Helvetica, Arial, 'DejaVu Sans', sans-serif !important;}
}
/*--------------------dta manager---------------*/
@-moz-document url(chrome://dta/content/dta/manager.xul) {
    #mainstack {
        -moz-appearance: menuitem !important;
        -moz-appearance: none !important;
        background-color: transparent !important;
        background-image: none !important;
        border: 0px solid !important;
        margin: 0 !important;padding: 0 !important;
        font-family: "Segoe UI", Tahoma, Verdana, Helvetica, Arial, 'DejaVu Sans', sans-serif !important;}
    
    #downloads {
        border: 0px !important;
        -moz-appearance: -moz-win-exclude-glass !important;}
    
    #speedLimit {background: none !important;}
    
    .menuitem-iconic {
        -moz-appearance: menuitem !important;
        padding-right: 0px !important;}
    
    .menuitem-iconic:hover {color: black !important;}
    
    #listSpeeds {
        -moz-appearance: menupopup !important;
        width: 100px !important;}
    
    #downloads * {opacity:1 !important; }
    
    #tools {
        height: 32px !important;
        max-height: 32px !important;
        min-height: 32px !important;
        background-image: url("data:image/jpeg;base64,") !important;
        background-position: 0 -1px;background-repeat: repeat-x;
        border: 0px !important;
        -moz-appearance: -moz-win-exclude-glass !important;
        background-color: #fff !important;
        padding: 0 !important;margin: 0 !important;
        color: white !important;}
    
    #tools spacer, #tools spacer.visible {
        max-width: none !important;
        min-width: 0 !important;
        padding: 0 !important;}
    
    #tools spacer {
        margin: 0 !important;
        display: none !important;
        height: 27px !important;
        max-height: 27px !important;
        background-image: none !important;}
    
    #tools spacer.visible {
        -moz-appearance: none !important;
        list-style-image: none !important;
        -moz-border-left-colors: transparent transparent rgba(0,0,0,.45) !important;
        -moz-border-right-colors: transparent transparent rgba(255,255,255,.3) !important;
        border: 1px solid transparent !important;
        border-left-width: 3px !important;
        border-right-width: 3px !important;
        background: none !important;
        display: -moz-box !important;
        margin: 0 0 0 !important;
        height: 22px !important;
        border-radius: 0 !important;}
    
    #tools toolbarbutton {
        height: 25px !important;
        max-height: 25px !important;
        min-width: 0px !important;
        -moz-image-region: auto !important;
        -moz-appearance: none !important;
        -moz-box-orient: horizontal !important;
        margin: 0 !important;
        margin-top: 2px !important;
        padding: 0 !important;
        color: rgb(12,48,78) !important;
        border: 3px solid transparent !important;
        background: transparent none repeat-x 0 0px !important;}
    
    #tools toolbarbutton:not([disabled="true"]):hover {
        -moz-border-image: url("data:image/png;base64,") 3 / 3px stretch stretch !important;}
    
    #tools toolbarbutton:not([disabled="true"]):active:hover {
        -moz-border-image: url("data:image/png;base64,") 3 / 3px stretch stretch !important;}
    
    #tools toolbarbutton {
        list-style-image: url("data:image/png;base64,") !important;
    }
    
    #tools toolbarbutton[disabled="true"] .toolbarbutton-text {
        opacity: .7 !important;}
    
    #tools #tooladd {
        list-style-image: url("data:image/png;base64,") !important; }
    
    #tools #toolplay { -moz-image-region: rect(0px,80px,16px,64px) !important; }
    #tools #toolpause { -moz-image-region: rect(0px,96px,16px,80px) !important; }
    #tools #toolcancel { -moz-image-region: rect(0px,112px,16px,96px) !important; }
    #tools #toolmovetop { -moz-image-region: rect(64px,16px,80px,0px) !important; }
    #tools #toolmoveup { -moz-image-region: rect(64px,32px,80px,16px) !important; }
    #tools #toolmovedown { -moz-image-region: rect(64px,48px,80px,32px) !important; }
    #tools #toolmovebottom { -moz-image-region: rect(64px,64px,80px,48px) !important; }
    
    #tools #tooladd[disabled="true"] {
        list-style-image: url("data:image/png;base64,") !important; }
    
    #tools #toolplay[disabled="true"] { -moz-image-region: rect(16px,80px,32px,64px) !important; }
    #tools #toolpause[disabled="true"] { -moz-image-region: rect(16px,96px,32px,80px) !important; }
    #tools #toolcancel[disabled="true"] { -moz-image-region: rect(16px,112px,32px,96px) !important; }
    #tools #toolmovetop[disabled="true"] { -moz-image-region: rect(80px,16px,96px,0px) !important; }
    #tools #toolmoveup[disabled="true"] { -moz-image-region: rect(80px,32px,96px,16px) !important; }
    #tools #toolmovedown[disabled="true"] { -moz-image-region: rect(80px,48px,96px,32px) !important; }
    #tools #toolmovebottom[disabled="true"] { -moz-image-region: rect(80px,64px,96px,48px) !important; }
    
    #tools #tooldonate {display: none !important;}
    
    #footer, #footer > statusbarpanel {
        border: 0px solid !important;
        -moz-appearance: -moz-win-glass !important;
        text-shadow: white -1px -1px 5px;}
    
    .statusbarpanel-iconic-text.text-link {text-shadow: none !important;}
}
/*-------------dta select window-------------------*/
@-moz-document url(chrome://dta/content/dta/select.xul) {
    #footer {
        margin-bottom: 45px !important;
        margin-top: -60px !important;}
    
    #DownThemAll > hbox:nth-child(14) {
        margin-top: 30px !important;}
    
    .statusbar-resizerpanel {display: none !important;}
    
    #views, viewpics {background-color: transparent !important;}
    
    #DownThemAll > hbox:nth-child(9), .closer {
        -moz-appearance: none !important;
        background: rgba(255,255,250,0.8) !important;
        margin-top: 0px !important;
        margin-bottom: 0px !important;
        padding-top: 3px !important;
        padding-bottom: 3px !important;
        padding-left: 3px !important;}
    
    #regexcontainer {
        border-bottom-left-radius: 5px !important;
        border-bottom-right-radius: 5px !important;
        margin-bottom: 30px !important;}
    
    
    #notifications {
        padding-left: 7px !important;
        padding-right: 7px !important;}
    
    #views {
        padding-left: 4px !important;}
    
    
    #urlList {
        -moz-appearance: none !important;
        border: 5px solid rgba(255,255,250,0.8) !important;
        border-top-left-radius: 5px !important;
        border-top-right-radius: 5px !important;}
    
    toolbox, toolbarbutton {
        border: none !important;
        background: none !important;
        -moz-appearance: none !important;}
    
    .findfiles > toolbarbutton > * {
        color: #fff !important;
        text-shadow: black 2px 2px 2px !important;
        font-weight: normal !important;}
    
    .findfiles > toolbarbutton[selected="true"] {
        background: rgba(255,255,250,0.8) !important;
        border-radius: 5px !important;
        border-bottom-left-radius:0px !important;
        border-bottom-right-radius:0px !important; }
    
    .findfiles > toolbarbutton[selected="true"] > *, toolbarbutton:hover > * {color: #010726 !important;}
    
    .findfiles > toolbarbutton[selected="true"] > * {text-shadow: none !important;}
    
    .findfiles > toolbarbutton:hover {
        color: #010726 !important;
        background: rgba(255,255,250,0.5) !important;
        border-radius: 5px !important;
        border-bottom-left-radius:0px !important;
        border-bottom-right-radius:0px !important; }
    
    
    #footer, #footer > statusbarpanel {
        padding-left: 5px !important;
        padding-right: 5px !important;
        border: 0px solid !important;
        -moz-appearance: -moz-win-glass !important;
        text-shadow: white -1px -1px 5px;}
    
    #logo {display: none !important;}
    
    /*------------------popup-------------------*/
    #maskPopup {
        -moz-binding: url("chrome://browser/content/tabbrowser.xml#statuspanel") !important;
        background-image: -moz-linear-gradient(white, rgb(221, 221, 221));
        border: 1px;
        border-style: solid;
        border-color: rgb(204, 204, 204);
        color: rgb(51, 51, 51);
        border-radius: 3px;}
}

/*Non-xul styles*/
/*Image Viewing*/
body > img.decoded:only-child {
    background: none !important;
}
