css:
@-moz-document domain("unfuddle.com") {
/**
 * Unfuddle Unfucker: Unfuddle's quick n' dirty restyle.
 *
 * @author
 * Sepehr Lajevardi <me@sepehr.ws>
 */

/* New design styles start: */
tr.ticket_row td:nth-child(3) a {
  font-weight:bold !important;
}
tr.ticket_row td:last-child {
  text-align: center !important;
}
tr.ticket_row td:last-child {
  font-weight: bold !important;
}
.panel-right {
  box-shadow: -2px 0px 3px #ccc;
}
.theme-classic #tickets_report_table td {
  padding: 8px 5px !important;
  font-size: 13px !important;
  line-height: 20px !important;
}
::-webkit-scrollbar {
  width: 0px !important;
  height: 10px !important;
}
select {
  border-radius: 0 !important;
  -webkit-border-radius: 0 !important;
  padding: 0 !important;
}
li.ng-scope {
  font-family: Aller, Consolas, monospace !important;
  font-style: italic !important;
  font-size: 13px !important;
  color: #444;
}
.new_features,
.theme-main .ticket-show .status,
.theme-main .ticket-show .ticket-summary-meta {
  font-family: Aller, Consolas, monospace !important;
  font-style: italic !important;
  font-size: 11px !important;
}
tr[ng-repeat^="changeset in changesets"] td.span12 span {
  font-family: Aller, Consolas, Monaco, monospace !important;
  font-style: italic !important;
  font-size: 11px !important;
  letter-spacing: 0px !important;
  white-space: pre !important;
}
#notice {
	padding: 15px !important;
}
#repository_commit_info div.message, 
#repository_browser .browser_message a {
  font-weight: normal !important;
}
.theme-main .ticket-show .ticket-header {
  box-shadow: inset 0px -2px 4px rgba(0, 0, 0, 0.1) !important;
}
/* Ticker Header */
.theme-main .ticket-show .fields .summary, 
.theme-main .ticket-show .fields .summary textarea {
  font-size: 24px !important;
}
.theme-main .ticket-show .fields .ticket-actions {
  margin-right: 5px !important;
}
/* New design styles end. */

#send_feedback_action,
#calendar_sidebar ~ hr,
div[u-toggle-callback='onToggleSection("history", isOpen)'] + .bg-success {
  display: none;
}

body {
  color: #232323;
  font-size: 13px;
  font-family: Helvetica Neue, Helvetica, Arial, "Lucida Grande" !important;
}

h1,
#messages_list {
  padding-bottom: 3px;
  border-color: #ddd !important;
}

body h1, body h2, body h3, .title {
  font-family: Helvetica Neue, Helvetica, Arial, Driod Sans, "Lucida Grande", sans-serif !important;
}

.page_form input.title,
.object_edit input.title,
#report_options_dialog .title {
  font-family: "Lucida Grande";
}

/* Links */
a, a:link, a:visited {
  color: #15C;
  text-decoration: none !important;
}

a:hover, a:active {
  color: #DD4B39 ;
}


/* High Blue*/
#WHAT_DAFAQ {
  color: #15C !important;
}

/* High Red! */
.message h2.title a,
#sidebar li.selected a {
  color: #DD4B39 !important;
}

#account_header > a {
  padding-top: 3px;
  font-size: 14px !important;
  font-family: Droid Sans, "Lucida Grande" !important;
}

#header h1 {
  font-family: "Lucida Grande" !important;
  text-shadow: 3px 2px #111 !important;
}

#wrapper,
#sidebar {
  background: #fff !important;
}

.theme-classic #tickets_report_table a.ticket_link, 
.theme-classic #time_tracking_report_table.pretty tr.hover_effect a {
  font-family: Helvetica Neue, Helvetica, Arial, sans-serif !important;
  font-size: 13px !important;
}

.activity_info pre {
  font-size: 11px !important;
  font-family: Helvetica Neue, Helvetica, Arial !important;
}

.activity_info .meta {
  font-size: 11px !important;
  font-family: Verdana !important;
}

.activity_info p {
  font-style: normal !important;
  font-size: 9px !important;
  font-family: tahoma !important;
  text-transform: uppercase !important;
  float: right;
}

.meta {
  font-size: 13px;
  margin-top: 2px;
}

.meta span {
  padding: 0 2px 1px 0;
  border-bottom: 1px solid #ddd;
}

.activity_info pre {
  font-size: 13px !important;
  font-style: normal !important;
  line-height: 19px;
  color: #555 !important;
  text-shadow: 0 1px #fff !important;
}

.row_0 {
  background-color: #f3f3f3 !important;
}

table.calendar_mini td.today {
  background-color: #DD4B39;
  border: none;
  color: #fff !important;
}

#notice {
  font-size: 13px;
  background: #EFE !important;
  color: #363;
}

#notice .info_wrapper, 
#error_explanation .info_wrapper,
#administrative_message .info_wrapper {
  background: none !important;
}

#error_explanation {
  font-size: 13px;
  background: #FCC !important;
}

a#project_delete {
  color: #fff;
  background: #DD4B39;
  padding: 5px 8px;
  border-radius: 5px;
}

/* Reports */
.report_container {
  background: #f1f1f1 !important;
}

.report_heading,
table.pretty th,
table.pretty th a,
#repository_browser th {
  color: #777 !important;
  background: #e7e7e7 !important;
  border-bottom: 1px solid #ddd !important;
}

table.pretty th,
.report_menu_left a,
.report_menu_right a,
#repository_browser th,
#report_options_action a,
#report_refresh_action a {
  color: #aaa;
  font-size: 12px;
  text-transform: uppercase;
  font-weight: bold;
}
table.pretty th:hover,
.report_menu_left a:hover,
.report_menu_right a:hover,
#repository_browser th:hover,
#report_options_action a:hover,
#report_refresh_action a:hover {
  color: #777;
}

#tickets_report_table .report_table th {
  background: #ddd;
  border-color: #ccc;
}

table.pretty th a {
  color: #777;
  font-size: 12px;
  text-transform: uppercase;
}

#tickets_report_table td {
  height: 30px;
  padding: 5px 4px;
}

table.pretty td {
  font-size: 13px;
}

#tickets_update_dialog, 
#report_options_dialog {
  font-size: 13px;
  background: #fafafa;
}

.ticket .ticket_created_at {
  font-size: 10px;
  font-family: DejaVu Sans, Verdana;
  text-transform: uppercase;
  color: #777;
}

#statements_here .invisible {
  background-color: #f7f7f7 !important;
}
 
#notebook_show .menu,
#report_render > .no_print {
  margin-top: 40px !important;
}

#report_render > .no_print,
#report_render > .menu a {
  opacity: 0.4 !important;
}
#report_render > .no_print:hover ~ .menu a,
#report_render > .menu:hover a {
  opacity: 1 !important;
}

#report_render .report_table a {
  border: none !important;
}

#show_comments.comments h3 {
  color: #777;
  font-size: 13px;
}

.milestone .actions a,
#show_comments.comments h3 a {
  font-family: DejaVu Sans, Verdana !important;
  font-size: 8px;
  text-transform: uppercase;
}

#show_comments.comments .comment {
  font-size: 13px;
}

/* Ticket */
.ticket_cycle {
  font-weight: bold;
  font-size: 18px;
  font-family: Arial, Lucida Grande, sans-serif;
}

.ticket_cycle a {
  color: #000;
}

#ticket_show_full {
  margin-top: 40px;
}

.ticket .number {
  color: #bbb !important;
  font-size: 28px !important;
}

.make_comment,
.ticket {
  background: #f5f5f5 !important;
  border-color: #ddd !important;
  text-shadow: 1px 1px #fff !important;
}

.ticket.priority_high,
.ticket table.invisible td,
.ticket.priority_highest td {
  border-color: #ddd !important;
}

.ticket table.invisible td.label,
.ticket.priority_high table.invisible td.label,
.ticket.priority_highest table.invisible td.label {
  color: #888;
  font-size: 12px;
  text-transform: uppercase;
}

.ticket.priority_high .timeline_container, 
.ticket.priority_high #ticket_associations,
.ticket.priority_highest .timeline_container, 
.ticket.priority_highest #ticket_associations,
.ticket.priority_normal .timeline_container, 
.ticket.priority_normal #ticket_associations {
  background: #efefef;
  border-color: #ddd !important;
}

.ticket .timeline_container,
.ticket #ticket_associations,
.ticket #ticket_attachments {
  border-color: #ddd;
}

.attachment_summary > span {
  display: block !important;
  margin-bottom: 5px !important;
}

#ticket_associations table.pretty tr.assoc_related td.assoc, 
#ticket_associations table.pretty tr.assoc_related td.assoc a,
#ticket_associations table.pretty tr.assoc_parent td.assoc, #ticket_associations table.pretty tr.assoc_parent td.assoc a {
  background-color: #d74a38 !important;
  text-shadow: 1px 1px #333 !important;
}

#ticket_associations table.pretty tr.assoc_child td.assoc, #ticket_associations table.pretty tr.assoc_child td.assoc a,
#ticket_associations table.pretty tr.assoc_sibling td.assoc, #ticket_associations table.pretty tr.assoc_sibling td.assoc a {
  text-shadow: none !important;
}

.ticket #show_ticket_description, 
.ticket #show_ticket_resolution {
  margin: 10px 0 20px 0;
  border-left: 4px solid #ccc;
  padding-left: 10px;
}

#ticket_show_full > h2,
#ticket_show_full .audit li {
  opacity: 0.2;
  cursor: default;
}
#ticket_show_full > h2:hover,
#ticket_show_full .audit li:hover {
  opacity: 1;
}

/* Notebooks, Repos */
#notebook_list .notebook,
#repositories_list .repository {
  text-shadow: 0 1px #fff !important;
}

#notebook_list .row_1,
#repositories_list .row_1 {
  border-color: #ddd;
  background-color: #f1f1f1;
}

#notebook_list .notebook .meta,
#repositories_list .repository .meta p {
  font-size: 13px;
  color: #888;
}

.report_heading .menu a,
#notebook_list .notebook .actions a {
  color: #aaa;
  text-shadow: 0 1px #fff !important;
  font-size: 12px;
  text-transform: uppercase;
  font-weight: bold;
  margin: 0 10px;
}
#notebook_list .notebook .actions a:hover {
  color: #888;
}

#show_notebook_render .notebook_page_table td {
  font-size: 13px;
}

table.pretty td {
  padding: 5px 3px;
}

/* Misc. Page Elements */
.new_object {
  background-color: #f5f5f5;
}

.page .meta {
  font-size: 9px;
  font-family: DejaVu Sans, Verdana;
  padding-bottom: 8px;
  text-transform: uppercase;
}

.page h2.title {
  text-shadow: 0 1px #fff !important;
}

#show_page_render .body,
#show_page_render .markdown_formatter {
  padding: 0 19px !important;
}

#show_page_render, 
#show_page_edit_preview_render, 
#show_page_edit, #show_page_history, 
#show_page_compare, .page_compare_legend {
  margin: 10px;
}

#help_content p {
  font-size: 13px;
}

.notice {
  font-size: 13px;
}

.notice div.code_wrapper, 
#help_content table.pretty div.code_wrapper {
  background: #DD4B39;
  border: none;
  margin: 5px 0;
}

.documentation_wrapper .notice {
  background: #f5f5f5;
  border-color: #ddd;
  text-shadow: 0 1px #fff !important;
}

#repository_browser *,
#repository_commit_info *,
#repository_current_commit *,
.documentation_wrapper pre code,
.code_pretty_print *,
.code_fixer *,
.attachment_actions > a {
  font-size: 13px !important;
  font-style: italic !important;
  font-family: Consolas, "Bitstream Vera Sans Mono" !important;
}

#repository_commit_info div.message,
#repository_browser .browser_message a {
  font-weight: bold !important;
  color: #aaa !important;
}

#repository_commit_info div.message,
#repository_browser .browser_message a:hover {
    color: #444 !important;
}

/* People and Settings */
#people_list .administrator, 
#people_list.people_list_account .administrator {
  background: #f5f5f5;
  border-color: #ddd;
}

.admin_label,
#people_list .person .last_signed_in, #people_list.people_list_account .person .last_signed_in {
  font-size: 9px;
  text-transform: uppercase;
  font-family: DejaVu Sans, Verdana !important;
  text-shadow: 0 1px #fff !important;
}

#people_list a[href^="mailto:"] {
  font-size: 12px;
}

#people_list .person .actions a {
  color: #aaa;
  text-shadow: 0 1px #fff !important;
  font-size: 11px;
  text-transform: uppercase;
  font-weight: bold;
}

#people_list .person .actions a:hover {
  color: #888;
}

#project_list.panel, .project_available_people .panel, .project_current_people .panel, .person_available_projects .panel, .person_current_projects .panel, .project_list_column .panel,
.page_form .panel {
  background: #f5f5f5;
}

.progress_bar_fill_account {
  background: #DD4B39 !important;
}

/* Milestones */
#all_milestones .milestone {
  background: #f5f5f5;
  border: #ddd;
  text-shadow: 0 1px #fff !important;
  margin: 5px 0;
}

#milestones_upcoming .milestone .meta {
  color: #444;
}

.progress > div table {
  margin-top: 5px;
}


/* NEW STYLES */
.history_item .meta strong {
  padding-bottom: 1px !important;
  border-bottom: 1px solid #ddd !important;
}

.theme-classic .report_container,
.theme-classic .report_heading {
	background: #fff !important;
}

.theme-classic .report_heading {
	border-bottom: none !important;
}

tr.report_header h2 {
	font-weight: bold;
}

/* No copyright in ticket footer */
.theme-main .ticket-show .footer {
  display: none;
}

/* Toggl button */
.toggl-button.unfuddle {
	float: right;
    margin-right: 15px;
    position: relative;
    top: 6px;
    color: #333;
    font-size: 13px;
}

}
