css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(youtube.com)
{

/* HD Suite Bar */
#hdsuite_dl_div, #hdsuite_logo, #hdsuite_switch, #hdsuite_update_button {
height:0px;
visibility:hidden !important;
}

/*Signed out page */
.lohp-newspaper-shelf{border-bottom:1px solid rgb(100,100,100) !important;}
.lohp-large-shelf-container{border-right:1px solid rgb(100,100,100) !important;}
.lohp-shelf-cell-container{border-color: -moz-use-text-color rgb(100,100,100) !important}
.branded-page-v2-col-container{border-left:none !important}
.branded-page-v2-container{border:none !important}
.lohp-blog-headline{border-color: -moz-use-text-color rgb(100,100,100) !important}

/* Dialog */
#dialog, .modal-dialog-title, #describe-panel {background:#1a1a1a !important}
#describe-description {background:#888888 !important}
.modal-dialog-title-text {color:#7a7a7a !important;}

/* Buttons */
.end.yt-uix-button  {height:27px !important}
.end.comment-action-vote-down {height:21px !important}
.start.end 
{
background:#555555 !important;
border:none !important; 
height:14px !important;
}
a.start.end {color:#888888 !important;}
/* Description text */
p {color:white;}

/* Ticker */
.ticker-content a {color:#eeeeee !important;}

/* Show More/Less Button */
.yt-uix-button, .yt-uix-button-default, .yt-uix-button-hh-default, .yt-uix-button-active 
{
background:#555555 !important;
text-shadow:none !important;
border:0px !important;
}
span.yt-uix-button-content {color:#888888 !important;}
.start.comment-action {padding-left:9px !important}

/* Video Comment */
.yt-alert-promo {background: -moz-linear-gradient(center top , rgb(140, 140, 140) 0px, rgb(155, 155, 155) 45px) repeat scroll 0% 0% rgb(155, 155, 155) !important;}
textarea.yt-uix-form-textarea.comments-textarea {color:white !important;}
.vdesc, ul.report-video-categories {color:white !important;}
span.yt-admin-h3 {color:#eeeeee !important;}
.body {color:#eeeeee !important;}
#watch7-content, #watch7-user-header {background:black !important;}
#watch7-headline 
{
background:-moz-linear-gradient(top, black 19%, #1a1a1a 20%, #212121 89%, #020202 90%) !important;
padding-bottom:15px !important;
}
#watch7-action-panel-footer {background:black !important}
#watch7-action-buttons, #watch7-discussion, #watch7-action-panels {border:none !important;}
.yt-uix-button-panel {background:black !important}
.comment-text {padding:5px 0px 8px 0px !important}
.comments-rating-positive, .comments-rating-negative {padding-right: 4px !important} 

/* View Count */
span.viewcount {color:maroon !important;}
span.watch-view-count {color:#888888 !important;}
span.view-count {color:maroon !important;}
span.stat.view-count {color:maroon !important;}
.feed-item-time {color:maroon !important;}

/* Post Date */
p.episode-air-date {color:maroon !important;}

/* Username/Content Provider */
span.stat {color:white !important;}
p.show-title {color:white !important;}
div.feed-item-actions-line {color:white !important;}
#masthead-user-button {
box-shadow:none !important;
background: -moz-linear-gradient(top, #4d4e4d 0%, #2e2e2e 100%) !important;
}
#masthead-user-image { box-shadow:none !important;}

/* Main video titles */
a.title.yt-uix-sessionlink {color:#888888 !important;}
span.title {color:#888888 !important;}
#eow-title {color:#888888 !important;}
h4.episode-title, span.yt-uix-expander-head {color:#888888 !important;}

/* Search Window */
h2 {color:#888888 !important;}
a.yt-uix-tile-link {color:#888888 !important;}
a.yt-uix-tile-link:hover {color:#808080 !important;}
.yt-tile-default p {color:#e3e3e3 !important;}
.yt-tile-default b {color:#818181 !important;}
.yt-tile-default:hover {background:#222222 !important;}

/* Main video container */
.feed-item-container {border-bottom:1px solid rgba(100,100,100,0.4) !important;}
.feed-item-container:hover {background: -moz-linear-gradient(top, #202020 0%, #030303 100%) !important;}
ul.context-data-container 
{
background:black !important;
border:none !important;
}
li.feed-item-container {border-left:none !important} 
.branded-page-v2-primary-col, .branded-page-v2-body, .branded-page-v2-detached-top {border:none !important}

/* Top container properties */
#masthead-search-terms input 
{
-moz-box-shadow:    inset 3px 0 7px #050505;
-webkit-box-shadow: inset 3px 0 7px #050505;
box-shadow:         inset 3px 0 7px #050505;
background:#999999 !important;
}

#masthead-search-terms.masthead-search-terms-border {border:none !important;}
#yt-masthead-container 
{
background:-moz-linear-gradient(top, #2d2e2d 0%, #1e1e1e 100%) !important;
border:none !important;
}
.masthead-user-username {color:#898989 !important;}
a.end, a.start {color:#898989 !important;}
#masthead-nav a {color:red !important;}
#masthead-user-wrapper, #masthead-user-button {border:none !important}

/* User Expanded */
li.masthead-expanded-menu-item a {color:red !important;}
li.playlist-bar-item.yt-uix-slider-slide-unit.yt-tile-visible { background:#333333 !important;}
span.playlist-data-item.list-title.yt-uix-slider-title {color:#999999 !important;}
span.list-video-facets {color:white !important;}

/* subcription/recommended box */
.context-title{
color:#999999 !important;
text-shadow:none !important;
}
.context-head {border:none !important;}
#watch-context {border:none !important;}
.context-link {background: -moz-linear-gradient(top, #3d3e3d 0%, #1e1e1e 100%) !important;}
#watch-context-body.context-body {background:#111111 !important;}
a.related-video:hover {background:#333333 !important;}
.watch-sidebar-head {color:#999999 !important;}

/* Side Videos */
.secondary-pane p {color:#555 !important;}
a.video-list-item-link.yt-uix-sessionlink:hover {background:#333 !important;}
.yt-horizontal-rule 
{
color:#333 !important;
opacity:0.4 !important;
}

/* Comments box */
.yt-uix-expander-head {border:none;}
li.comment.yt-tile-default.child:hover {background:#333333 !important;}
li.comment.yt-tile-default.has-child:hover {background:#333333 !important;}

/* footer */
#footer-hh-container {background:black !important}
#footer ul {text-shadow:none !important;}
#footer-links-primary a{color:#888888 !important;}
#footer-links-secondary a{color:#888888 !important;}

/*personal messages*/
#folderlinks a
{
color:#888888 !important;
text-shadow:none !important;
}
tbody h3,a
{
color:#888888 !important;
text-shadow:none !important;
}

/* Guide bar (new left hand thing)*/
#guide, #guide-container {border:none !important}
hr.guide-section-separator {border-bottom: 1px solid rgb(100, 100, 100) !important;} 

/* Playlist bar */
#playlist-bar-bar {background:#333333 !important;}
.yt-uix-tooltip {text-shadow:none !important;}

/* Contained Links*/
a.yt-uix-redirect-link {color:white !important;}
a.yt-uix-redirect-link:hover 
{
color:#999 !important;
text-decoration:none !important;
}

/* Background */
body, #content, .primary-col, li.feed-item-container, .branded-page-v2-col-container, .branded-page-v2-secondary-col, #watch-headline-container, #watch-video-container, #watch-main-container, .footer .yt-uix-pager, .splitter, #mymedia #view-pane .settings, #mymedia #view-pane .actions, #mymedia #table .video.even, #mymedia #table .video-details.even, #mymedia #table .video.odd, #mymedia #table .video-details.odd, #mymedia #view-pane .footer, #mymedia #table, #no-playlists, .searchFooterBox .yt-uix-pager, input:not([class*="html5"]), .feedmodule-anchor, textarea, #record-tab-content, .vert-bar, .message td, .msg-subject-open .subject, .shows-table, .shows-table .alt td, #google_companion_ad_div, #watch-show-brand-div, #watch-channel-brand-div, #user_playlist_navigator, #main-channel-content, .watch-next-list-actions, .ux-thumb-96, .multiple-promoted, #quicklist-bar-container, #vm-pageheader-container h1, #vm-page-subheader, #vm-video-actions-inner, #yt-admin h1, #folder_title, #theForm *, #footer-container, #vm-playlist-header, #page.watch-branded #watch-sidebar, #yt-admin-content h2, #mymedia #table, #yt-admin #table, #page, #watch-description .expand, #watch-description .collapse, #yt-admin.recommendations, #watch-owner-container, #annotator-div, #annotator-select-menu .yt-uix-button-menu-item td, .browse-bg-gradient, #page.search-movies-browse #content-container , #channel-body
{
    /*background-image:url('http://denederlandseclub.com/img/paper-background.jpg')  !important;
    background-repeat:no-repeat; */
    background:black !important;
}
}
