css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dobrochan.ru"),domain("dobrochan.org"),domain("dobrochan.com") {
:root {font-size: 14px !important; font-family: sans-serif !important;}
body {margin: 0 !important; padding: 8px !important; background: none transparent !important;}
a {color: #420071 !important; text-decoration: none !important;}
a:hover {text-decoration: underline !important;}
.checked~.postername {text-shadow: 0 0 3px  red !important; color: white !important; text-decoration: line-through !important;}
.hide.icon:before {content: "[hide]" !important;}
.hide.icon {float: right !important;}

/* 1024x1024 */
html {background: #ddd url("https://lh5.googleusercontent.com/-E3bbm-Hg68o/UcikQ4LxVCI/AAAAAAAAAR0/axNHvT7uKwU/I/back3.png") repeat top left !important;}
/*  640x400  *//*
html {background: #ddd url("https://lh6.googleusercontent.com/-gtlEBMmSTfY/UcikP6LCiFI/AAAAAAAAARs/pZAnaqLGO_g/I/back1.png") repeat top left !important;}
*//* alt *//*
html {background: #ddd url("https://lh6.googleusercontent.com/-EPfgv3cW7dE/Ucim-LTVT1I/AAAAAAAAASk/OkystbOt5PQ/I/back3k.png") repeat top left !important;}
*/

.logodobleft,.logodobright,[style*="green.png"],#jquery_jplayer {display: none !important;}
.logo {text-align: center !important; color: #600000 !important; font-weight: normal !important;}
.adminbar,.stylebar,.hideinfo {font-size: inherit !important; font-weight: normal !important;}
.stylebar,.banner, .logo a+br {display: none !important;}

body[onload*="update"] {padding: 154px 0 0 !important;}
body[onload*="update"] a {text-decoration: none !important; color: #603344 !important; background: none transparent !important; font-size: 12px !important;}
body[onload*="update"] a:hover {text-decoration: underline !important;}
body[onload*="update"] .category {font-size: inherit !important; border: thin none #dca !important; background: rgba(255,255,255,0.5) !important; margin: 0 !important; padding: 3px 4px !important; border-radius: 0 !important;}
body[onload*="update"] .category:first-of-type {border-style: solid none none !important;}
body[onload*="update"] .category:last-of-type {border-style: none none solid !important; margin-bottom: 7px !important;}
body[onload*="update"] .logo {position: absolute !important; top: 0 !important; left: 0 !important; margin: 0 0 0 -5px !important;;}

.header {padding: 0 3px !important; color: #600000 !important; font-weight: bold !important; background: none transparent !important;}
.list,.header {border: 0 none !important;}

pre {white-space: pre-wrap !important;} code:after {white-space: pre-wrap !important; content: "\A";}

.featuredimgtop,.featuredimgbottom,.newsbg {background: none transparent !important;}
.featuredimgbottom {height: 250px !important;}
.newsbg {width: auto !important; margin: 10px 50px !important; }
.newstop {padding: 5px !important; background: none rgba(255,255,255,0.5) !important; border-radius: 5px !important; box-shadow: 0 0 5px white !important;}
.newsbottom {background: none transparent !important;}
.newssize {padding: 0 !important; font-size: inherit !important;}
/*
.thread:only-of-type {counter-reset: list 1;}
.thread:only-of-type>table .reflink+.cpanel:before {counter-increment: list; content: counter(list); float: right !important;
font: normal bold 11px/16px sans !important; color: rgba(0,0,0,0.4) !important; margin: 2px 2px -2px 0 !important;}*/

.threadlist {border: 0 none !important;}
.threadlist .logo,.threadlist .logo a {font-size: inherit !important;}
.doubledash,.theader {display: none !important;}
.reply,.highlight,.popup,.document {color: rgb(96,0,0) !important;}
.reply,.highlight,.popup,.document {background: none rgba(255,255,255,0.5) !important; border: thin solid #dca !important; padding: 0 !important; box-shadow: none !important;  -webkit-box-shadow: none !important;}
.highlight {border-color: #666 !important;}
.popup,.oppost.popup,.de-pview {background-color: #ececec !important;}

.reply .postbody,.popup .postbody {border: thin solid #dca !important; border-style: solid none none !important;}
.postbody {line-height: normal !important;}
.reply label {margin: 0 0 0 10px !important;}
.cpanel {margin-right: 10px !important;}

.fileinfo,.fileinfo a {font-size: smaller !important; color: #666 !important; font-weight: normal !important;}
.fileinfo {margin: 0 10px 2px !important;}
.fileinfo.limited {margin: 0 0 2px !important;}
.oppost .fileinfo {color: black !important;}
.fileinfo br {display: none !important;}
.fileinfo.limited br:last-of-type {display: block !important;}
.fileinfo a:first-child:not(:only-of-type) {display: none !important;}
.fileinfo a.edit_:before {content: "[edit]" !important;}
.fileinfo a.search_google:before {content: "[google]" !important;}
.fileinfo a.search_iqdb:before {content: "[iqdb]" !important;}
.file {margin: 8px 10px 7px !important;}

hr {display: none !important;}

.oppost {color: black !important;}

.spoiler,.spoiler blockquote {display: inline !important; line-height: normal !important;}
div.spoiler {line-height: 16px !important;}
.spoiler blockquote:after,.spoiler blockquote:before,div.spoiler:after,div.spoiler:before {content: "\A"; white-space: pre-wrap;}
.spoiler {background: none rgba(150,150,150,0.2) !important; color: transparent !important;}
.spoiler blockquote,.spoiler a,.spoiler blockquote a {background: transparent !important; color: inherit !important;}
.spoiler:hover {background: none rgba(150,150,150,0.2) !important; color: black !important;}
.spoiler:hover blockquote {color: #666 !important;}
.spoiler:hover a,.spoiler:hover blockquote a {color: inherit !important;}

.oppost,.post {background: 0 none !important; margin: 0 !important;}
.reply_:before {content: ">>" !important;}
.icon:not([class*="signed"]) img {display: none !important;}
.icon[class*="signed"] {height: 14px !important; display: inline-block !important;}
.icon:not([class*="signed"]) {background: none transparent !important;}
.icon {font-size: smaller !important; color: black !important; font-weight: normal !important;}

blockquote,.abbrev,.postertrip {color: #666 !important; padding: 0 !important;}

.postername {color: #004A99 !important;}
.replytitle {font-size: 16px !important; color: black !important;}
.reflink {font-weight: bold !important; font-size: smaller !important;}
.reflink a,label {color: black !important;}
.reflink a {font-family: tahoma,verdana,arial,sans-serif !important;}
select input,.postnode {border: 0 none !important;}

.pages {border: 0 none !important;}
.postblock {font-size: 12px !important; font-weight: bold !important; border: 0 none !important; background: none transparent !important; color: #600000 !important;}
.thumb {border: 0 none !important;}

#replyform {display: inline-block !important;}
#replyform input,#replyform textarea,#replyform select,#replyform button {background: rgba(255,255,255,0.5) !important;}

.rules,.rules a {font-size: smaller !important;}

input,textarea,select,button {-moz-appearance: none !important; color: black !important; font-size: 12px !important; border: thin solid #ccc !important; background: rgba(255,255,255,0.25) !important;}
select *,.postnode {border: 0 none !important;}
input[type="checkbox"] {border: thin solid #ccc !important; background-color: #ececec !important;}
input[type="file"] input[type="button"] {margin: 0 5px !important;}
input[type="button"] {margin: 1px !important;}
#txt_btns input {margin: 2px 1px 0 !important;}

#de-qarea {display: inline-block !important; width: auto !important;}
.de-menu a {background: transparent !important;}
.de-postpanel a {margin: 2px 4px -2px 0 !important; position: relative !important;}
#de-cfg-head {display: none !important;}
#de-cfg-btns,.de-cfg-body,#de-cfg-bar,#de-cfg-bar .reply {border: 0 none !important; background: none transparent !important;}
.de-cfg-tab {border-radius: 0 !important;}
#de-panel {background: rgba(0, 0, 0, 0.25) !important;}
.de-abtn:hover {border-color: #ccc !important;}
.de-btn-src + a {display: none !important;}

#de-txt-panel {height: auto !important;}
#de-txt-panel span {padding: 0 !important; background: none rgba(255,255,255,0.25) !important; vertical-align: middle !important; line-height: 16px !important; width: 16px !important; height: 16px !important; font-size: 12px !important; margin-right: 0.1em !important; display: inline-block !important; text-align: center !important; border: thin solid #ccc !important; font-weight: bold !important; border-radius: 4px !important;}
#de-btn-bold:before {content: "B" !important;}
#de-btn-italic:before {content: "i" !important; font-style: italic !important;}
#de-btn-under:before {content: "U" !important; text-decoration: underline !important;}
#de-btn-strike:before {content: "S" !important;}
#de-btn-strike:after {content: "" !important; background: black !important; width: 12px !important; height: 2px !important; display: block !important; margin: -9px 2px 0 !important;}
#de-btn-spoil:after {content: "" !important; background: black !important; width: 10px !important; height: 14px !important; display: block !important; margin: 1px 3px 0 !important;}
#de-btn-code:before {content: "c" !important; font-size: 16px !important; font-family: monospace !important;}
#de-btn-quote:before {content: ">" !important; font-weight: normal !important;}
#de-btn-quote:hover {color: #539 !important;}
}
