css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:home") {

html, #launcher, .spacer, #searchContainer, #snippetContainer, #topSection {background:#222222 url("http://i25.photobucket.com/albums/c70/MDLeers/pic_zps23b1877c.jpg") fixed no-repeat right top!important; background-size:100% 100%!important;
border:transparent!important;
box-shadow:none!important;
border-top: none !important;
}

a[href] {text-decoration:underline!important;}

#brandLogo {display:none!important;}

span[id*="Snippet"], #snippets p, span[id*="Snippet"] a:not(:hover), .launchButton:not(:hover), #searchText:focus #searchSubmit, #searchSubmit, #snippets > div > div:nth-child(3) > div > p > a {
color: white !important;
border:none!important;
box-shadow:none!important;
text-shadow: -2px 0 2px black, 0 2px 2px black, 2px 0 2px black, 0 -2px 2px black!important;
}

span[id*="Snippet"] a:hover, #snippets a:hover, .launchButton:hover, #searchSubmit a:hover {color:black; text-shadow: -2px 0 2px white, 0 2px 2px white, 2px 0 2px white, 0 -2px 2px white!important; opacity:.7!important; -moz-transition-property:text-shadow, opacity, color!important; -moz-transition-duration:500ms!important; -moz-transition-timing-function:ease-in-out!important; border:none!important;}

.launchButton:hover, #searchSubmit:hover {background-image: -moz-linear-gradient(#ffffff 50%, rgba(0,0,0,.1))!important;
box-shadow: inset 3px 3px 9px 3px #dcdcdc!important; opacity:1!important; -moz-transition-property:background-image, opacity, box-shadow!important; -moz-transition-duration:500ms!important; -moz-transition-timing-function:linear!important;}

.launchButton {/* position:relative!important; bottom:535px!important; */ /* background:#dcdcdc!important; */ opacity:1!important; border-radius:50px!important;}

.launchButton {position:relative!important; bottom:480px!important;}

#searchContainer {position:relative!important; bottom:-160px!important;}

#snippetContainer {position:relative!important; bottom:-180px!important;}

#flame-wrap {position:relative!important; top:80px!important;}

#searchText, #searchSubmit {border-radius:8px!important;border:none!important;}

}
