css:
@-moz-document domain("assembla.com") {

.user-name, table.u-stream {
	font-family: tahoma;
}
table.u-stream div.description {
	text-align: right;
	direction: rtl;
}

table#tickets-right-col-wrap tbody tr td #resultsContainer #ticket_list {
	direction: rtl;
	font-family: tahoma;
}

.tickets td.summary, .tickets td.ticket_status_id {
	text-align: right;
}
.ticket-popup-menu-link {
	float: left;
}
.top-ticket_details {
	direction: rtl;
	text-align: right;
}
#ticket ul {
	font-family: tahoma !important;
}
#ticket .comment, .title-division, .ticket_action div.col_left textarea, input[type=text], textarea {
	font-family: tahoma !important;
	direction: rtl;
	text-align: right;
}
h3, .ticket-context-menu {
	font-family: tahoma !important;
}
.ticket_action div#action_form div.col_right ul li label, .ticket_action div#action_form div.col_right ul li input[type="radio"] {
	float: right;
}
.cw-card .cw-link {
	direction: rtl;
	font-family: tahoma;
	text-align: right;
	float: right;
}
.milestones_list {
	text-align: right;
	direction: rtl;
	font-family: tahoma;
}
#ticket-tool .progress-bar .title, select {
	font-family: tahoma;
}
.new-calendar {
	font-family: tahoma;
	font-size: 11px;
}
table#details, .message-wrap {
	direction: rtl;
	font-family: tahoma;
}
.list .message-col {
	margin-right: 46px;
	margin-lefts: 0;
}
#ticket_list {
	direction: rtl;
	font-family: tahoma;
}
.top-ticket_details h1 input {
	font-size: 14px;
	font-weight: normal;
}
table.u-stream tbody tr td.space-name div.description .ticket-comment {
	font-style: normal;
}

}
