css:
.delete_item {
top: 6px;
right: -15px;
}
.profiles-vertical-list li a.group_title {font-size: 12px; width: 220px;}



.stream_total_points { color: purple !important; }
.stream_note { color: #571B7E !important; }

#point-clipboard-container {
height: 655px; width: 500px; top: 0px;}
#activity-list-container {padding-left: 60px}
#activity-search-field { width: 265px;}

.stream-author          { font-size: 11pt !important; }
.workout_headline       { font-size: 14pt !important; }
.action_prompt          { font-size: 11pt !important; }
.user_comment_textarea  { font-size: 10pt !important; }
ul.action_detail li     { font-size: 10pt !important; }
.main-content-column p  { font-size: 10pt !important; }
.stream_status          { font-size: 10pt !important; }

p.timestamp             { font-size: 10pt !important; }
.proppers               { font-size: 10pt !important; }


div#content.clearfix   { width: 100% !important; }

.main-content-column   { width: 855px !important; }
.stream_item_content   { width: 600px !important; }
.stream_item { padding: 10px; border-bottom: 1px solid #CCC; }
.user_comment_textarea { width: 520px !important; }
.stream_item_info { float: left; width: 140px; margin-right: 8px; }
#footer_stats ul li p { color: white !important; }

#global_notification_bubble > div {
 font-size: 20px !important;
  line-height: 20px !important;
  min-width: 19px !important;
}

#global_notification_bubble {
  margin: 4px 0 0 0 !important;
}
#footer_stats { display: block !important; }

.avatar.large {
  height: 200px !important;
  width: 200px !important;
}

ul.action_detail li { line-height: 150% !important; }
ul.action_detail li ul  { margin-bottom: 5px !important; }
.action_prompt { margin-bottom: 5px !important; }
.stream_note { margin: 0 0 0 0 !important; }

#mini-dash {
background-color: 
#2E393D;
z-index: 9999;
position: fixed;
bottom: -207px;
width: 380px; }

#mini-dash > div {
border-bottom: 1px solid 
#1E2B32;
border-top: 1px solid 
#626D61;
padding: 8px 5px;}

#user-search-results {height: 400px; }
#user-search-results .modal-follower-list { overflow-y: scroll; max-height: 300px;}
