css:
#suggested_container {visibility: hidden;}
.ad_container {visibility: hidden;}


.proppers.propped > span:nth-last-child(2) > a:not(.show_all_props):before,

.proppers.propped > span:not(:nth-last-child(2)) > a:before {

  content: attr(href) !important;

  margin-left: -27pt !important;

  font-size: 8pt !important;

}



/* This seems to help make them flow like text...

 * display:inline or inline-block wasn't working... */

.proppers.propped > span {

  float: left !important;

}



/* Get rid of the two newlines in the expanded "+ X others propped this". */

.proppers.propped > span > br {

  display:none !important;

}



/* Turn off the tiny avatars, since we have usernames instead. */

.proppers.propped > span > a > img {

  display: none !important;

}



/* Get rid of the drop-shadow on tiny avatars, which we are hiding anyway. */

.proppers.propped .avatar.tiny:after {

  content: none !important;

}



/* Clip the /profile/ part of the URLs. */

.proppers.propped > span:nth-last-child(2) > a:not(.show_all_props),

.proppers.propped > span:not(:nth-last-child(2)) > a {
  overflow-x: hidden !important;
  width: auto !important; }
ul.comment_list { clear: left !important; }



.profiles-vertical-list li a.group_title {
font-size: 12px; width: 220px;}
table.ldrbrd td {
font-size: 14px;
font-weight: bold;
padding: 10px;
padding-top: 10px;
padding-right: 10px;
padding-bottom: 10px;
padding-left: 10px;}

div#content.clearfix   { width: 100% !important; }
/*Link colors*/
a { color: #D9713D;}
a:hover { color: #E1095F; }
#point-clipboard-container {
height: 655px; width: 500px; top: 0px;}
#activity-list-container {padding-left: 100px}
#activity-search-field { width: 265px;}
.delete_item {
top: 6px;
right: -15px;
}
#point-clipboard-container {
height: 655px; width: 500px; top: 0px;
}
#activity-list-container {padding-left: 60px}

#header_profile_pic {
margin: 0 8px 0 5px;
padding: 8px 8px;
display: block;
float: left;
width: 25px;
height: 25px; }
#header_profile_pic:hover {
background:none; }

.callout {background: white}
#mini-profile #user-menu {
font-size: 12px;
padding: 13px;
background-color: #EDEDED }


#profile-header ul {
width: 420px;
}

/*Font sizes*/
.stream-author          { font-size: 12pt !important; }
.workout_headline       { font-size: 11pt !important; }
.action_prompt          { font-size: 11pt !important; }
.user_comment_textarea  { font-size: 10pt !important; }
ul.action_detail li     { font-size: 10pt !important; }
.main-content-column p  { font-size: 11pt !important; }
.stream_status          { font-size: 10pt !important; }
p.timestamp             { font-size: 10pt !important; }
.proppers               { font-size: 10pt !important; }
#message_container textarea { font-size: 12pt;}

/*Stream properties*/
.stream-author { color: #1EA7DB}
.stream_total_points { color: #88B741 !important; font-size: 16pt !important; }
.stream_note { color: #1EA7DB !important; }
.stream_item { padding-left: 25px; padding-top: 15px;
padding-right: 10px; padding-bottom: 15px }


/*Important events in stream*/
.dramatic-event .dramatic-title { font-size: 11pt;
font-weight: bold; color: #E1095F; }
.dramatic-event .dramatic-title a:hover { color: #88B741; }

/*Left side navigation*/
.vert-nav { padding-left: 15px}
.vert-nav li a.current, .vert-nav li a:hover { color: #2E393D;}
.vert-nav li a { color: #D9713D; }

#profile-header h1 a { color: #E1095F; margin-left: 20px;}
#profile-header h1 a:hover { color: #8A3497; }
#global_notification_bubble > div { color: #88B741; font-size: 20px !important;
  line-height: 20px !important; border-color: #88B741; 
  min-width: 19px !important;
}
#profile-nav ul li a { color: #88B741; }

div#content.clearfix   { width: 100% !important; }

.main-content-column   { width: 915px !important; }
.stream_item_content   { width: 620px !important; }
.user_comment_textarea { width: 520px!important;  height: 40px !important;}

#global_notification_bubble { margin: 4px 0 0 0 !important; }

.avatar.large {  height: 200px !important;
  width: 200px !important; }

ul.action_detail li { line-height: 150% !important; }
ul.action_detail li ul  { margin-bottom: 5px !important; }
.action_prompt { margin-bottom: 5px !important; }
.stream_note { margin: 0 0 0 0 !important; }

#mini-dash > div, #mini-dash > ul {
padding-top: 8px;
padding-right: 5px;
padding-bottom: 8px;
padding-left: 5px;
}
#mini-dash {
background-color: #2E393D;
z-index: 9999;
position: fixed;
bottom: -207px;
width: 350px; }

.sidebar-column {
display: inline;
float: left;
width: 315px;
margin: 0 12.5px;
min-height: 1px;
}
.sidebar-column > div:first-child { width: 287px !important; }
.sidebar-column h3 { color: #8A3497; }
#mini-dash .challenge-count > div {
border: 3px solid #8A3497;
padding: 4px;
color: #8A3497;
font-size: 16px;
}
#mini-dash .tab-nav {
font-size: 13px;
padding: 8px 10px;
height: 25px;
}
#mini-dash .dash-challenge-list {max-height:290px;}
#mini-dash .dash-challenge-list li > div {
margin-left: 20px;
padding-right: 10px;
}

.stream-duel .duelist {
font-size: 14px;
font-weight: bold;
text-transform: uppercase;
color: #1E2B32;
}

.stream-duel .duel-type {
font-size: 16px;
font-weight: bold;
color: #8A3497;
display: inline-block;
line-height: 130%;
margin-bottom: 5px;
}


#user-search-results {height: 400px;
}
#user-search-results .modal-follower-list {
overflow-y: scroll;
max-height: 300px;}

/* About me, Followers, Achievements, Groups, Tags */

.sidebar-column > div {

  padding-left: 0px !important;

  margin-left: 0px !important;

}



/* Level */

#footer_stats .progressbar-container {

  padding-left: 0px !important;

  margin-left: 0px !important;

}



/* Titles, Achievements, Quests */

#footer_stats ul {

  padding-left: 0px !important;

  margin-left: 0px !important;

}



/* Footer stats box */

#footer_stats {

  padding-left: 13px !important;

  margin-left: 0px !important;

}
#footer_stats ul li:not(:last-child) {

  margin-right: 15px !important; }
