/* Update: 2015-01-04 (userstyles.org/styles/63573) */
/* Based on ModernWiki by vetinari (userstyles.org/users/18227) */
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikipedia.org"), domain("wikimedia.org"), domain("wiktionary.org"), domain("wikiquote.org"), domain("wikibooks.org"), domain("wikisource.org"), domain("wikinews.org"), domain("wikiversity.org"), domain("wikimediafoundation.org"), domain("mediawiki.org"), domain("wikivoyage.org"), domain("wiki.openstreetmap.org"), domain("wikidata.org"), domain("translatewiki.net"), url-prefix("http://www.baka-tsuki.org/project/") {

/* main elements */ body > .noprint, #footer, #p-logo, #jump-to-nav,
/* sidebar */ .portal > h5, .portal > h3, #p-iproject, /*wiktionary*/ .portal > .body > p,
/* fundraising */ #siteNotice, #centralNotice, /* notes */ .metadata.plainlinks,
/* ToC toggle */ .toctoggle, /* search image */ #searchButton > img,
/* variants */ .vectorMenu > h5, .vectorMenu > h3, .vectorMenu > h4,
/* popup arrows */ .uls-menu.callout .caret-after, .uls-menu.callout .caret-before,
/* empty references */ .references:empty, .reflist:empty, .refbegin:empty,
/* baka-tsuki */ #p-googleadsense,
/* homepage */ body > .bookshelf-container, body > .langlist, .search-container > h5
{ display: none !important; }

/* links to wiktionary */
.metadata.mbox-small.plainlinks { display: table !important; }

/* backgrounds */

html { background: #eed; }
#content, #p-personal, .vectorTabs:hover, /* on blog */ #toc.toc { background: #fff; }
#mw-panel#mw-panel .portal > .body, .portal > .body { background: #fff !important; }
/* user */ #pt-login, #pt-userpage, /* drop-down arrows */ .portal, /* main tabs */ .vectorTabs, .vectorTabs > ul, .vectorMenu, /* #p-lang: en, cz-sk, es */ li.FA, li.GA, li.featured, li.good, li.bueno, li.destacado { background-image: none !important; }
body, .vectorTabs > ul > li, .vectorTabs > ul > li > span, .vectorTabs > ul > li > span > a, .reference:target, #column-content, #mw-mf-page-center { background: none !important; }

/*** Main Content ***/

body.mediawiki { padding-right: 15px !important; padding-left: 15px !important; }
#content { width: 90% !important; min-width: 750px !important; max-width: 1260px !important; margin: 48px auto 24px auto !important; padding: 1.25em 1.5em 1.5em !important; border-width: 1px !important; border-style: solid !important; border-color: #444; border-radius: 7px !important; /* .de fix */ z-index: auto !important; }
div[id="content"] { border-color: #444 !important; }
/* jump to */ #content :target { padding-top: 32px !important; }
/* mobile */
#content_wrapper { margin: 0 auto !important; }
#content_wrapper, #content_wrapper > #content { min-width: 0 !important; max-width: 900px !important; }
/* blog.wikimedia.org */
#column-content { padding: 1em 0 0 !important; border: 0 !important; }
.blog #globalWrapper, .single #globalWrapper, .archive #globalWrapper { max-width: 950px !important; margin: auto !important; }
.blog #column-one, .single #column-one, .archive #column-one { left: auto !important; }

/* overall settings */

#bodyContent, /* ro */ .topicon { z-index: auto !important; }
#content input[type="submit"] { padding: 2px 10px !important; font-weight: bold !important; }
dl dt { margin: 8px 0 -1px 0 !important; padding: 0 2px !important; }
.gallerytext > p { margin: 0 !important; }
.page-Main_Page table[id] table th[style] { margin: 0 !important; padding: 3px !important; }

/* list bullets */

ul { list-style: disc inside none !important; }
/*ul[id],*/ #toc ul, form ul, .search-types > ul, .searchresults > ul, .results-info > ul, .plainlist ul, #pagehistory, .vectorMenu > .menu > ul, #mw-subcategories ul,
/* related portals */ #plainSister, /* kbd selection */ .ime-list, /* mobile */ .mobile ul[id], .suggestions-result,
/* wikivoyage Main */ .panes { list-style-type: none !important; }

/* header */

#mw-head { display: block !important; position: fixed !important; z-index: 99 !important; height: 28px !important; overflow: visible !important; border-top: 1px solid #999 !important; border-bottom: 1px solid #000 !important; background-color: #cedff2; background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAeCAQAAABHnLxMAAAAHElEQVQI12P478TE8I+J4R8Tw38kEqsIJgOfGgBRBh5MLGeL4QAAAABJRU5ErkJggg==") !important; background-repeat: repeat-x !important; /* discussion/44573 */ top: 0 !important; margin-top: 0 !important; }
#mw-panel { display: block !important; position: fixed !important; top: 0 !important; z-index: 101 !important; width: 100% !important; height: 1px !important; overflow: visible !important; margin: 0 !important; padding: 0 !important; }
.ve-ui-toolbar-floating .ve-ui-toolbar-bar { top: 29px !important; z-index: 95 !important; left: 0 !important; right: 0 !important; }

/* search box */

#p-search { position: absolute !important; top: 0 !important; left: 50%; margin: 1px 0 0 !important; padding: 3px 0 3px 56px !important; border-left: 1px solid #111; }
#searchform { margin: 0 !important; }
#simpleSearch { height: 20px !important; width: 224px !important; margin: 0 !important; padding: 0 0 0 4px !important; border: none !important; border-bottom: 1px solid #ccc !important; border-radius: 11px !important; }
#simpleSearch #searchButton, #simpleSearch #mw-searchButton { height: 20px !important; width: 18px !important; padding: 0 !important; background: url("data:image/png;base64,") left no-repeat !important; }
#globalWrapper #topsearch { left: 0 !important; right: 0 !important; margin: auto !important; }
#globalWrapper #p-search { margin: auto 0.5em !important; padding: 0 !important; border-left: none !important; }
#globalWrapper #simpleSearch { margin-top: 8px !important; border: 1px solid !important; }
#simpleSearch > #s { width: 200px !important; padding: 0.25em 0 !important; }
#www-wikiquote-org #p-search, #www-wikibooks-org #p-search, #www-wikiversity-org #p-search { position: relative !important; top: auto !important; left: auto; margin: 1em auto 1.5em !important; padding: 0.3em !important; border-left: none !important; }
/* wikivoyage */ #searchform { display: block !important; }

/* search suggestions */

html > body > .suggestions { right: 50% !important; margin-right: -285px !important; opacity: 0.94 !important; box-shadow: 2px 2px 3px #000; }
.suggestions-results, .suggestions-special { border-color: inherit !important; }

/* left menu (namespaces) | right menu (views) */

#left-navigation, .vectorTabs { position: absolute !important; }
#left-navigation { left: 6% !important; right: auto !important; top: 5px !important; z-index: 555 !important; margin: 0 !important; }
#p-namespaces { left: 0 !important; right: auto !important; top: 0 !important; }
#p-views { left: auto !important; right: 6% !important; top: 5px !important; z-index: 999 !important; }
.vectorTabs:before { content: "\2193 \2193" !important; display: block !important; margin: 4px 0 0 !important; font-size: 10px !important; line-height: 12px; }
#p-namespaces:before { text-align: left !important; }
#p-views:before { text-align: right !important; }
.vectorTabs:hover:before { content: "\a0" !important; line-height: normal; }
.vectorTabs { height: auto !important; width: auto !important; margin: 0 !important; padding: 0 !important; cursor: default; }
.vectorTabs:hover { padding: 0 8px 5px 3px !important; border: 1px solid !important; border-radius: 5px !important; opacity: 0.94 !important; }
.vectorTabs > ul { display: none !important; }
.vectorTabs:hover > ul { display: block !important; }
.vectorTabs > ul > li { display: list-item !important; float: none !important; height: auto !important; margin: 3px 1px 3px 3px !important; padding: 0 3px 0 4px !important; line-height: 0.9em !important; text-decoration: none !important; list-style: circle inside none !important; }
.vectorTabs > ul > li.selected { list-style-type: disc !important; }
.vectorTabs a { float: none !important; height: auto !important; padding: 0 !important; text-decoration: none !important; }
.vectorTabs a:hover { text-decoration: underline !important; }
/* WebKit */ #rndm::-webkit-input-placeholder, .vectorTabs > ul { padding-right: 1em !important; }

/* script (variants) | admin (cactions) */

.vectorMenu { position: fixed !important; top: 37px !important; left: 1% !important; margin: 0 !important; cursor: default !important; line-height: 1em !important; }
#p-cactions { top: 60px !important; }
.vectorMenu > .menu { top: auto !important; line-height: normal !important; }
#p-variants:before { content: "\a0 v \a0" !important; }
#p-cactions:before { content: "\a0 o \a0" !important; }

/* account menu | table of contents */

#p-personal { position: absolute !important; right: 2% !important; left: auto !important; }
#toc.toc { display: block !important; position: fixed !important; right: auto !important; left: 2% !important; -moz-box-sizing: content-box !important; box-sizing: content-box !important; }
#p-personal, #toc.toc { top: 4px !important; z-index: 9999 !important; height: 22px !important; width: 22px !important; overflow: hidden !important; margin: 0 !important; padding: 0 !important; border: 0 !important; cursor: default !important; }
#p-personal:hover, #toc.toc:hover { width: auto !important; height: auto !important; max-height: 511px !important; overflow-y: auto !important; margin-top: 2px !important; padding: 8px !important; border: 2px solid !important; border-radius: 8px !important; opacity: 0.94 !important; background: #fff !important; box-shadow: inset -1px -1px 0 #111, inset 1px 1px 1px #ccc, -1px -1px 1px #111 !important; }
#p-personal > ul > li { display: block !important; float: none !important; margin: 2px 2px 4px !important; padding: 0 !important; }
#p-personal > ul { margin: 20px 0 0 !important; padding: 2px 4px !important; text-align: left !important; }
#p-personal:hover > ul { margin: 0 !important; }
#p-personal > ul > li > a:hover { text-decoration: underline !important; }
#toc td > ul { padding: 0 !important; }
.toc ul ul { margin-left: 1.2em !important; }
.tocnumber { margin-right: 0.3em !important; }
#p-personal > ul, #toc.toc > tbody, #toc.toc > ul, #toctitle { display: none !important; }
#p-personal:hover > ul, #toc.toc:hover > tbody, #toc.toc:hover > ul, #toc.toc:hover > #toctitle { display: block !important; }
#toc.toc { background: url("data:image/png;base64,") center no-repeat !important; }
#p-personal { background: url("data:image/png;base64,") center no-repeat !important; }

/* panels */

#mw-panel#mw-panel .portal, .portal, .portal > .body > ul { padding: 0 !important; }
#mw-panel .portal { position: absolute !important; top: 4px !important; left: 50% !important; cursor: default !important; margin-top: 0 !important; line-height: 0 !important; }
#p-lang { z-index: 900 !important; margin-left: -283px !important; }
#p-navigation, #p-Navigation { z-index: 910 !important; margin-left: -233px !important; }
#p-coll-print_export { z-index: 920 !important; margin-left: -183px !important; }
#p-tb { z-index: 930 !important; margin-left: -133px !important; }
div[id="mw-panel"] > .portal { display: block !important; z-index: 940 !important; margin-left: -83px !important; }
/*baka-tsuki*/
#p-Network { z-index: 923 !important; margin-left: -158px !important; }
#p-Original_Light_Novels { z-index: 945 !important; margin-left: -58px !important; }
#p-Light_Novels { z-index: 933 !important; margin-left: -108px !important; }
#p-Visual_Novels { z-index: 943 !important; margin-left: -83px !important; }
#p-Abandoned_\.5BEng\.5D { z-index: 950 !important; margin-left: -33px !important; }

.portal > .body { display: none !important; max-height: 500px !important; max-width: 540px !important; overflow-y: auto !important; margin: 1px 0 0 -22px !important; padding: 4px 8px !important; border: 1px solid !important; border-radius: 5px !important; opacity: 0.94 !important; line-height: normal; }
#p-lang > .body { margin-left: -50px !important; }
.portal:hover > .body { display: block !important; }
.portal > .body > ul { margin: 0 !important; }
#p-lang > .body > ul { min-width: 12em !important; -moz-column-count: 3; }
/* WebKit */ #rndm::-webkit-input-placeholder, #p-lang > .body { width: 26em !important; -webkit-columns: 3; }
/* Opera */ #rndm:-o-prefocus, #p-lang > .body > ul { width: 26em !important; columns: 3; }
.portal > .uls-settings-trigger { display: none !important; position: absolute !important; float: none !important; margin: -18px 0 5px 26px !important; }
.portal:hover > .uls-settings-trigger { display: block !important; }
.uls-menu { position: fixed !important; top: 28px !important; left: 0 !important; right: 0 !important; margin: /*66px*/0 auto !important; }

.rtl #mw-panel > .portal { text-align: left !important; }
.rtl #mw-panel > .portal > .body { text-align: right !important; }

li.interwiki-en, li.interwiki-de, li.interwiki-es, li.interwiki-fr, li.interwiki-it, li.interwiki-ru { font-weight: bold !important; }
.portal li > a { font-size: 1em !important; }
.portal > .body > ul > li { display: block !important; margin: 0 !important; padding: 0.25em 0 !important; text-shadow: 1px 1px 1px #158; }
#p-lang > .body > ul > li { display: list-item !important; list-style-type: disc !important; list-style-position: inside !important; }
#p-lang > .body > ul > .uls-p-lang-dummy, #p-lang > .body > ul > .wbc-editpage { list-style-type: none !important; }

/* #p-lang: en, cz-sk, fr, es */
li.GA, li.good, li.BA, li.bueno { list-style-image: url("data:image/png;base64,") !important; }
li.FA, li.featured, li.AdQ, li.destacado { list-style-image: url("data:image/png;base64,") !important; }

.portal:before { opacity: 0.7; }
.portal:hover:before { opacity: 1; }
#p-lang:before { content: url("data:image/png;base64,") !important; }
#p-navigation:before, #p-Navigation:before { content: url("data:image/png;base64,") !important; }
#p-coll-print_export:before { content: url("data:image/png;base64,") !important; }
#p-tb:before { content: url("data:image/png;base64,") !important; }
div[id="mw-panel"] > .portal:before { content: url("data:image/png;base64,") !important; }

/* collapsing references if JavaScript enabled */
@supports (transition: max-height) {
  .client-js #mw-content-text > .references, .client-js #mw-content-text > .reflist, .client-js #mw-content-text > .refbegin
  { max-height: 0 !important; overflow: hidden !important; border: 10px solid; border-color: #444 #f33 #ccc; border-radius: 8px; transition: max-height 3s ease 1s, border-width 3s ease 1s !important; }
  .client-js #mw-content-text > .references:hover, .client-js #mw-content-text > .reflist:hover, .client-js #mw-content-text > .refbegin:hover
  { max-height: 500em !important; overflow: visible !important; overflow-x: auto !important; overflow-y: visible !important; border-width: 0 1px !important; }
  }

}
