css:
@-moz-document domain("community.eu.playstation.com") {

/* Rimuovo sidebar nei topics */
#lia-body.ForumTopicPage .lia-content .lia-quilt-column-06 { display: none !important; }

/* Rendo header largo 100% */
#lia-body.ForumTopicPage .lia-content .lia-quilt-layout-two-column-main-side .lia-quilt-column-common-header { background: white; width: 100% !important;  border-radius: 5px 5px 0px 0px; margin-top: 0px !important;}

/* Allargo breadcrumb */
#lia-body.ForumTopicPage .lia-content .lia-quilt-row .lia-quilt-column-breadcrumb { width: 100% !important; }
/* Rendo main largo 100% */
#lia-body.ForumTopicPage .lia-content .lia-quilt-layout-two-column-main-side .lia-quilt-column-main-content { width: 100% !important; }

/* Allargo box post */
#lia-body .lia-content .lia-quilt-layout-forum-message .lia-quilt-column-20 { width: 86% !important; }

/* Rimuovo link pubblicitarioo nello showcase */
#adSalesTopClick { display: none !important; }

/* Rimuovo icona di stampa, noi siamo ecologisti! */
.print-page-icon { display: none !important; }

/* Sistemo la scritta "Segnala al moderatore" */
#lia-body .lia-content .lia-panel-message .lia-component-report-abuse { position: absolute; padding-top: 7px !important; }

/* Sistemo Opzioni V */
#lia-body .lia-content .lia-quilt-layout-forum-message .lia-quilt-column-main-right .lia-quilt-column-04 { position: absolute; right: 45px; padding-top: 11px; }

/* Fix quick reply */
#lia-body .lia-content .lia-form .lia-form-input-wrapper > textarea, #lia-body .lia-content .lia-form .lia-form-input-wrapper > .lia-form-radio-group { margin-left: 111px; width: 882px; }
.lia-form-footer { padding: 8px; }
#lia-body .lia-content .lia-form-vertical .lia-form-body-entry, #lia-body .lia-content .lia-form-vertical .lia-form-body-entry .lia-form-label-wrapper { margin: 0px !important; }
#lia-body .lia-content .lia-form textarea + .lia-form-error-text, #lia-body .lia-content .lia-form textarea + .lia-form-validation-help-text { margin-bottom: 0px !important; display: block !important; top: -12px !important;  padding-left: 111px; }

/* Rimozione Showcase */
#lia-body .lia-content .lia-quilt-layout-two-column-main-side .lia-quilt-column-search-bar, #lia-body .lia-content .lia-quilt-layout-one-column .lia-quilt-column-search-bar {margin-top: -178px !important; }
#lia-body .MinimumWidthContainer .lia-content { padding-top: 0px !important; }
#adSalesTopHolder { display: none !important; }

/* Topic viewer */
.lia-quilt-row lia-quilt-row-header > div { margin-top: 0px !important; }

/* Fix pagina modifica profilo */
#profilesignature, #profilebiography, #profilenotes { margin-left: 0px !important; width: 100% !important;}
.lia-decoration-border-content { width: 99% !important; }


/* Fix pager */
#lia-body.ForumTopicPage #pager { color: black !important; background: white !important; }
#lia-body.ForumTopicPage #pager a { color: #005CCE !important; }

/* Rimuovo logo lithium sucks */
#lia-body .lia-quilt-footer .lia-component-common-widget-lithium-logo { display: none !important; }

/* Firme */
#lia-body .lia-content .lia-message-signature { margin: 20px auto 10px auto !important; } /* Centro le firme */

/* Input box segnala abuso */
#lia-additionalInformation { margin-left: 40px !important; }

/* Topbar */
#top-bar { display: none !important; }
}

/* Pagina messaggi personali */
@-moz-document url("http://community.eu.playstation.com/t5/notes/privatenotespage") {

/* Rimuovo spazio vuoto tra elenco messaggi e breadcrumb */
#tabgroup {margin-top: -100px !important; }
}
