css:
@-moz-document domain("ponibooru.413chan.net"), domain("ponibooru.org"), domain("whitetail.ponibooru.org") {
body {background-color: #bbf !important;}
input:not([type]),input[type="text"],input[type="password"],input[type="checkbox"],input[type="radio"],textarea,select,.pbh-tag-box {
   background-image: -moz-linear-gradient(bottom, #FFFFFF 0%, #DDDDDD 100%) !important;
   background-image: -webkit-linear-gradient(#ddd, #fff);
   border: 1px solid gray;
   border-radius: .25em;
   box-sizing: border-box;
   padding: .1em !important;
   -moz-appearance: none;
}
.pbh-tag-box {border: 1px solid gray !important; padding: 4px !important;}
a:hover {color: #419fff}
input[type="button"],input[type="submit"],button {
   background-color: #ddf !important;
   border: 1px solid gray;
   border-radius: 4px;
   box-shadow: inset 0 1px 3px #fff, inset 0 -9px #cce;
   margin: .2em 0px !important;
   padding: .1em .5em !important;
   -moz-appearance: none;
}
input[type="button"]:hover,input[type="submit"]:hover,button:hover {
   border-color: #3f3f3f;
}
input[type="button"]:active,input[type="submit"]:active,button:active {
   box-shadow: inset 0 1px 3px #fff, inset 0 9px #cce;
}
input[type="checkbox"],input[type="radio"] {
   -moz-appearance: none;
   -webkit-appearance: none;
   padding: 0px !important;
   width: 1em !important;
   height: 1em !important;
   margin: -.2em .2em !important;
}
input[type="radio"] {border-radius: .5em;}
input[type="checkbox"]:checked:before,input[type="radio"]:checked:before {
   display: block;
   width: 100%;
   height: 100%;
   content: '';
   background-position: center center;
   background-size: 100% 100%;
}
input[type="checkbox"]:checked:before {
   background-image: url("data:image/png;base64,");
}
input[type="radio"]:checked:before {
   background-image: url("data:image/png;base64,");
}
select {
/*   -moz-appearance: none;
   -webkit-appearance: none;
   padding-right: 1em !important;
   background-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABMAAAAICAYAAAAbQcSUAAAAB3RJTUUH3AMPFhkFRSqeJwAAAAlwSFlzAAALEgAACxIB0t1+/AAAAARnQU1BAACxjwv8YQUAAABmSURBVHjaY2CgImDEIW4DxBx49P0A4iPogkzYVCYmJrIyMzMnApnp6BgkDpIn2rn///8XXbBgQQ4bG9tyIHc1DIP4IHGQPEn+RzeQbIPQDeTj41tCkUHIBr59+zacYoOQDGQmRh0AZ2g5//NPNMYAAAAASUVORK5CYII="), -webkit-linear-gradient(#ddd, #fff);
   background-repeat: no-repeat;
   background-position: 100% 50%;*/
   padding: 0px !important;
}
ul {margin-left: 1.5em !important;}
.ac_results > ul {margin-left: 0px !important;}
form > table {
   width: auto !important;
   min-width: 455px;
}

#site-title a:visited,#site-title a:visited,#site-title a:hover {color: #004 !important;}

div#header,div#header ul#subnavbar {margin-bottom: 0px !important;}
div#header ul#navbar,div#header ul#subnavbar {margin-left: 0px !important;}
div#header ul#navbar {border-bottom: 1px solid #77f;}
div#header ul#navbar li {
   margin: 0px -2px !important;
   background-color: #ddf;
   position: relative;
   border-top-left-radius: 8px;
   border-top-right-radius: 8px;
   border-color: #77f;
   border-style: solid;
   border-width: 1px 1px 0px;
   box-shadow: 0px 0px 4px gray;
}
div#header ul#navbar li.current-page {padding-bottom: 3px !important; z-index: 4;}
div#header ul#subnavbar {position: relative; z-index: 8;}

#blotter1 {
   position: absolute !important;
   z-index: 10;
   top: 1em;
   right: 1em;
   text-align: right;
}
#blotter1 > span {position: static !important; display: block;}

#blotter2 {
   right: 1em;
   top: 4em;
   width: 30em;
   text-align: justify;
   background-color: #ffffe1;
   padding: 1em !important;
   border: 1px solid #004;
   border-radius: 0.3em;
   box-shadow: rgb(0, 0, 127) 0px 0px 0.5em;
   position: absolute;
   z-index: 10;
   display: block;
}
#blotter2 li {
   display: block;
   text-indent: -1em;
   margin-left: 1em !important;
}

/*** Navigation bar ***/
#nav {
   background-color: #F7F7FF;
   width: 192px !important;
   padding: 14px !important;
   position: absolute;
   border-bottom-right-radius: 2em;
}
#nav input {
   box-sizing: border-box;
   -moz-box-sizing: border-box;
   padding: .1em !important;
}
#nav input[type="submit"] {border-color: #bbb;}
#nav input[type="submit"]:hover {border-color: gray;}
#Featured_Imageleft {text-align: center;}
#Featured_Imageleft > a > img {
   margin: .5em 0p !importantx;
   box-shadow: 0px 0px .5em gray;
}

.tag_name {
   padding-right: 12px !important;
   background: #eef url("data:image/png;base64,") no-repeat 100% 50%;
   border: #ddf solid 1px;
   border-radius: 4px;
}

.withleft {margin-left: 232px !important;}

#body {position: relative;}

#main_image {
   box-shadow: 0px 0px .5em #00007f;
   margin: .5em 0px !important;
}

div#paginator a {
   border-color: #ddf;
   border-radius: .3em;
   box-shadow: 0px 0px .3em white;
   background-color: rgba(255,255,255,0);
   transition: background-color 250ms;
   -o-transition: background-color 250ms;
   -moz-transition: background-color 250ms;
   -webkit-transition: background-color 250ms;
}
div#paginator a:hover {
   background-color: rgba(255,255,255,.5);
}


span.thumb, #\A0 main > table > tbody > tr > td:first-child {line-height: 200px !important; text-align: center;}
span.thumb > a, #\A0 main > table > tbody > tr > td:first-child > a {
   display: inline-block;
   height: 200px !important;
   width: 200px !important;
   background-color: rgba(255,255,255,.2);
   box-shadow: 0px 0px 10px #00007f;
   transition: background-color 250ms;
   -o-transition: background-color 250ms;
   -moz-transition: background-color 250ms;
   -webkit-transition: background-color 250ms;
}
span.thumb > a:hover, #\A0 main > table > tbody > tr > td:first-child > a:hover {background-color: rgba(255,255,255,.4);}
span.thumb > a > img, #\A0 main > table > tbody > tr > td:first-child > a > img {vertical-align: middle; margin-bottom: 4px !important;}


/*** Image page/Comment list ***/
.comment:nth-child(even) {
   background-color: #ccf;
   width: 100%;
}
.comment .meta {color: #444;}
.meta > .username, .meta > .modname {
   display: inline-block;
   width: 160px;
   overflow: hidden;
   text-overflow: ellipsis;
   word-break: normal;
}
.meta > .username:hover, .meta > .modname:hover {
   word-break: break-word;
}
.meta+td {word-break: break-word;}
.meta+td img {box-shadow: 0px 0px 10px gray;}

.comment blockquote {
   background-color: #99f;
   padding: .5em !important;
   border: #77f solid 1px;
   border-radius: .5em;
   box-shadow: .2em .2em .5em gray;
   display: table-cell; /* Shouldn't use this here, but it fixes a thing... */
   position: relative;
   left: -1px;
}
.comment blockquote > em:first-child {
   display: inline-block;
   background-color: #77f;
   width: 100%;
   padding: .1em .5em !important;
   position: relative;
   top: -.5em;
   left: -.5em;
   border-top-left-radius: .3em;
   border-top-right-radius: .3em;
}
.comment blockquote > em > a {color: #7cf;}
.comment blockquote > em > a:hover {color: #adf;}

.meta+td span { /* These should be spoilers */
   padding: 0px .2em !important;
   border-radius: .3em;
   box-shadow: 0px 0px .5em gray;
}

textarea[name="comment"] {min-width: 48em; min-height: 9.6em;}

/*** Comment list ***/
#\A0 main > table {width: 100%;}

#footer {
   position: fixed;
   bottom: 1em;
   right: 1em;
   background: #ffffe1 url("data:image/png;base64,") no-repeat center center;
   border: 1px solid black;
   border-radius: .5em;
   width: 24px;
   height: 24px;
   padding: .5em !important;
   color: gray;
}
#footer > em {display: none;}
#footer:hover {
   width: auto;
   height: auto;
   background-image: none;
   background-position: top left;
}
#footer:hover > em {display: inline;}

/*** User page ***/
#pms th {
   background-color: #ddf;
   padding: .1em .3em !important;
   border: 1px solid #ccf;
}
#pms td {
   vertical-align: middle !important;
   padding: .3em !important;
}
#pms > tbody > tr:nth-child(even) {background-color: #ccf;}

/*** Upload page ***/
#large_upload_form input[type="radio"] {width: 1em;}

/*** Tag list ***/
#Tagsmain a {
   display: inline-block;
   margin: .1em !important;
   background-color: #e8e8ff;
   padding: 0em .3em !important;
   border: 1px solid #ccf;
   border-radius: 4px;
}

/*** Ad hiding ***/
#pw_adbox_61164_6_0,ins,.adbox {display: none !important;}

.tag_info_link {
   color: rgba(0,0,0,0) !important;
   display: inline-block;
   width: 16px !important;
   background-image: url("data:image/png;base64,");
}

}
