css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {

*{text-shadow:#dadada!important;
color:#000000 !important}

body{
background-image:url('http://img.photobucket.com/albums/v13/limejuice/MinWoo-Tumblr/tumblrstyle.gif') !important;
background-color:#ffffff !important;

background-repeat:repeat !important;

background-position: left top !important;

background-attachment:fixed !important;

font-family: Calibri !important;
}

#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background: rgba(0,0,0,0.2) !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

background-color: #ffffff !important
}

#left_column #posts .post .permalink {

background-color:#ffffff !important;
}

#left_column #posts .notification .avatar:hover{

opacity:1 !important;
                    }
.post{opacity:0.6 !important; border-bottom:5px !important; color:#000000}

.post:hover{opacity:1 !important}

.permalink{background-color: #ffffff !important; background-image:none !important}

.active{background-color:transparent !important}

.nav_item_nipple{display:none !important}

.nav_menu{background-color:#ffffff !important; border-bottom:5px !important
}


#left_column #posts .post{

border-bottom:0px #ffffff solid !important;

}

#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background-color:#ffffff !important;

background-image:none !important;
border:0px !important
}

#nav .nav_item .nav_menu {

border-bottom:0px !important
}

#nav .nav_item .nav_menu a {
color:#ffffff !important;
border-top-color:#000000 !important;
}

#nav .nav_item .nav_menu a:hover {
color:#000000 !important;
}

#footer, #footer a {
color:#ffffff !important;
background: rgba(0,0,0,0.3) !important;
background-image:none !important;
margin-left:5px;
}

.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {
color:#edecec !important
}


#right_column li{
background: white !important;
border: 1px solid #f6f6f6 !important;
background:rgba(255,255,255,1) !important;
}

#right_column .dashboard_nav_item {
border-bottom: solid 1px #000000 !important;
background-image:none  !important
}

#right_column .dashboard_nav_item .dashboard_nav_title,
                    #right_column .dashboard_nav_item .dashboard_nav_title a,
                    #right_column .dashboard_nav_item a,
                    #right_column .dashboard_nav_item ul.dashboard_subpages,
                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {
                        color:#000000 !important;

border:0px !important;
}
#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {
background:rgba(0,0,0,0.1) !important;

}


#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {
                        color:#ffffff !important ;
                    }

#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {
                        background:#ffffff !important;
                    }

#show_radar a, #hide_radar a,
                        #hide_recommended_blogs a, #show_recommended_blogs a {
                            color:#ffffff !important;
                        }


#left_column #posts .notification {
                        background: rgba(0,0,0,0.1) !important;
border: 1px #000000 dashed;
                        color:#d62222 !important;
                        border-bottom-color: #000000 !important
                    }

#left_column #posts .notification .block {
                            color:#d62222 !important;
                        }


#left_column #posts .notification.alt {
                        background-color: transparent !important;
                    }
#left_column #posts .notification.last_notification,
                    #left_column #posts .notification.single_notification {
                        border-bottom-color:#000000 !important;
                    }
#left_column #posts .notification a {
                        color:#ffffff !important; 
                        border-bottom:0px !important; 
                    }
#left_column #posts .notification blockquote {
                        border-left-color:#000000 !important;
                    }

#left_column #posts .notification .avatar {
                        border-color:black !important;
                        background-color:#d62222 !important;
opacity:50 !important;
                    }

#left_column #posts .notification .avatar:hover{
opacity:50 !important;
                    }

.dashboard_switch_blog_menu_header{

background-color:#666666 !important;
color:#d62222 !important;
}
.dashboard_switch_blog_menu_item{

background-color:#666 !important;
color:#d62222 !important;

border:1px !important;
}
.dashboard_switch_blog_menu_footer{

background-color:#666 !important;
color:#d62222 !important;
}
.dashboard_switch_blog_menu_wrapper{
background-color:#d62222 !important;
}

#new_post_notice_container{
color:#d62222 !important
}

#new_post_notice_container a{
color:#d62222 !important
}

#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current
{
background-image:none !important
}
.notification_type_icon{

display:none !important
}


h1,h2,h3,h4,h5,h6{color:#d62222 !important}



.new_post_label{

font-size:12px !important;

padding-top:60px !important;

text-transform:lowercase !important;

letter-spacing:2px !important;

font-style: normal !important;

text-shadow: #666666 0px 1px 2px; !important;

font-weight:normal !important;

margin:0px !important;

font-family:Calibri !important;

color:#000000 !important
}
.new_post_label:after{
content:"" !important
}


.new_post_label:hover{
color:#e54a4a !important
}
.arrow{display:none !important}

.sub_avatar{width:30px !important; height:30px !important; background-color:transparent !important}

.new_post_label_icon{background-image:none !important
}

#new_post{
background: url('http://img.photobucket.com/albums/v13/limejuice/MinWoo-Tumblr/topbar.jpg') right top no-repeat;
background-color:#ffffff !important;
}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    opacity: 0;
}

.tab a{

color:transparent !important;
}

div[class*="tab iconic"] {

  background-color: rgba(0,0,0,0.2) !important;

  height: 0px !important;

  border-radius: 20px !important;
}


.items li a {color:#fff !important}

.last_item a{color:#fff !important}

.selected a{text-shadow:1px !important; color:#fff !important}
.recessed a{text-shadow-color:none !important; color:#e54a4a !important}

.right_column_section li{background:rgba(0,0,0,0.1) !important; border:0px !important; color:#e54a4a !important}

.right_column_section li a{color:#e54a4a !important}


.right_column_section li a:hover{background:rgba(0,0,0,0.5) !important}

.notification, .avatar_frame, .nipple{border:0px !important}

.permalink{background-image:none !important; height:15px !important; width:15px !important; margin-top:0px !important; margin-right:0px !important;-webkit-border-radius:0px 0px 0px 0px !important;-moz-border-radius:0px 0px 0px 0px  !important}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:rgba(0,0,0,0.2) !important}

.dashboard_options_form, .dashboard_options_form_disclaimer{ border:0px !important; color:#e54a4a !important; font-weight:underline !important; text-shadow:1px 1px 1px #999999 !important}


.selection_nipple{

display:none !important;
}
.permalink{background-image:none !important; height:15px !important; width:15px !important; margin-top:0px !important; margin-right:5px !important;-webkit-border-radius:0px 0px 0px 0px !important;-moz-border-radius:0px 5px 5px 0px  !important}
#left_column #posts .post .post_avatar{-moz-border-radius: 30px !important; -webkit-border-radius: 5px !important; width: 64px !important; height: 64px !important; margin: 0px !important;}
#left_column #posts .post .sub_avatar_inner {
-moz-border-radius: 30px !important; -webkit-border-radius: 5px !important; width: 30px !important; height: 30px !important; margin: 0px !important;
}
#right_column li:hover {
   opacity:1; 
   background: rgba(229,74,74,0.5) !important;
 }

#logo {

height: 0 !important;
	width: 0 !important;

	padding-left: 350px !important;

	padding-top: 44px !important;

	background: url(http://img.photobucket.com/albums/v13/limejuice/MinWoo-Tumblr/titlebar.png) no-repeat !important;
}
::selection {
        background: #e54a4a; 
color:#fff
        }
::-moz-selection {
        background: #e54a4a; 
color:#000000}
.tab_bar a{color:#fff !important}

#new_post_notice_container{
color:#fff !important
}
#new_post_notice_container a{
color:#fff !important
}
ul#user_channels{color:#666 !important}

#right_column a{color:#e54a4a !important}

ol#posts li blockquote,


body.mceContentBody blockquote {

margin-left: 0 !important;

margin-right: 0 !important;

padding-left: 10px !important;

border-width: 4px !important;

border-color: #000000 !important;
}
ol#posts li blockquote blockquote,

body.mceContentBody blockquote blockquote {

border-color: #000000 !important;
}
ol#posts li blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote {

border-color: #000000 !important;
}
ol#posts li blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote {

border-color: #000000 !important;
}
ol#posts li blockquote blockquote blockquote blockquote blockquote,

body.mceContentBody blockquote blockquote blockquote blockquote blockquote {

border-color: #000000 !important;
}
ol#posts li.notification.single_notification {

border-bottom: 1px dashed #000000;
}
#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: rgba(0,0,0,0) !important;
}

#return_to_top .return_to_top_icon{

background: url('http://img.photobucket.com/albums/v13/limejuice/MinWoo-Tumblr/UP.jpg') no-repeat transparent!important

}

.tab_notice {
  background-color: #e54a4a !important; 
}
.tab_notice_value {
  color: #ffffff!important; 
}
.tab_notice_nipple {
  background-image: none !important;
}

.tab_notice_nipple:before {
  position: absolute !important;
  content: "" !important;
  width: 0 !important;
  height: 0 !important;
  left: 0 !important;
  border-top: solid 4px #e54a4a !important; 
  border-right: solid 4px transparent !important;
  border-bottom: solid 3px transparent !important;
  border-left: solid 3px #e54a4a !important; 
  z-index: 2 !important;
}
.tab_notice_nipple:after {
  background-image: none !important;
  position: absolute !important;
  content: "" !important;
  width: 0 !important;
  height: 0 !important;
  top: .25px !important;
  left: 1px !important;
  border-top: solid 3.75px #e54a4a !important; 
  border-right: solid 3.75px transparent !important;
  border-bottom: solid 3.75px transparent !important;
  border-left: solid 3.75px #e54a4a !important; 
  z-index: 1 !important;

}

a[class^="like_button"], a[class*="already_like"], .notification_type_icon.like_icon, .likes {background-image: none !important;}
a[class^="like_button"]:before,
a[class^="like_button"]:after,
a[class*="already_like"]:before,
a[class*="already_like"]:after,
.notification_type_icon.like_icon:before,
.notification_type_icon.like_icon:after,
.likes:before,
.likes:after {
  position: absolute !important;
  content: "" !important;
  left: 10px !important;
  top: 0 !important;
  width: 10px !important;
  height: 15px !important;
  background: rgba(102,129,149, 1.0) !important; 
  -moz-border-radius: 50px 50px 0 0 !important;
  -webkit-border-radius: 50px 50px 0 0 !important;
  border-radius: 50px 50px 0 0 !important;
  -moz-transform: rotate(-45deg) !important;
  -webkit-transform: rotate(-45deg) !important;
  -moz-transform-origin: 0 100% !important;
  -webkit-transform-origin: 0 100% !important;
}
a[class*="already_like"]:before,
a[class*="already_like"]:after {
  background: #e54a4a !important; 
}
a[class^="like_button"]:before,
a[class^="like_button"]:after,
a[class*="already_like"]:before,
a[class*="already_like"]:after {
  margin-top: 2px !important; 
  margin-left: 232px !important; 
}

.post_controls {
  min-width:250px !important;
  text-align: right !important;
}

.notification_type_icon.like_icon:before,
.notification_type_icon.like_icon:after {
  margin-top: -1px !important;
  margin-left: -5px !important;
}
a[class^="like_button"]:after,
a[class^="already_like"]:after,
.notification_type_icon.like_icon:after,
.likes:after {
  left: 0 !important;
  background: rgba(102,129,149, 1.0) !important; 
  -moz-transform: rotate(45deg) !important;
  -webkit-transform: rotate(45deg) !important;
  -moz-transform-origin: 100% 100% !important;
  -webkit-transform-origin: 100% 100% !important;
}
.likes:before,
.likes:after {
  margin-top: 12px !important;
  margin-left: 12px !important;
}
a[class*="already_like"]:after {
  background: #9c1717 !important; 
}


#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: rgba(0,0,0,0.3) !important;

    -webkit-border-radius: 20px;

    -moz-border-radius: 20px;

    border-radius: 20px;

    margin: 10px;

    padding-left:20px;

padding-top:50px;
padding-right:-100px;
}

h1 {

color: #666666;

text-shadow: 0 0 3px #000000;

}



.no_posts_found, h1.dashboard_header {

color: #666666;

}



img#content_top, img#content_bottom {

display: none;

}

.answer_container {

background-color: #e29393;

}

}
