@-moz-document url-prefix("https://irccloud.com/"),url-prefix("https://www.irccloud.com/"),url-prefix("https://alpha.irccloud.com/"){
@media only screen and (max-width:/*[[sidebarpx]]*/){body.app{min-width:100%}#sidebarcell,div#sidebarwrapper{position:fixed;z-index:999;top:0;right:-197px;background:#D9E7FF;-webkit-transition:right 200ms ease-out;-moz-transition:right 200ms ease-out}#sidebarcell:hover,div#sidebarwrapper:hover{right:0}#sidebar>div{margin:4px}#mainwrapper{padding-right:0}div#container{min-width:auto}#statusActions{padding-right:8px}}@media only screen and (max-width:/*[[memberpx]]*/){.memberList,td.memberscell,.nickcell,.memberwrapper{display:none!important}.showMembers div.buffermain{padding-right:0}}
}
