css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("photo-forum.net") {
body {
	background-color: #333333 !important;
	color:#777777 !important;
	text-align: center !important;
        background-image: url(http://img227.imageshack.us/img227/2909/ffdown1gt7.jpg) !important; 
        background-repeat: repeat-x !important;
        background-position: left top !important;
	background-attachment: fixed !important;
}
a[href*="photo-forum.net/adrotator/ad_click"] {display:none !important;}
#tableHead, table[bgcolor="#E5E5E5"] {width: 943px !important;}
table#tableHead td[bgcolor="#F7F7F7"], object, td[background="http://212.36.3.59/site_design/menutail.jpg"] {display: none !important;}
table {
	margin-right: auto !important;
	margin-left: auto !important;
}
table, tr, td, table.tableThumb {background-color: #000000 !important;}
.Link2 {color:#CC6600 !important;}
.PhorumNav {
	color: #669999 !important;
	padding-left: 4px !important;
}
font.PhorumNav a font.PhorumNav:hover {color:#66CCCC !important;}
.PhorumListHeader {
	padding-left: 10px !important;
	padding-top: 10px !important;
	padding-bottom: 10px !important;
	font-weight: bold !important;
	color: #777777 !important;
	background-color:#222 !important;
}
a, a.PhorumListRow, .PhorumSubject, .boxText12 { color:#999999 !important; }
table.PhorumListTable font.PhorumTableHeader { color:#ccc !important; font-weight:bold !important}
a:hover, a.PhorumListRow:hover, .PhorumSubject:hover{ color:#CCCCCC !important; }
.PhorumSubject:hover {text-decoration:none !important; }
td.PhorumListRow {
	color: #999999 !important;
	padding-left: 10px !important;
}
td.PhorumListRow[bgcolor="#ffffff"] {background-color: #222222 !important;}
img[src="http://212.36.3.59/site_design/forum_fotografia.gif"] {display:none !important;}
img[src="http://212.36.3.59/site_design/forum_obshtatema.gif"] {display:none !important;}
img[src="http://212.36.3.59/site_design/forum_tehnika.gif"] {display:none !important;}
img[src="http://212.36.3.59/site_design/forum_zanovaci.gif"] {display:none !important;}
img[src="http://212.36.3.59/site_design/forum_konkursi-izlojbi.gif"] {display:none !important;}
.PhorumNewFlag, font.PhorumNav span[style]  { color: #996600 !important; }
td.menuButton {background-color: #444 !important;}
td.menuButton a {
	background-color: #777 !important;
	color: #000 !important;
	height: 15px !important;
	vertical-align: middle !important;
}
td.menuButton a:hover {background-color: #000 !important; color: #999 !important;}
table.menutext {color:#000000 !important;}
table [width="781"] [cellpadding="3"] {background-color:#999999 !important;}
.PhorumLegendStyle table {background-color:#fff !important;}
table.PhorumListTable table td {background-color:#222 !important;}
.PhorumBodyArea, .PhorumListTable input {background-color:#999999 !important;}
textarea.PhorumBodyArea {background-image: url(http://img227.imageshack.us/img227/2909/ffdown1gt7.jpg) !important; background-repeat: no-repeat !important;	background-position: right bottom !important;}
table.PhorumListTable font {color:#999999 !important;}
table.PhorumListTable .PhorumMessage {color:#aaa !important;   border: none !important; padding:10px !important; display: block; overflow: auto;}
table.PhorumListTable table td {background-color:#333 !important; border: 0px !important; padding: 0px !important; margin: 10px !important; }
table.PhorumListTable table td:before {
  background: transparent url(http://virtuelvis.com/gallery/css/rounded/top-right.png) scroll no-repeat top right !important;
margin: 0px !important;  
  height: 30px !important;
margin-bottom: -20px !important;
  display: block !important;
  border: 0px !important ;
  content: url(http://virtuelvis.com/gallery/css/rounded/top-left.png) !important;
  line-height: 0.1 !important;
  font-size: 1px !important;
}
.PhorumTxtStyle {color:#999999 !important;}
.boxText, .boxText span, .boxText a, .boxTextRight, .boxTextRights {color:#999999 !important;}
.boxText font {color:#669999 !important; font-weight:bold !important;}
.boxText font:hover, .boxText a:hover {color:#336666 !important;}
img[src="http://212.36.3.59/site_design/user.jpg"], img[src="http://212.36.3.59/site_design/album.jpg"] {display: none !important;}
.menuAlbum, .menuAlbum1 {color:#cccccc !important;}
.menuAlbum:hover, .menuAlbum1:hover  {color:#CCCCCC !important;}
.mainTitle {background-color:#333 !important;}
td [width="10"] {background-color:#222 !important;}
td [height="1"] {background-color:#333 !important;}
#fldComment {background-color:#999 !important; color:#000!important;}
#tableHead a img[width="200"] {display:none !important;}
.tableInfo {color:#999999 !important;}
.PhorumBodyLink {color: #449999 !important;}
.PhorumBodyLink:hover {color:#227766 !important;}
span span.PhorumLegendStyleBold {color:#449999 !important;}
span span.PhorumLegendStyleBold:hover {color:#227766 !important; cursor:default !important;}
body[bgcolor="#CCCCCC"][link="#FFFFCC"][vlink="#FFFFCC"][alink="#FFFFFF"] {background-image:none !important; background-color:#999 !important;
	text-align: center !important;
        background-image: url(http://img227.imageshack.us/img227/2909/ffdown1gt7.jpg) !important; 
        background-repeat: no-repeat !important;
        background-position: bottom right !important;}
body[bgcolor="#CCCCCC"][link="#FFFFCC"][vlink="#FFFFCC"][alink="#FFFFFF"] td {background-color:#999 !important; color:#ccc !important;}
body[bgcolor="#CCCCCC"][link="#FFFFCC"][vlink="#FFFFCC"][alink="#FFFFFF"] img {display:none !important;}
body[bgcolor="#CCCCCC"][link="#FFFFCC"][vlink="#FFFFCC"][alink="#FFFFFF"] a {display:none !important; }
iframe[src*="googlesyndication"] {display:none !important;} 

}
