css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*[[settingplaceholder]]*/

#window-controls toolbarbutton, 
#titlebar-buttonbox toolbarbutton {
	-moz-appearance: none !important;
	padding: 0 !important;
	border: none !important;
	width: 14px !important;
	min-width: 14px !important;
	height: 14px !important;
	min-height: 14px !important;
	background: none !important;
	box-shadow: none !important;
	list-style-image: url("data:image/png;base64,") !important;
}

#minimize-button, 
#titlebar-min {
    margin:0 !important;
	-moz-image-region: rect(0 14px 14px 0) !important;
}
#minimize-button:hover, 
#titlebar-min:hover {
	-moz-image-region: rect(14px 14px 28px 0) !important;
}
#minimize-button:hover:active, 
#titlebar-min:hover:active {
	-moz-image-region: rect(28px 14px 42px 0) !important;
}


#restore-button, 
#titlebar-max {
	margin-left: 6px !important;
	margin-right: 6px !important;
	-moz-image-region: rect(0 28px 14px 14px) !important;
}
#restore-button:hover, 
#titlebar-max:hover {
	-moz-image-region: rect(14px 28px 28px 14px) !important;
}
#restore-button:hover:active, 
#titlebar-max:hover:active {
	-moz-image-region: rect(28px 28px 42px 14px) !important;
}


#close-button, 
#titlebar-close {
	margin:0 !important;
	-moz-image-region: rect(0 42px 14px 28px) !important;
}
#close-button:hover, 
#titlebar-close:hover {
	-moz-image-region: rect(14px 42px 28px 28px) !important;
}
#close-button:hover:active, 
#titlebar-close:hover:active {
	-moz-image-region: rect(28px 42px 42px 28px) !important;
}

style_setting_options.value:
#titlebar-buttonbox{
position:fixed !important;
left:5px !important;
top:5px !important;
}
#main-window[sizemode="maximized"] #titlebar-buttonbox{
position:fixed !important;
left:8px !important;
top:11px !important;
}

#titlebar-buttonbox{
-moz-transform:rotateY(180deg) !important;
}
