@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digg.com") {

a[href^="http://www.theonion.com/"] {

    color: #006600 !important;
 }

#title0 a[href^="http://www.theonion.com/"]:before {
content: url('data:image/png;base64,')!important; 
}

a[href^="http://www.theonion.com1/"]:before {
content: " and some text after." !important;

} 


a[href^="http://www.theonion.com/"]:visited,
a[href^="http://www.theonion.com/"]:hover {

    color: #35C235 !important;
}
}
