css:
@-moz-document domain("google.co.uk", "google.com") {

/* google bar */

#gb #gbx3,

#gb #gbx4 {

  background-color: #F6F6F6 !important;

  border-color: #E5E5E5 !important;

}



/* google bar links */

#gb #gbz .gbzt, 

#gb #gbz .gbgt, 

#gb #gbg .gbgt {

  color: #999 !important;

  font-weight: normal !important;

}

#gb #gbz .gbz0l span.gbts, 

#gb #gbz .gbzt-hvr, 

#gb #gbz .gbzt:focus, 

#gb #gbz .gbgt-hvr, 

#gb #gbz .gbgt:focus, 

#gb #gbg .gbz0l span.gbts, 

#gb #gbg .gbgt-hvr, 

#gb #gbg .gbgt:focus {

  color: #DD4B39 !important;

  font-weight: bold !important;

}

#gb #gbz .gbzt:hover, 

#gb #gbz .gbgt:hover, 

#gb #gbz .gbz0l span.gbts:hover,

#gb #gbg .gbgt:hover, 

#gb #gbg .gbz0l span.gbts:hover {

  color: #DD4B39 !important;

  font-weight: normal !important;

  background: #fbfbfb !important;

}



/* red text ("NEW") */

#gb .gbsup {

  display: none !important;

}



/* more drop-down */

#gb #gbz .gbto .gbma {

  border-top-color: #777 !important;

}

#gb #gbztms:hover .gbma {

  border-top-color: #DD4B39 !important;

}

#gb #gbz .gbto .gbts, 

#gb #gbd .gbmt {

  font-weight: normal !important;

}

#gb #gbz .gbto .gbts, 

#gb #gbd .gbmt {

  color: #666 !important;

}

#gb #gbd .gbmtc:hover a, 

#gb #gbz li.gbt.gbto #gbztms:hover #gbztms1 {

  color: #DD4B39 !important;

}



/* google logo (cozy) */

#gb .gbem#gbql, 

.gbemi#gb #gbql {

  background-position: 0 0 !important;

  height: 45px !important;

  width: 116px !important;

}

}
