css:
#watch-sidebar #watch-context-container, #watch-sidebar h4.watch-sidebar-head, #homepage-sidebar-ads, #playlist-bar.min, .yt-horizontal-rule {
display: none;
}

#masthead-container {
background: none;
}

body, .watch-branded #watch-main-container,
.watch-branded #watch-sidebar {
  color: #fff !important;
  background: #000 url('http://i.imgur.com/3ybm0.png') !important;
}

div#content {
background-image: none;
background-color: transparent;
}

.yt-uix-range-tooltip-tip-content, .yt-uix-tooltip-tip-content {
color: #34393C;
}

#logo {
background: no-repeat url(http://i.imgur.com/Jylef.png) -114px -146px;
}

#watch-headline-user-info .subscribed .yt-uix-button-icon-subscribe {
background: no-repeat url(http://i.imgur.com/Jylef.png) -133px -66px;
}

.video-thumb {
background-color: #1D1D1D;
position: relative;
}

.comments-section .yt-tile-default:hover {
background-color: transparent;
}

.comments-rating-positive {
color: #4F94CD;
white-space: nowrap;
}


.yt-tile-static, .yt-tile-visible, .yt-tile-default:hover {
color: #fff !important;
}

.watch-pyv-vid {
background: none;
}

#watch-video-annotation {
background: none;
padding: 10px;
border:  none;
font-size: 13px;
}

.watch-wide #watch-video-container {
background-color: transparent;
background-image: none;

}


.browse-videos .browse-content {
border-left: 1px solid #000;
}

#video-sidebar h2 {
color: #ccc;
}

#footer ul {
text-shadow: 0 1px 1px #000;
}

#video-sidebar .video-list-item-link .title, #video-sidebar .recommended-videos-link {
color: #ccc;
}

.watch-branded #watch-video-container, .watch-branded #watch-headline-container {

background: #000 url('http://i.imgur.com/3ybm0.png')
}

.watch-playlists-drawer ul {
border: 1px solid #000;
padding: 10px;
margin-bottom: 10px;
background: none;
}



.watch-sparkbar-likes {
float: left;
height: 4px;
border-right: 1px solid white;
background: #3692c2;
}

.yt-uix-button .yt-uix-button-icon-watch-insight {
background: url('http://i.imgur.com/Jylef.png') -154px -207px !important;
}

.watch-sparkbar-dislikes {
float: right;
height: 4px;
margin-right: -1px;
background: #515151;
}

.yt-uix-button-default, .yt-uix-button-default:focus, body .yt-uix-button-default[disabled] {
  background-color: #454545 !important;
  border-color: #000 #000 #000 !important;
  text-shadow: 0 1px 0 rgba(0, 0, 0, .45) !important;
  outline: 0 !important;
  background-image: -webkit-linear-gradient(top,#474747 0,#2B2B2B 100%) !important;
  -webkit-box-shadow: inset 0 1px 0 rgba(255,255,255,.1) !important;
}

.yt-uix-button-default, a.yt-uix-button-default .yt-uix-button-content {
  color: #c3c3c3 !important;
}


.yt-uix-button-default .yt-uix-button-arrow, .yt-uix-button-text .yt-uix-button-arrow {
  border-top-color: #fff !important;
}

.yt-uix-button:hover .yt-uix-slider-next-arrow, .yt-uix-button:focus .yt-uix-slider-next-arrow {
  border-left-color: #fff !important;
}

.yt-uix-button:hover .yt-uix-slider-prev-arrow, .yt-uix-button:focus .yt-uix-slider-prev-arrow {
  border-right-color: #fff !important;
}

.yt-uix-button-text, a.yt-uix-button-text .yt-uix-button-content {
  color: #666 !important;
}

.yt-uix-button-text, body .yt-uix-button-text[disabled] {
  text-shadow: none !important;
  border-color: #333 !important;
  box-shadow: 0 1px 0 #333 !important;
  -moz-box-shadow: 0 1px 0 #333 !important;
  -webkit-box-shadow: 0 1px 0 #333 !important;
  -ms-box-shadow: 0 1px 0 #333 !important;
}

.yt-tile-default, .yt-tile-default a, .yt-tile-visible, .yt-tile-visible a {
color: #bababa;
}

.comment-list .comment a, .comments-section h4, #watch-description-text {
color: grey;
}

.video-list-item:hover a .title, .video-list-item:hover .stat {
  color: #333 !important;
}

.yt-alert-naked .yt-alert-content {
  color: #fff !important;
}


#watch-sidebar .video-list-item a:hover {
  background-color: #ccc !important;
  border-bottom-color: #999 !important;
}


.yt-horizontal-rule {
  border-top-color: #333 !important;
  border-bottom-color: #000 !important;
}

.yt-uix-button-default:hover,
.yt-uix-button-text:hover {
  border-color: #333 !important;
}

.navigation-menu .menu-item a {
color: #c6c6c6;
}

body, .watch-branded #watch-main-container, .watch-branded #watch-sidebar {
color: #929292 !important;
}

.browse-videos .browse-collection .collection-header a.heading {
color: #c9c9c9;
}

.yt-playall-link {
border: 3px solid #363636;
}

.browse-item.yt-tile-default h3 a, .browse-item.yt-tile-visible h3 a {
color: #c9c9c9;
}

.yt-tile-default h3, .yt-tile-default h3 a, .yt-tile-visible h3, .yt-tile-visible h3 a {
color: #c9c9c9;
}

.navigation-menu .menu-item {
border-top: 1px solid black;
border-bottom: 1px solid #595959;
text-shadow: 0 1px 1px #292929;
text-shadow: 0 0 0 transparent,0 1px 1px rgba(255, 255, 255, .5);
}

.yt-uix-button-default:hover,
.yt-uix-button-text:hover {
background-image: -webkit-linear-gradient(top,#565656 0,#565656 100%);
background-image: linear-gradient(to bottom,#565656 0,#E565656 100%);
}

.yt-tile-default.video-list-item a .title,
#watch-sidebar .video-list-item .title {
  color: #bbb !important;
}

#watch-description a, a {
color: #658da8;
}

#watch-description, #watch-description-extras h4  {
color: #616161;
}

.live-comments-setting {
margin: 10px 0px;
border: 1px solid #2f2f2f;
padding: 5px;
font-size: 11px;
font-weight: bold;
color: #666;
text-align: center;
}

.comments-section h4 {
margin: 5px 0;
padding: 0 3px;
border-bottom: 1px solid #2f2f2f;
color: #666;
font-weight: normal;
line-height: 1.9231em;
}

.yt-tile-default h3 a:visited, .yt-tile-visible h3 a:visited {
color: #768d97!important;
}

#watch-channel-discoverbox {
  box-shadow: inset 0 4px 8px rgba(0,0,0,.05),0 1px 0 #333 !important;
  -moz-box-shadow: inset 0 4px 8px rgba(0,0,0,.05),0 1px 0 #333 !important;
  -webkit-box-shadow: inset 0 4px 8px rgba(0,0,0,.05),0 1px 0 #333 !important;
  -ms-box-shadow: inset 0 4px 8px rgba(0,0,0,.05),0 1px 0 #333 !important;
}

#watch-channel-discoverbox .video-list-item .title {
  color: #666 !important;
}

.yt-uix-pager .yt-uix-button, .yt-uix-pager .yt-uix-pager-link, .yt-uix-pager .yt-uix-pager-selected {
  color: #fff !important;
}



.yt-uix-form-input-text, .yt-uix-form-textarea {
background: #808080;
color: #434343;
font-size: 13px;
padding: 7px 10px;
border: 0px solid #434343;
border-top-color: #434343;
}

#watch-description .expand,
#watch-description .collapse {
  background-color: transparent !important;
}

#watch-description-expand, #watch-description-collapse {
  border-top-color: transparent !important;
}

#watch-description-expand .yt-uix-button, #watch-description-collapse .yt-uix-button {
  color: #fff;
  text-shadow: 0 1px 0 #333 !important;
}

.yt-uix-button#watch-description-expand .yt-uix-button:hover, #watch-description-collapse .yt-uix-button:hover {
  background-image: linear-gradient(to bottom,#333 0,#000 100%) !important;
  background-image: -moz-linear-gradient(top,#333 0,#000 100%) !important;
  background-image: -webkit-linear-gradient(top,#333 0,#000 100%) !important;
  background-image: -ms-linear-gradient(top,#333 0,#000 100%) !important;
  filter: progid:DXImageTransform.Microsoft.Gradient(GradientType=0, startColorStr="#FF333333", endColorStr="#FF000000") !important;
}

#watch-description .yt-horizontal-rule {
clear: both;
border: 0;
margin-top: 0px;
}

#masthead-nav a, #masthead-user a, #masthead-nav a:hover, #masthead-user a:hover, #masthead-nav a:active, #masthead-user a:active {
  color: #ccc !important;
}

#masthead-expanded-container {
  box-shadow: 0 5px 5px #222 !important;
  -moz-box-shadow: 0 5px 5px #222 !important;
  -webkit-box-shadow: 0 5px 5px #222 !important;
  -ms-box-shadow: 0 5px 5px #222 !important;
}


#watch-headline h1,
#eow-title-input {
color: #fff !important;
}

#masthead-user-expander .yt-uix-expander-head, #masthead-gaia-user-wrapper, #masthead-gaia-photo-wrapper {
border-color: #000 !important;
}

.feed-item-main .feed-item-time {
background: transparent !important;
box-shadow: none !important;
text-shadow: 0 1px 0 black !important
}

.feed-item-outer, #feed .feed-page, #feed .feed-load-more-container {
    background:#222222 !important;
}
.feed-item-main h4, #video-sidebar .video-list-item-link .title, #video-sidebar .recommended-videos-link, .viewcount, .snippet-metadata li.views {
    color:#999999 !important;
}
.feed-item-main .description {
    color:#666666 !important;
}
.feed-item-actions-line {
    color:#999999 !important;
}
.feed-item-container, .playlist-video-item {
    border-bottom:1px solid #151515 !important;
}
#feed .feed-load-more-container {
    padding: 0 10px 10px !important;
    margin:0px !important;
}

#masthead-search-terms {
  background-color: #898989 !important;
}

#masthead-search-terms input {
  color: #fff !important;
  text-shadow: 0 1px 1px #333 !important;
}

.masthead-search-terms-border {
  border-color: #333 !important;
  box-shadow: inset 0 1px 2px #333 !important;
  -moz-box-shadow: inset 0 1px 2px #333 !important;
  -webkit-box-shadow: inset 0 1px 2px #333 !important;
  -ms-box-shadow: inset 0 1px 2px #333 !important;
}


#watch-context {
  border-color: #333 !important;
}

#watch-context .context-head {
  border-bottom-color: #333 !important;
}


.context-body {
  background: #222 !important;
}

#watch-actions-area {
  background: #333 !important;
  box-shadow: 0 1px 1px #666 !important;
  -moz-box-shadow: 0 1px 1px #666 !important;
  -webkit-box-shadow: 0 1px 1px #666 !important;
  -ms-box-shadow: 0 1px 1px #666 !important;
}

#watch-description-toggle {
  border-top-color: #333 !important;
}

.watch-sparkbars {
  border-color: #333 !important;
}

.watch-view-count {
  color: #fff !important;
}

#insight-ratings td {
  color: #fff !important;
}

.watch-stats-title-cell {
  background-color: #666 !important;
}

#flag-video-panel h3,
#flag-video-panel label,
#flag-video-panel-buttons,
#flag-video-panel .flag-video-result {
  color: #fff !important;
}
 
.share-panel-show-more,
.share-panel-show-url-options {
  color: #fff !important;
}

.share-panel-hangout .share-panel-hangout-description {
  color: #fff !important;
}

#footer-main .pickers .yt-uix-button {
color: #9b9b9b !important;
text-shadow: 0 1px 1px #333 !important;
}

.comments-post-alert {
  background-color: #333 !important;
}

#footer ul {
  text-shadow: 0 1px 1px #333 !important;
}

#footer-links-primary a,
#footer-links-secondary a,
#footer .pickers a,
#picker-loading {
  color: #999 !important;
}
