/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


/* Compacting Code */

#main-window[sizemode="normal"] #navigator-toolbox #toolbar-menubar[autohide="true"]
{position: fixed !important;
top:120px !important;
left:0px !important;
background: #000000 !important;}


#main-window:not([sizemode="fullscreen"]) #navigator-toolbox[tabsontop="true"] #toolbar-menubar[autohide="false"]
{position: fixed !important;
right:0 !important;}

#main-window:not([sizemode="fullscreen"]) #navigator-toolbox:not([customizing])[tabsontop="false"] #toolbar-menubar[autohide="false"]
{min-height: 3px !important;
max-height: 3px !important;
opacity: 0 !important;}

#main-window:not([sizemode="fullscreen"]) #navigator-toolbox:not([customizing])[tabsontop="false"] #toolbar-menubar[autohide="false"]:hover
{min-height: 0 !important;
max-height: 0 !important;
opacity: 1.0 !important;}


#navigator-toolbox #toolbar-menubar .menubar-text
{font-family: corbel !important;
font-size: 8pt !important;}



#main-window[sizemode="normal"] #toolbar-menubar[autohide="false"] ~ #TabsToolbar[tabsontop="true"]
{margin-right: 24em !important;}

#main-window[sizemode="maximized"] #toolbar-menubar[autohide="false"] ~ #TabsToolbar[tabsontop="true"]
{margin-right: 25em !important;
padding-left: 2px !important;}



#main-window[sizemode="normal"] #toolbar-menubar[autohide="true"] ~ #TabsToolbar[tabsontop="true"]
{margin-top: -15px !important;
margin-left: 80px !important;
margin-right: 11em !important;}

#main-window[sizemode="maximized"] #toolbar-menubar[autohide="true"] ~ #TabsToolbar[tabsontop="true"]
{margin-left: 0 !important;}

#main-window[sizemode="normal"] #toolbar-menubar[autohide="false"] ~ #TabsToolbar[tabsontop="true"]
{margin-left: -4px !important;}

#main-window[sizemode="maximized"] #toolbar-menubar[autohide="false"] ~ #TabsToolbar[tabsontop="true"]
{margin-left: -4px !important;}


